#include "LIB::commons.js";	// 공통 스크립트 
#include "LIB::utility.js";	// 다중 선택 
//=========================================================================================
// [ PART 1 ]
// Form에서 사용될 전역변수를 선언
// FORM에서 사용되는 전역변수는 대문자로 작성하며, F_XXX 의 형태를 갖도록 한다. 

//=========================================================================================
var userGroup;
var FROW = 0;
//=========================================================================================
// [ PART 2 ]
// Form Loading / Unloading 시 작업 정의
//=========================================================================================
function fn_OnLoadCompleted(obj) {
	gfn_initForm(true);

	userGroup = gfn_getUserGroup();

	if(G_USER_GROUP_B < 'B30') {
		ufn_enableBtn("approval", false);
	} else {
		ufn_enableBtn("approval", true);
	}

	//코드콤보 설정
	var codeArr = ["BS107","CS131"];
	var dsObjArr = [ds_pst,ds_gbn];
	var addTypeArr = [G_CODE_ALL,G_CODE_ALL];
	gfn_searchCommCode(codeArr, dsObjArr, addTypeArr);

	cal_frqt.Value = left(gfn_getCurrDate(),6)+"01";
	cal_trqt.Value = gfn_getCurrDate();

	cb_ord1.index = 0;
	cb_ord2.Index = 0;

	ds_cond_sel.SetColumn(0, "USR_ID", G_USER_ID);

	tit_clearActionInfo();
	tit_addSearchActionInfo("bs01:BS0102008A_S1");
	tit_addSearchActionInfo("bs01:BS0102008A_S3");
	tit_addSearchActionInfo("bs01:BS0102008A_S4");
	tit_addSearchActionInfo("bs01:BS0102008A_S6");

	tit_callService(
        ""
        , ""
        , "ds_cond=ds_cond ds_cond_sel=ds_cond_sel"
        , "ds_gno=ds_gno ds_user=ds_user ds_ara=ds_ara ds_ara_sel=ds_ara_sel"
        , ""
        , "fn_afterGno"
        , true
        , true);
}

/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterGno(errCode, errMsg) {
	gfn_insertEmptyRow(ds_ara, G_CODE_ALL);
	cb_ara.value = "";
	gfn_insertEmptyRow(ds_gno, G_CODE_ALL);
	cb_gno.value = "";
	gfn_insertEmptyRow(ds_user, G_CODE_ALL);
	cb_user.value = "";

	var userGroup = gfn_getUserGroup();

	if((substr(G_USER_ID,0,1) == "7" || substr(G_USER_ID,0,1) == "8" || substr(G_USER_ID,0,1) == "9") && userGroup > "10") {
		ufn_enableBtn("query", false);
	} else if(userGroup == "10") {
		cb_ara.Index = 0;
		cb_ara.Enable = false;

		ds_gno.Filter("CODE=="+quote(G_VEND_CODE));
		cb_gno.Index = 0;
		cb_gno.Enable = false;

		cb_gno_OnChanged(ds_gno,cb_gno.Value,"","","");

		ds_user.Filter("CODE=="+quote(G_USER_ID));
		cb_user.Index = 0;

		if(ds_user.GetColumn(0, "DESC") == "REP") {
			cb_gno_OnChanged(ds_gno,cb_gno.Value,"","","");
		} else {
			cb_user.Enable = false;
		}
	} else if(userGroup == "20") {
		var dpt;

		if(G_DPT1 == "B4") {
			dpt = ds_ara_sel.GetColumn(0, "ARA_SEL");
		} else {
			dpt = G_DPT1;
		}

		ds_ara.Filter("CODE=="+quote(dpt));
		cb_ara.Index = 0;
		cb_ara.Enable = false;

		cb_ara_OnChanged(ds_ara,cb_ara.Value,"","","");
	} else if(userGroup == "30" && G_USER_ID <> '2016893' && G_USER_ID <> '2017687' && G_USER_ID <> '2009269' && G_USER_ID <> '2016311') {
		ds_ara.Filter("CODE=="+quote(G_DPT1));
		cb_ara.Index = 0;
		cb_ara.Enable = false;

		cb_ara_OnChanged(ds_ara,cb_ara.Value,"","","");
	}
}

/********************************************************************************
* 기      능   : 공통코드가 조회된 후의 Callback 함수 
********************************************************************************/
function fn_completeInit(sCode, sDsObj, sCodeType) {
	cb_ara.value = "";
	cb_yst.value = "";

	if(G_DPT1 == 'B4')
		cb_gbn.Index = 1;
	else if(G_DPT1 == 'C7')
		cb_gbn.Index = 2;
	else if(G_DPT1 == 'C8')
		cb_gbn.Index = 3;
	else if(G_DPT1 == 'C6')
		cb_gbn.Index = 4;
	else
		cb_gbn.Index = 0;
}

/********************************************************************************
* 기      능   : form UnloadCompleted 
********************************************************************************/
function fn_OnUnloadCompleted(obj) {
	return gfn_isFormUnload(obj);
}

//=========================================================================================
// [ PART 3 ]
// Main Process Event 정의(조회,입력,삭제,저장,인쇄,form닫기)
//=========================================================================================
/********************************************************************************
* 기      능   : 조회 
********************************************************************************/
function fn_query() {
	if(substr(G_USER_ID,0,1) == "9" && (length(cb_gno.Text) == 0 || length(cb_user.Text) == 0)) {
		gfn_showMsg("조회조건을 초기화중입니다. 잠시만 기다려 주십시요.");
		return;
	}

	if(length(trim(cb_gbn2.Value)) == 0) {
		gfn_showMsg("CW00001",st_gbn2.value);
		return;
	}

	if(length(med_sno.Value) > 0 && length(med_sno.Value) < 8) {
		gfn_showMsg("승인번호를 정확히 입력하십시요.");
		return;
	}

	ds_cond.SetColumn(0, "SAM", substr(med_sno.Value,0,4));
	ds_cond.SetColumn(0, "SAQ", substr(med_sno.Value,4,4));

	if(cb_yst.Value == "") { //전체
		ds_cond.SetColumn(0, "SGB", "0");
	} else if(cb_yst.Value == "A0") { //미작성
		ds_cond.SetColumn(0, "SGB", "1");
	} else if(cb_yst.Value == "A1") { //작성
		ds_cond.SetColumn(0, "SGB", "2");
	} else { //그외
		ds_cond.SetColumn(0, "SGB", "3");
	}

	var sql = "";

	if(length(med_sno.Value) == 8) { //승인번호 조회
		sql = "bs01:BS0102008A_S8";
	} else {
		if(chk_his.Value == false) { //최종차수만 조회
			sql = "bs01:BS0102008A_S2";
		} else { //이력조회
			sql = "bs01:BS0102008A_S7";
		}
	}

	tit_clearActionInfo();
	tit_addSearchActionInfo(sql);

	tit_callService(
        ""
        , ""
        , "ds_cond=ds_cond"
        , "ds_list=ds_list"
        , ""
        , "fn_afterQuery"
        , true
        , true);
}

/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterQuery(errCode, errMsg) {
	gfn_showMsg("CI00002",ds_list.RowCount()+"");
	ds_list.row = FROW;
}

/********************************************************************************
* 기      능   : 승인
********************************************************************************/
function fn_approval() {
	gfn_clearMsgBar();

	if(ds_list.rowCount() < 1) {
		// 승인할 정보 없음.
		return;
	}

	for(var i=0; i<ds_list.rowcount; i++) {
		if(ds_list.GetColumn(i, "CHK") == true) {
			var rdt = ds_list.GetColumn(i, "BS201_RDT");
			var seq = ds_list.GetColumn(i, "BS201_SEQ");
			var sir = ds_list.GetColumn(i, "BS201_SIR");

			if(fn_Chk(i, rdt, seq, sir) == false) { //호기별 원가계산 여부
				return false;
			}

			if(ds_list.GetColumn(i, "YST") <> "A3") {
				var arr = [parseInt(i) + 1];
				gfn_showMsg("CW00042", arr);
				return;
			}
		}
	}

	tit_clearActionInfo();

	tit_addSaveActionInfo(
		""
		, "bs01:BS0102008A_U1"
		, ""
		, "FLAG"
		, ""
		, -1
		, "");

	tit_callService(
		""
		, ""
		, "ds_list=ds_list"
		, ""
		, ""
		, "fn_afterApproval");
}

/********************************************************************************
* 기      능   : 승인Callback
********************************************************************************/
function fn_afterApproval(errCode, errMsg) {
	if(errCode != 0) {
		gfn_showMsg("CE00001");
	} else {
		gfn_showMsg("CI00003");
		fn_query();
	}
}

function fn_Chk(row_num, rdt, seq, sir) {
	ds_cond2.SetColumn(0, "RDT", rdt);
	ds_cond2.SetColumn(0, "SEQ", seq);
	ds_cond2.SetColumn(0, "SIR", sir);

	tit_clearActionInfo();
	tit_addSearchActionInfo("bs01:BS0102008A_S5");
	tit_callService(
		""
		, ""
		, "ds_cond2=ds_cond2"
		, "ds_chk=ds_chk"
		, ""
		, ""
		, true
		, true
		, true
	);

	if(ds_chk.GetColumn(0, "GBN") == "X") {
		gfn_showAlert("[" + parseInt(row_num) + 1 + "]번째 행의 원가계산이 완료되지 않았습니다.");
		return false;
	}

	return true;
}

/********************************************************************************
* 기      능   : 닫기  
********************************************************************************/
function fn_close() {
	close();
}

