********************************************************************************/
function fn_excel() {
	gfn_saveToExcel(grd_list, true);
}

/********************************************************************************
* 기      능   : 그리드 헤더 클릭시 정렬 처리
********************************************************************************/
function grd_list_OnHeadClick(obj,nCell,nX,nY,nPivotIndex) {
	if(nCell == 0) {
		// 그리드 표시할 컬럼 선택
		//gfn_selectAllGrid(obj,nCell,"FLAG",true);
	} else {
		// 그리드 정렬 처리
		gfn_sortGrid(obj,nCell);
	}
}

/********************************************************************************
* 기      능   : 그리드2에 DATASET변경시
********************************************************************************/
function ds_list_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex) {
	obj.fireevent = false;

	switch(strColumnID) {
		case "CHK":
			ds_list.SetColumn(nRow, "FLAG", "U");
			break;
	}

	obj.fireevent = true;	
}

/********************************************************************************
* 기      능   : 그리드 더블 클릭시 팝업연동
********************************************************************************/
function grd_list_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex) {
	if(nCell <> 0) {
		var arg = "gubun='S'";
		gfn_openPopup("BS0102009A",true,arg);
		FROW = ds_list.row;
		fn_query();
	}
}

/********************************************************************************
* 기      능   : 지역콤보 변경시
********************************************************************************/
function cb_ara_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex) {
	if(length(trim(strCode)) == 0) {
		ds_gno.Filter("LENGTH(ARA) == 0 || 1==1");
		ds_user.Filter("LENGTH(LIFNR) == 0 || 1==1");
		cb_gno.Index = 0;
		cb_user.Index = 0;
	} else {
		ds_gno.Filter("LENGTH(ARA) == 0 || ARA == "+quote(strCode));
		ds_user.Filter("LENGTH(LIFNR) == 0 || ARA == "+quote(strCode));
		cb_gno.Index = 0;
		cb_user.Index = 0;
	}	
}

/********************************************************************************
* 기      능   : 협력업체콤보 변경시
********************************************************************************/
function cb_gno_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex) {
	if(length(trim(strCode)) == 0) {
		if(length(trim(cb_ara.Value)) > 0)
			ds_user.Filter("LENGTH(LIFNR) == 0 || ARA == "+quote(cb_ara.Value));
		else
			ds_user.Filter("LENGTH(LIFNR) == 0 || 1==1");

		cb_user.Index = 0;
	} else {
		ds_user.Filter("LENGTH(LIFNR) == 0 || LIFNR == "+quote(strCode));
		cb_user.Index = 0;
	}	
}

function fn_enableYn() {
	if(userGroup >= "30") {
		return "checkbox";
	} else {
		return "none";
	}	
}

/********************************************************************************
* 기      능   : 출력
********************************************************************************/
function fn_print() {
	gfn_clearMsgBar();
	ds_list1.ClearData();
	ds_list2.ClearData();

	if(ds_list.rowCount() < 1) {
		// 출력할 정보 없음
		return;
	}

	if(!gfn_isChangeDs(ds_list, true)) {
		return;
	}

	var k = 0;
	var z = 0;
	for(var i=0; i<ds_list.RowCount(); i++) {
		if(ds_list.GetColumn(i, "CHK") == true) { //체크박스가 true 일 경우
			if(ds_list.GetColumn(i, "YST") <> "A6") {
				var arr = [parseInt(i) + 1];
				gfn_showMsg("CW00042", arr);
				return false;
			}

			z = i;
			k++;
		}
	}

	if(k == 0 || k > 1) {
		gfn_showMsg("견적서 출력은 한건씩 가능합니다.");
		return false;
	}

	ds_cond1.SetColumn(0,"BS201_RDT",ds_list.GetColumn(z,"BS201_RDT"));
	ds_cond1.SetColumn(0,"BS201_SEQ",ds_list.GetColumn(z,"BS201_SEQ"));
	ds_cond1.SetColumn(0,"BS201_SIR",ds_list.GetColumn(z,"BS201_SIR"));

	tit_clearActionInfo();
	tit_addSearchActionInfo("bs01:BS0102008B_S1");
	tit_addSearchActionInfo("bs01:BS0102008B_S2");
	tit_callService(
		""
		, ""
		, "ds_cond1=ds_cond1"
		, "ds_list1=ds_list1 ds_list2=ds_list2"
		, ""
		, "fn_afterPrint"
		, true
		, true);
}

/********************************************************************************
* 기      능   : 출력 Callback
********************************************************************************/
function fn_afterPrint(errCode, errMsg) {
	Dialog("BS01::BS0102008B.xml", "");
}
