function fn_OnLoadCompleted(obj) {
	gfn_initForm();

	ds_cond2.SetColumn(0,"GNO",ds_list2.GetColumn(ds_list2.row,"GNO"));
	ds_cond2.SetColumn(0,"RDT",ds_list2.GetColumn(ds_list2.row,"BS202_RDT"));
	ds_cond2.SetColumn(0,"SEQ",ds_list2.GetColumn(ds_list2.row,"BS202_SEQ"));
	ds_cond2.SetColumn(0,"SIR",ds_list2.GetColumn(ds_list2.row,"BS202_SIR"));
	ds_cond2.SetColumn(0,"ISR",ds_list2.GetColumn(ds_list2.row,"BS202_ISR"));
	ds_cond2.SetColumn(0,"GBN","2");

	//코드콤보 설정
	var codeArr = ["CS131","BS107","BS403"];
	var dsObjArr = [ds_gbn2,ds_pst,ds_buse];
	var addTypeArr = [G_CODE_NONE,G_CODE_NONE,G_CODE_NONE];
	gfn_searchCommCode(codeArr, dsObjArr, addTypeArr);

	tit_clearActionInfo();
	tit_addSearchActionInfo("bs01:BS0102001A_S4");
	tit_addSearchActionInfo("bs01:BS0102010A_S4");
	tit_addSearchActionInfo("bs01:BS0102010A_S5");

	tit_callService(
        ""
        , ""
        , "ds_cond=ds_cond2"
        , "ds_userp=ds_userp ds_gbn=ds_gbn ds_ord=ds_ord"
        , ""
        , "fn_afterUser"
        , true
        , true);
}

