/********************************************************************************
* 기      능   : 조회 
********************************************************************************/
function fn_query() {
	tit_clearActionInfo();
	tit_addSearchActionInfo("bs01:BS0102010A_S1");
	tit_addSearchActionInfo("bs01:BS0102010A_S2");
	tit_addSearchActionInfo("bs01:BS0102010A_S3");

	tit_callService(
        ""
        , ""
        , "ds_cond=ds_cond2"
        , "ds_head=ds_head ds_list3=ds_list3 ds_list4=ds_list4"
        , ""
        , "fn_afterQuery"
        , true
        , true);
}

/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterQuery(errCode, errMsg) {
	gfn_showMsg("CI00002", ds_list4.RowCount()+"");

	if(ds_head.rowcount == 0) {
		ds_head.AddRow();
	}
}

/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterUser(errCode, errMsg) {
	fn_query();
}

/********************************************************************************
* 기      능   : 저장
********************************************************************************/
function fn_save() {
	ds_list5.ClearData();

	var userGroup = gfn_getUserGroup();

	if(userGroup < 30 && ds_head.GetColumn(0, "BS201_YST") == "A3") {
		gfn_showAlert("요청 상태에서는 수정이 불가능합니다.");
		return false;
	}

	if(ds_list4.rowcount == 0) {
		gfn_showMsg("CW00113", st_param.value);
		return;
	}
/*
	// 의장미옵션정보 필수입력 체크
	var dsArr = ["BS204_SEC","BS204_GBN","BS204_ORD"];
	var lbArr = [st_sec.value,st_gbn.value,st_ord.value];
	if(gfn_isGridNull(grd_list4,dsArr,lbArr,"")) {
		return;
	}
*/
	var j = 0;
	for(var i=0; i<ds_list4.RowCount(); i++) {
		if(ds_list4.GetColumn(i, "FLAG") == "I") {
			if(trim(ds_list4.GetColumn(i, "BS204_ORD")) == "NUM") {
				if(substr(trim(ds_list4.GetColumn(i, "BS204_GBN")),1,3) == "ETC") {
					if(length(trim(ds_list4.GetColumn(i, "BS204_TXT"))) == 0) {
						var arr = [parseInt(i) + 1, st_txt.value];
						gfn_showMsg("CW00055", arr);
						return;
					}
				}

				if(ds_list4.GetColumn(i, "BS204_AMT") == 0) {
					var arr = [parseInt(i) + 1, st_amt.value];
					gfn_showMsg("CW00055", arr);
					return;
				}
			} else {
				if(trim(ds_list4.GetColumn(i, "BS204_GBN")) == "BEL_ZCJM1_FR" || trim(ds_list4.GetColumn(i, "BS204_GBN")) == "BEL_ZCJM2_FR") {
					if(length(trim(ds_list4.GetColumn(i, "BS204_ORD"))) == 0) {
						var arr = [parseInt(i) + 1, st_ord.value];
						gfn_showMsg("CW00055", arr);
						return;
					}
				}

				if(trim(ds_list4.GetColumn(i, "BS204_ORD")) <> "CCRD" && trim(ds_list4.GetColumn(i, "BS204_ORD")) <> "HTH") { // 카드키, 층고증가 제외
					if(length(trim(ds_list4.GetColumn(i, "BS204_TXT"))) > 0) {
						var arr = [parseInt(i) + 1, st_txt.value];
						gfn_showMsg("CW00055", arr);
						return;
					}

					if(ds_list4.GetColumn(i, "BS204_AMT") > 0) {
						var arr = [parseInt(i) + 1, st_amt.value];
						gfn_showMsg("CW00055", arr);
						return;
					}
				} else {
					if(trim(ds_list4.GetColumn(i, "BS204_GBN")) == "CEL_ZOPTION09") {
						if(ds_list4.GetColumn(i, "BS204_AMT") == 0) {
							var arr = [parseInt(i) + 1, st_amt.value];
							gfn_showMsg("CW00055", arr);
							return;
						} else {
							if(length(trim(ds_list4.GetColumn(i, "BS204_ORD"))) == 0) {
								var arr = [parseInt(i) + 1, st_ord.value];
								gfn_showMsg("CW00055", arr);
								return;
							}
						}
					}

					if(trim(ds_list4.GetColumn(i, "BS204_GBN")) == "CEL_ZOPTION11") {
						if(ds_list4.GetColumn(i, "BS204_AMT") == 0) {
							var arr = [parseInt(i) + 1, st_amt.value];
							gfn_showMsg("CW00055", arr);
							return;
						} else {
							if(length(trim(ds_list4.GetColumn(i, "BS204_ORD"))) == 0) {
								var arr = [parseInt(i) + 1, st_ord.value];
								gfn_showMsg("CW00055", arr);
								return;
							}
						}
					}
				}
			}

			if(length(trim(ds_list4.GetColumn(i, "BS204_RDT"))) == 0) {
				ds_list4.SetColumn(i, "BS204_RDT", ds_cond2.GetColumn(0, "RDT"));
				ds_list4.SetColumn(i, "BS204_SEQ", ds_cond2.GetColumn(0, "SEQ"));
				ds_list4.SetColumn(i, "BS204_SIR", ds_cond2.GetColumn(0, "SIR"));
				ds_list4.SetColumn(i, "BS204_ISR", ds_cond2.GetColumn(0, "ISR"));
			}

			ds_list5.AddRow();
			ds_list5.SetColumn(j, "FLAG", "I");
			ds_list5.SetColumn(j, "BS204_RDT", ds_list4.GetColumn(i, "BS204_RDT"));
			ds_list5.SetColumn(j, "BS204_SEQ", ds_list4.GetColumn(i, "BS204_SEQ"));
			ds_list5.SetColumn(j, "BS204_SIR", ds_list4.GetColumn(i, "BS204_SIR"));
			ds_list5.SetColumn(j, "BS204_ISR", ds_list4.GetColumn(i, "BS204_ISR"));
			ds_list5.SetColumn(j, "BS204_SEC", ds_list4.GetColumn(i, "BS204_SEC"));
			ds_list5.SetColumn(j, "BS204_GBN", substr(ds_list4.Getcolumn(i, "BS204_GBN"),1));
			ds_list5.SetColumn(j, "BS204_ORD", ds_list4.GetColumn(i, "BS204_ORD"));
			ds_list5.SetColumn(j, "BS204_TXT", ds_list4.GetColumn(i, "BS204_TXT"));
			if(length(trim(ds_list4.GetColumn(i, "BS204_AMT"))) == 0) {
				ds_list5.SetColumn(j, "BS204_AMT", 0);
			} else {
				ds_list5.SetColumn(j, "BS204_AMT", ds_list4.GetColumn(i, "BS204_AMT"));
			}
			ds_list5.SetColumn(j, "BS204_RMK", ds_list4.GetColumn(i, "BS204_RMK"));
			j++;
		} else {
			if(trim(ds_list4.GetColumn(i, "BS204_GBN")) == "BEL_ZCJM1_FR" || trim(ds_list4.GetColumn(i, "BS204_GBN")) == "BEL_ZCJM2_FR") {
				if(length(trim(ds_list4.GetColumn(i, "BS204_ORD"))) == 0) {
					var arr = [parseInt(i) + 1, st_ord.value];
					gfn_showMsg("CW00055", arr);
					return;
				}
			}
		}
	}

	tit_clearActionInfo();
	tit_addSingleActionInfo("bs01:BS0102010A_D1");
	//tit_addSingleActionInfo("bs01:BS0102010A_U1");
	tit_addSelectExActionInfo("bs01:BS0102010A_S6", "", "bs01:BS0102010A_U1");
	tit_addSingleActionInfo("bs01:BS0102010A_U2");
	tit_addMultiActionInfo("bs01:BS0102010A_I1");

	tit_callService(
		""
		, ""
		, "ds_list5=ds_list5"
		, ""
		, ""
		, "fn_afterSave"
		, true);
}

/********************************************************************************
* 기      능   : 저장 Callback
********************************************************************************/
function fn_afterSave(errCode, errMsg) {
	if(errCode != 0) {
		gfn_showMsg("CE00001");
	} else {
		gfn_showMsg("CI00006", "저장");
		fn_query();
	}
}

/********************************************************************************
* 기      능   : 닫기  
********************************************************************************/
function fn_close() {
	close();
}

