********************************************************************************/
function btn_plus_OnClick(obj) {
	ds_list4.AddRow();
}
/********************************************************************************
* 기      능   : 그리드에 row삭제
********************************************************************************/
function btn_minus_OnClick(obj) {
	ds_list4.DeleteRow(ds_list4.row);
}

function ds_list4_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex) {
	obj.fireevent = false;

	if(length(trim(ds_list4.GetColumn(nRow, "BS204_ORD"))) == 0) {
		ds_list4.SetColumn(nRow, "FLAG", "");
		ds_list4.SetColumn(nRow, "BS204_TXT", "");
		ds_list4.SetColumn(nRow, "BS204_AMT", "0");
	}

	if(length(trim(ds_list4.GetColumn(nRow, "BS204_ORD"))) > 0 && ds_list4.GetOrgColumn(nRow, "BS204_ORD") <> ds_list4.GetColumn(nRow, "BS204_ORD")) {
		ds_list4.SetColumn(nRow, "FLAG", "I");
	}

	if(length(trim(ds_list4.GetColumn(nRow, "BS204_TXT"))) > 0 && ds_list4.GetOrgColumn(nRow, "BS204_TXT") <> ds_list4.GetColumn(nRow, "BS204_TXT")) {
		ds_list4.SetColumn(nRow, "FLAG", "I");
	}

	if(ds_list4.GetColumn(nRow, "BS204_AMT") > 0 && ds_list4.GetOrgColumn(nRow, "BS204_AMT") <> ds_list4.GetColumn(nRow, "BS204_AMT")) {
		ds_list4.SetColumn(nRow, "FLAG", "I");
	}

	obj.fireevent = true;
}

function grd_list4_OnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex) {
	var t_use = ds_list2.GetColumn(ds_list2.row,"BS202_USE");

	if(nRow == 32) {
		if(t_use == "BD" || t_use == "BH" || t_use == "EB" || t_use == "EH" || t_use == "EP" || t_use == "HC" || t_use == "ND" || t_use == "NH" || t_use == "PS") { //전망용이 아닐경우
			grd_list4.SetCellProp("body",3,"Edit","none");
			grd_list4.SetCellProp("body",4,"Edit","none");
			grd_list4.SetCellProp("body",5,"Edit","none");
		} else {
			grd_list4.SetCellProp("body",3,"Edit","combo");
			grd_list4.SetCellProp("body",4,"Edit","normal");
			grd_list4.SetCellProp("body",5,"Edit","number");
		}
	} else {
		grd_list4.SetCellProp("body",3,"Edit","combo");
		grd_list4.SetCellProp("body",4,"Edit","normal");
		grd_list4.SetCellProp("body",5,"Edit","number");
	}
}
