/********************************************************************************
* 기      능   : 저장
********************************************************************************/
function fn_saveTrans(gbn) {
	gfn_clearMsgBar();

	if(ds_list.rowcount == 0) {
		gfn_showMsg("CW00113",st_param.value);
		return;
	}

	if(!gfn_isChangeDs(ds_list, true, "")) {
		return;
	}

	ds_list2.ClearData();
	var rows = 0;
	for(var i=0; i<ds_list.rowcount; i++) {
		if(ds_list.GetColumn(i,"FLAG") == 'U') {
			ds_list2.AddRow();
			ds_list2.SetColumn(rows,"CHK","1");
			ds_list2.SetColumn(rows,"FLAG","U");
			ds_list2.SetColumn(rows,"BS201_RDT",ds_list.GetColumn(i,"BS201_RDT"));
			ds_list2.SetColumn(rows,"BS201_SEQ",ds_list.GetColumn(i,"BS201_SEQ"));
			ds_list2.SetColumn(rows,"BS201_SIR",ds_list.GetColumn(i,"BS201_SIR"));
			ds_list2.SetColumn(rows,"BS201_GBN",ds_list.GetColumn(i,"BS201_GBN"));
			ds_list2.SetColumn(rows,"BS201_CID",ds_list.GetColumn(i,"BS201_CID"));
			ds_list2.SetColumn(rows,"BS201_AYM",ds_list.GetColumn(i,"BS201_AYM"));
			ds_list2.SetColumn(rows,"BS201_ASQ",ds_list.GetColumn(i,"BS201_ASQ"));
			ds_list2.SetColumn(rows,"BS201_NO",ds_list.GetColumn(i,"BS201_NO"));
			ds_list2.SetColumn(rows,"BS201_GNM",ds_list.GetColumn(i,"BS201_GNM"));
			ds_list2.SetColumn(rows,"BS201_SPT",ds_list.GetColumn(i,"BS201_SPT"));
			ds_list2.SetColumn(rows,"BS201_QTY",ds_list.GetColumn(i,"BS201_QTY"));
			ds_list2.SetColumn(rows,"BS201_PST",ds_list.GetColumn(i,"BS201_PST"));
			ds_list2.SetColumn(rows,"BS201_GNO",ds_list.GetColumn(i,"BS201_GNO"));
			ds_list2.SetColumn(rows,"BS201_JRT",ds_list.GetColumn(i,"BS201_JRT"));
			ds_list2.SetColumn(rows,"BS203_TDT",ds_list.GetColumn(i,"BS203_TDT"));
			ds_list2.SetColumn(rows,"BS203_GDT",ds_list.GetColumn(i,"BS203_GDT"));
			ds_list2.SetColumn(rows,"BS203_COMA",ds_list.GetColumn(i,"BS203_COMA"));
			ds_list2.SetColumn(rows,"BS203_AMTA",ds_list.GetColumn(i,"BS203_AMTA"));
			ds_list2.SetColumn(rows,"BS203_AMTW",ds_list.GetColumn(i,"BS203_AMTW"));
			ds_list2.SetColumn(rows,"BS203_AMTT",ds_list.GetColumn(i,"BS203_AMTT"));
			ds_list2.SetColumn(rows,"BS203_RSN",ds_list.GetColumn(i,"BS203_RSN"));
			ds_list2.SetColumn(rows,"BS203_COMB",ds_list.GetColumn(i,"BS203_COMB"));
			ds_list2.SetColumn(rows,"BS203_AMTB",ds_list.GetColumn(i,"BS203_AMTB"));
			ds_list2.SetColumn(rows,"BS203_RMK",ds_list.GetColumn(i,"BS203_RMK"));
			if(gbn == "A") ds_list2.SetColumn(rows,"BS203_PST","10");
			else if(gbn == "B") ds_list2.SetColumn(rows,"BS203_PST","15");
			ds_list2.SetColumn(rows,"BS202_TYP",ds_list.GetColumn(i,"BS202_TYP"));

			rows++;
		}	
	}	

	if(ds_list2.rowcount == 0) {
		gfn_showMsg("CW00113",st_param.value);
		return;
	}

	for(var i=0; i<ds_list2.rowcount; i++) {
		if(ds_list2.GetColumn(i,"FLAG") == 'U') {
			if(trim(ds_list2.GetColumn(i,"BS203_RSN")) <> "35") { //실패사유 : 등록불가가 아닐 경우
				if(length(ds_list2.GetColumn(i,"BS203_TDT")) == 0) {
					ds_list.row = ds_list.FindRow("BS201_NO",ds_list2.GetColumn(i,"BS201_NO"));
					gfn_showMsg("CW00001", st_tdt.value);
					return;
				}
				if(length(ds_list2.GetColumn(i,"BS203_COMA")) == 0) {
					ds_list.row = ds_list.FindRow("BS201_NO",ds_list2.GetColumn(i,"BS201_NO"));
					gfn_showMsg("CW00001", st_coma.value);
					return;
				} 
				if(ds_list2.GetColumn(i,"BS203_AMTA") <= 0) {
					ds_list.row = ds_list.FindRow("BS201_NO",ds_list2.GetColumn(i,"BS201_NO"));
					gfn_showMsg("CW00001", st_amta.value);
					return;
				} 
				if(ds_list2.GetColumn(i,"BS203_AMTW") <= 0) {
					ds_list.row = ds_list.FindRow("BS201_NO",ds_list2.GetColumn(i,"BS201_NO"));
					gfn_showMsg("CW00001", st_amtw.value);
					return;
				} 
/* 2011.09.07 홍만유차장님 요청(투찰가, 투찰율 제외)
				if(ds_list2.GetColumn(i,"BS203_AMTT") <= 0) {
					ds_list.row = ds_list.FindRow("BS201_NO",ds_list2.GetColumn(i,"BS201_NO"));
					gfn_showMsg("CW00001", st_amtt.value);
					return;
				} 
*/
				if(ds_list2.GetColumn(i,"BS203_COMA") != '10' && length(ds_list2.GetColumn(i,"BS203_RSN")) == 0) {
					ds_list.row = ds_list.FindRow("BS201_NO",ds_list2.GetColumn(i,"BS201_NO"));
					var arr = [st_coma.value, st_tasa.value, st_rsn.value];
					// [${}] 항목의 [${}] 값이 입력될 경우 [${}] 항목은 필수입력입니다.\n확인하여 주십시오.
					gfn_showMsg("CW00144", arr);
					return;
				}
				if(ds_list2.GetColumn(i,"BS203_COMA") == '10' && length(ds_list2.GetColumn(i,"BS203_GDT")) == 0) {
					ds_list.row = ds_list.FindRow("BS201_NO",ds_list2.GetColumn(i,"BS201_NO"));
					var arr = [st_coma.value, st_jasa.value, st_gdt.value];
					// [${}] 항목의 [${}] 값이 입력될 경우 [${}] 항목은 필수입력입니다.\n확인하여 주십시오.
					gfn_showMsg("CW00144", arr);
					return;
				}
			} else { //실패사유 : 등록불가일 경우
				if(length(ds_list2.GetColumn(i,"BS203_RMK")) == 0) {
					ds_list.row = ds_list.FindRow("BS201_NO",ds_list2.GetColumn(i,"BS201_NO"));
					gfn_showMsg("CW00001", st_rmk.value);
					return;
				}
			}
		}
	}

	tit_clearActionInfo();
	tit_addSelectExActionInfo("bs01:BS0103001A_S2", "bs01:BS0103001A_I1", "bs01:BS0103001A_U1");
	tit_callService(
		""
		, ""
		, "ds_list=ds_list2:A"
		, ""
		, ""
		, "fn_afterSave");
}
/********************************************************************************
* 기      능   : 저장 Callback
********************************************************************************/
function fn_afterSave(errCode, errMsg) {
	ds_list2.ClearData();
	if(errCode != 0) {
		gfn_showMsg("CE00001");
	} else {
		gfn_showMsg("CI00003");
		fn_query();
	}
}

/********************************************************************************
* 기      능   : DS_LIST 칼럼 변경시
********************************************************************************/
function ds_list_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex) {
	obj.fireevent = false;
	switch(strColumnID) {
		case "CHK":
			if(varNewValue == '1' && (ds_list.GetColumn(nRow,"BS203_PST") == "10" || ds_list.GetColumn(nRow,"BS203_PST") == "")) {
				ds_list.SetColumn(nRow,"CHK","1");
				ds_list.SetColumn(nRow,"FLAG","U");
			} else {
				ds_list.SetColumn(nRow,"CHK","0");
				ds_list.SetColumn(nRow,"FLAG","");
			}
			break;
		case "BS203_COMA":
			if(ds_list.GetColumn(nRow,"BS203_COMA") == "10") {
				ds_list.SetColumn(nRow,"BS203_RSN","");
//				ds_list.SetColumn(nRow,"BS103_COMB","");
//				ds_list.SetColumn(nRow,"BS103_AMTB","0");
			}
			break;
		case "BS203_RSN":
			if(ds_list.GetColumn(nRow,"BS203_COMA") == "10") {
				ds_list.SetColumn(nRow,"BS203_RSN","");
			}
			break;
		case "BS203_COMB":
			if(ds_list.GetColumn(nRow,"BS203_COMA") == "10") {
//				ds_list.SetColumn(nRow,"BS103_COMB","");
			}
			break;
		case "BS203_AMTA":
			if(ds_list.GetColumn(nRow,"BS203_COMA") == "10") {
				ds_list.SetColumn(nRow,"BS203_AMTT",ds_list.GetColumn(nRow,"BS203_AMTA"));
			}
			if(ds_list.GetColumn(nRow,"BS203_AMTA") != 0) {
				var rate = ROUND(ds_list.GetColumn(nRow,"BS203_AMTW")/ds_list.GetColumn(nRow,"BS203_AMTA")*100,2);
				ds_list.SetColumn(nRow,"SI_RATE",rate);
				rate = ROUND(ds_list.GetColumn(nRow,"BS203_AMTT")/ds_list.GetColumn(nRow,"BS203_AMTA")*100,2);
				ds_list.SetColumn(nRow,"TU_RATE",rate);
			} else {
				ds_list.SetColumn(nRow,"SI_RATE",0);
				ds_list.SetColumn(nRow,"TU_RATE",0);
			}
			break;
		case "BS203_AMTW":
			if(ds_list.GetColumn(nRow,"BS203_AMTA") != 0) {
				var rate = ROUND(ds_list.GetColumn(nRow,"BS203_AMTW")/ds_list.GetColumn(nRow,"BS203_AMTA")*100,2);
				ds_list.SetColumn(nRow,"SI_RATE",rate);

			} else {
				ds_list.SetColumn(nRow,"SI_RATE",0);
			}
			if(ds_list.GetColumn(nRow,"BS203_AMTB") != 0) {
				var rate = ROUND(ds_list.GetColumn(nRow,"BS203_AMTW")/ds_list.GetColumn(nRow,"BS203_AMTB")*100,2);
				ds_list.SetColumn(nRow,"TU2_RATE",rate);
			} else {
				ds_list.SetColumn(nRow,"TU2_RATE",0);
			}
			break;
/* 2011.09.07 홍만유차장님 요청(투찰가, 투찰율 제외)
		case "BS203_AMTT":
			if(ds_list.GetColumn(nRow,"BS203_AMTA") != 0)
			{
				var rate = ROUND(ds_list.GetColumn(nRow,"BS203_AMTT")/ds_list.GetColumn(nRow,"BS203_AMTA")*100,2);
				ds_list.SetColumn(nRow,"TU_RATE",rate);
			}
			else
			{
				ds_list.SetColumn(nRow,"TU_RATE",0);
			}
			break;
*/
		case "BS203_AMTB":
			if(ds_list.GetColumn(nRow,"BS203_AMTB") != 0) {
				var rate = ROUND(ds_list.GetColumn(nRow,"BS203_AMTW")/ds_list.GetColumn(nRow,"BS203_AMTB")*100,2);
				ds_list.SetColumn(nRow,"TU2_RATE",rate);
			} else {
				ds_list.SetColumn(nRow,"TU2_RATE",0);
			}
			break;
	}
	obj.fireevent = true;
}
/********************************************************************************
* 기      능   : 그리드에서 날짜 입력시
********************************************************************************/
function grd_list_OnExpandEdit(obj,nRow,nCell,strVal,nPivotIndex) {
	var rect = obj.GetCellRect(nRow, ncell);

	cal_temp.top = rect[1] + 60;
	if (cal_temp.top > 400)	{
		cal_temp.top = 400;
	}
	cal_temp.Left = rect[0] + 10;
	if(cal_temp.Left > 790) {
		cal_temp.Left = 790;
	}
	cal_temp.Visible = true;

	if(nCell == 12) {
		cal_temp.value = ds_list.getColumn(nRow, "BS203_TDT");
	} else if(nCell == 13) {
		cal_temp.value = ds_list.getColumn(nRow, "BS203_GDT");
	}

//	cal_temp.value = ds_list.getColumn(nRow, "BS103_TDT");
	cal_temp.SetFocus();
}
/********************************************************************************
* 기      능   : 달력에서 일자 선택했을 경우
********************************************************************************/
function cal_temp_OnDayClick(obj,strText) {
	if(grd_list.GetClickedCell() == 12) {
		ds_list.SetColumn(ds_list.row, "BS203_TDT", strText);
	} else if(grd_list.GetClickedCell() == 13) {
		ds_list.SetColumn(ds_list.row, "BS203_GDT", strText);
	}
	obj.Visible = false;
}
/********************************************************************************
* 기      능   : 지역콤보 변경시
********************************************************************************/
function cb_ara_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex) {
	if(length(trim(strCode)) == 0) {
		ds_gno.Filter("LENGTH(ARA) == 0 || 1==1");
		ds_user.Filter("LENGTH(LIFNR) == 0 || 1==1");
		cb_gno.Index = 0;
		cb_user.Index = 0;
	} else {
		ds_gno.Filter("LENGTH(ARA) == 0 || ARA == "+quote(strCode));
		ds_user.Filter("LENGTH(LIFNR) == 0 || ARA == "+quote(strCode));
		cb_gno.Index = 0;
		cb_user.Index = 0;
	}	
}
/********************************************************************************
* 기      능   : 협력업체콤보 변경시
********************************************************************************/
function cb_gno_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex) {
	if(length(trim(strCode)) == 0) {
		if(length(trim(cb_ara.Value)) > 0)
			ds_user.Filter("LENGTH(LIFNR) == 0 || ARA == "+quote(cb_ara.Value));
		else
			ds_user.Filter("LENGTH(LIFNR) == 0 || 1==1");

		cb_user.Index = 0;
	} else {
		ds_user.Filter("LENGTH(LIFNR) == 0 || LIFNR == "+quote(strCode));
		cb_user.Index = 0;
	}	
}
