/********************************************************************************
* 기      능   : 조회 
********************************************************************************/
function fn_query() 
{
	// 필수 입력사항 및 일자 체크 
	if ( !gfn_checkFromToObj(cal_ftdt, cal_ttdt, true, st_tdate.value) )
	{
		return;
	}
	// 필수 입력사항 및 일자 체크 
	if ( !gfn_checkFromToObj(cal_fcdt, cal_tcdt, true, st_cdate.value) )
	{
		return;
	}

	// sort mark 삭제 
	gfn_clearGridSortMark(grd_list);
	ds_list.row = -1;
	grd_list.SetCellProp("head", 1, "Text", 0);

	if(cal_ftdt.Value > '' && cal_fcdt = '') ds_cond.SetColumn(0,"GUBUN","A");
	else if(cal_ftdt.Value > '' && cal_fcdt > '') ds_cond.SetColumn(0,"GUBUN","B");
	else if(cal_ftdt.Value = '' && cal_fcdt > '') ds_cond.SetColumn(0,"GUBUN","C");

	tit_clearActionInfo();
	tit_addSearchActionInfo("bs01:BS0103002A_S1");
	tit_callService(
        ""
        , ""
        , "ds_cond=ds_cond"
        , "ds_list=ds_list"
        , ""
        , "fn_afterQuery"
        , true
        , true);
}
/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterQuery(errCode, errMsg) 
{
	gfn_showMsg("CI00002",ds_list.RowCount()+"");
}
