	gfn_saveToExcel(grd_list, true );
}
/********************************************************************************
* 기      능   : 그리드 더블 클릭시 팝업연동
********************************************************************************/
function grd_list_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	if(nCell != 0 && nCell != 1 )
	{   
		var arg = "gubun='S'";
		gfn_openPopup("BS0102001A",true,arg);
		fn_query();
	}	
}
/********************************************************************************
* 기      능   : 그리드 헤더 클릭시 정렬 처리 
********************************************************************************/
function grd_list_OnHeadClick(obj,nCell,nX,nY,nPivotIndex)
{
	if ( nCell == 0 ) {
		gfn_selectGridCol("grd_list");
	} else if ( nCell == 1 ) {
		gfn_selectAllGrid(obj,nCell,nCell,true);
	} else {
		gfn_sortGrid(obj,nCell);
	}	
}
/********************************************************************************
* 기      능   : 반송
********************************************************************************/
function fn_sendback() 
{
	if(ds_list.rowcount == 0)
	{
		gfn_showMsg("CW00113",st_param.value);
		return;
	}

	if ( !gfn_isChangeDs(ds_list, true, "")){
		return;
	}

	ds_list2.ClearData();
	var rows = 0;
	for(var i=0; i<ds_list.rowcount; i++)
	{
		if(ds_list.GetColumn(i,"FLAG") == 'U') {
			ds_list2.AddRow();
			ds_list2.SetColumn(rows,"CHK","1");
			ds_list2.SetColumn(rows,"FLAG","U");
			ds_list2.SetColumn(rows,"BS201_AYM",ds_list.GetColumn(i,"BS201_AYM"));
			ds_list2.SetColumn(rows,"BS201_ASQ",ds_list.GetColumn(i,"BS201_ASQ"));
			ds_list2.SetColumn(rows,"BS203_PST","10");

			rows++;
		}	
	}	

	if(ds_list2.rowcount == 0)
	{
		gfn_showMsg("CW00113",st_param.value);
		return;
	}

	tit_clearActionInfo();
	tit_addSelectExActionInfo("bs01:BS0103001A_S2", "", "bs01:BS0103002A_U1");
	tit_callService(
		""
		, ""
		, "ds_list=ds_list2:A"
		, ""
		, ""
		, "fn_afterSave");
}
/********************************************************************************
* 기      능   : 전송 
********************************************************************************/
function fn_trans() 
{
}
/********************************************************************************
* 기      능   : 저장 Callback
********************************************************************************/
function fn_afterSave(errCode, errMsg) 
{
	ds_list2.ClearData();
	if ( errCode!= 0 ) 
	{
		gfn_showMsg("CE00001");
	} 
	else 
	{
		gfn_showMsg("CI00003");
		fn_query();
	}

}

/********************************************************************************
* 기      능   : DS_LIST 칼럼 변경시
********************************************************************************/
function ds_list_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	obj.fireevent = false;
	switch( strColumnID )
	{
		case "CHK":
			if(varNewValue == '1' )
			{
				ds_list.SetColumn(nRow,"CHK","1");
				ds_list.SetColumn(nRow,"FLAG","U");
			}
			else
			{
				ds_list.SetColumn(nRow,"CHK","0");
				ds_list.SetColumn(nRow,"FLAG","");
			}
			break;
	}
	obj.fireevent = true;
}
/********************************************************************************
* 기      능   : 지역콤보 변경시
********************************************************************************/
function cb_ara_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	if(length(trim(strCode)) == 0) 
	{
		ds_gno.Filter("LENGTH(ARA) == 0 || 1==1");
		ds_user.Filter("LENGTH(LIFNR) == 0 || 1==1");
		cb_gno.Index = 0;
		cb_user.Index = 0;
	}	
	else 
	{
		ds_gno.Filter("LENGTH(ARA) == 0 || ARA == "+quote(strCode));
		ds_user.Filter("LENGTH(LIFNR) == 0 || ARA == "+quote(strCode));
		cb_gno.Index = 0;
		cb_user.Index = 0;
	}	
}
/********************************************************************************
* 기      능   : 협력업체콤보 변경시
********************************************************************************/
function cb_gno_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	if(length(trim(strCode)) == 0) 
	{
		if(length(trim(cb_ara.Value)) > 0)
			ds_user.Filter("LENGTH(LIFNR) == 0 || ARA == "+quote(cb_ara.Value));
		else
			ds_user.Filter("LENGTH(LIFNR) == 0 || 1==1");

		cb_user.Index = 0;
	}	
	else 
	{
		ds_user.Filter("LENGTH(LIFNR) == 0 || LIFNR == "+quote(strCode));
		cb_user.Index = 0;
	}	
}
