//=========================================================================================
/********************************************************************************
* 기      능   : 조회 1
********************************************************************************/
function fn_query() {
	gfn_clearMsgBar();

	var user_grp = gfn_getUserGroup();

	ds_cond.SetColumn(0, "ZZPJT_ID", ed_pjt.value);
	ds_cond.SetColumn(0, "VKBUR", cb_ara.value);
	ds_cond.SetColumn(0, "LIFNR", cb_gno.value);
	ds_cond.SetColumn(0, "KUNNR", cb_user.value);
	ds_cond.SetColumn(0, "CBOX1", chk1.value);
	ds_cond.SetColumn(0, "CBOX2", chk2.value);
	ds_cond.SetColumn(0, "CBOX3", chk3.value);
	ds_cond.SetColumn(0, "CBOX4", chk4.value);
	ds_cond.SetColumn(0, "CBOX5", chk5.value);
	ds_cond.SetColumn(0, "CBOX6", chk6.value);
	ds_cond.SetColumn(0, "CBOX7", chk7.value);

	// sort mark 삭제 
	gfn_clearGridSortMark(grd_list);
	ds_list.row = -1;
	ds_list.ClearData();

	fn_createWait("", this);

	tit_callService(
        "BS0104001A_ACT"
        , "query"
        , "ds_cond=ds_cond"
        , "ds_list=ds_list ds_error=ds_error"
        , ""
        , "fn_afterQuery");
}

/********************************************************************************
* 기      능   : 저장
********************************************************************************/
function fn_save() {

	destroy("ds_list_chg");
	create("dataset","ds_list_chg");
	ds_list.UnFilter();
	ds_list.Filter("CHG ==" +quote("update"));
	object("ds_list_chg").copyF(ds_list);
	ds_list.UnFilter();

	if (fn_valid_chk("save") == false) {
		return;
	}

	if (fn_param_set("save") == false) {
		return;
	}

	fn_createWait("", this);

	tit_callService(
        "BS0104001A_ACT"
        , "save"
        , "ds_list_chg=ds_list_chg"
        , "ds_error=ds_error"
        , ""
        , "fn_afterSave");
}

function fn_valid_chk(pProcGb) {
	switch(pProcGb)	{

		case "save" :

			if(ds_list_chg.getRowCount() == 0) {
				gfn_showAlert("CW00005");
				return false;
			}

			var chgSoptp;
			var chgSopdt;

			for(var i=0; i<ds_list_chg.getRowCount(); i++) {
				chgSoptp = ds_list_chg.getColumn(i, "SOPTP");
				chgSopdt = ds_list_chg.getColumn(i, "SOPDT").Replace("-", "");

				if(chgSoptp == "No") {
					if(chgSopdt <= substr(gfn_getCurrDate(),0,8)) {
						gfn_showAlert("CW10025", "공사번호("+ds_list_chg.getColumn(i, "ZZPJT_ID")+")의 현장개설 전 현장개설(예정)일자는 미래 날짜만 입력 가능합니다.");
						return false;
					}
				} else if(chgSoptp == "Yes") {
					if(chgSopdt == "00000000" || chgSopdt > substr(gfn_getCurrDate(),0,8)) {
						gfn_showAlert("CW10025", "공사번호("+ds_list_chg.getColumn(i, "ZZPJT_ID")+")의 현장개설 후 현장개설(예정)일자는 과거/금일 날짜만 입력 가능합니다.");
						return false;
					}
				}
			}
			break; 
		default: 
			break; 
	}   

	return true;
}

function fn_param_set(pProcGb) {
	switch(pProcGb)	{

		case "save" : 

			ds_list_chg.AddColumn("PSPID", "STRING");

			for(var i=0; i<ds_list_chg.getRowCount(); i++) {
				if(ds_list_chg.getColumn(i, "SOPTP") == "No") {
					ds_list_chg.setColumn(i, "SOPTP", "");
				} else if(ds_list_chg.getColumn(i, "SOPTP") == "Yes") {
					ds_list_chg.setColumn(i, "SOPTP", "X");
				}
				ds_list_chg.SetColumn(i, "PSPID", ds_list_chg.GetColumn(i, "ZZPJT_ID"));

				if(Length(ds_list_chg.getColumn(i, "SOPDT")) > 0) {
					fn_format_date(ds_list_chg, i, "SOPDT");
				}

				if(ds_list_chg.GetColumn(i, "SOPTP") == "X" && ds_list_chg.GetColumn(i, "SOP_Y_REGDT").Replace("-", "") == "00000000") {
					ds_list_chg.setColumn(i, "SOP_Y_REGDT", gfn_formatDate(substr(gfn_getCurrDate(),0,8), "-"));
				}
			}
			break;

		default:
	}
	return true;
}

/********************************************************************************
* 기      능   : SAP으로 저장하기 위해 날짜타입에 포맷설정 
********************************************************************************/
function fn_format_date(pDs, pRow, pColid)
{
	var date = gfn_formatDate(pDs.GetColumn(pRow, pColid), "-");

	pDs.SetColumn(pRow, pColid , date);   
}

