/********************************************************************************
* 기      능   : 지역콤보 변경시
********************************************************************************/
function cb_ara_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex) {
	if(length(trim(strCode)) == 0) {
		ds_gno.Filter("LENGTH(ARA) == 0 || 1==1");
		ds_user.Filter("LENGTH(LIFNR) == 0 || 1==1");
		cb_gno.Index = 0;
		cb_user.Index = 0;
	} else {
		ds_gno.Filter("LENGTH(ARA) == 0 || ARA == "+quote(strCode));
		ds_user.Filter("LENGTH(LIFNR) == 0 || ARA == "+quote(strCode));
		cb_gno.Index = 0;
		cb_user.Index = 0;
	}	
}

/********************************************************************************
* 기      능   : 협력업체콤보 변경시
********************************************************************************/
function cb_gno_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex) {
	if(length(trim(strCode)) == 0) {
		if(length(trim(cb_ara.Value)) > 0)
			ds_user.Filter("LENGTH(LIFNR) == 0 || ARA == "+quote(cb_ara.Value));
		else
		ds_user.Filter("LENGTH(LIFNR) == 0 || 1==1");

		cb_user.Index = 0;
	} else {
		ds_user.Filter("LENGTH(LIFNR) == 0 || LIFNR == "+quote(strCode));
		cb_user.Index = 0;
	}	
}

/********************************************************************************
* 기      능   : 원프로젝트 팝업
********************************************************************************/
function btn_pjt_OnClick(obj) {
	var arg = "pjt="+quote(ed_pjt.Text)+" gbn=A";
	gfn_openPopup("CS9601002A",true,arg);
}

function ds_list_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	var dsSoptp = ds_list.GetColumn(nRow, "SOPTP");
	var dsSopDt = ds_list.GetColumn(nRow, "SOPDT").Replace("-", "");

	ds_list.SetColumn(ds_list.GetCurrow(), "CHG", "update");

	if(strColumnID == "SOPTP") {
		if(dsSoptp == "No") {
			if(dsSopDt <= substr(gfn_getCurrDate(),0,8)) {
				gfn_showAlert("CW10025", "공사번호("+ds_list.getColumn(nRow, "ZZPJT_ID")+")현장개설 전 현장개설(예정)일자는 미래 날짜만 입력 가능합니다.");
				ds_list.SetColumn(nRow, "SOPTP", "No");
				return;
			}
		} else if(dsSoptp == "Yes") {
			if(dsSopDt == "00000000" || dsSopDt > substr(gfn_getCurrDate(),0,8)) {
				gfn_showAlert("CW10025", "공사번호("+ds_list.getColumn(i, "ZZPJT_ID")+")의 현장 개설 후 현장개설(예정)일자는 과거/금일 날짜만 입력 가능합니다.");
				ds_list.SetColumn(nRow, "SOPTP", "Yes");
				return;
			}
		}

		if(dsSoptp == "Yes" && varOldValue == "No" && varNewValue == "Yes") {
			grd_list.SetCellProp("BODY", grd_list.GetBindCellIndex("body", "SOPTP"), "edit", "none");
			grd_list.SetCellProp("BODY", grd_list.GetBindCellIndex("body", "SOPDT"), "edit", "none");
		} else {
			grd_list.SetCellProp("BODY", grd_list.GetBindCellIndex("body", "SOPTP"), "edit", "combo");
			grd_list.SetCellProp("BODY", grd_list.GetBindCellIndex("body", "SOPDT"), "edit", "normal");
		}
	}
}
