/********************************************************************************
* 기      능   : 조회 1
********************************************************************************/
function fn_query() {
	gfn_clearMsgBar();

	var user_grp = gfn_getUserGroup();

	ds_cond.SetColumn(0, "ZZPJT_ID", ed_pjt.value);
	ds_cond.SetColumn(0, "VKBUR", cb_vkbur.value);
	ds_cond.SetColumn(0, "VKGRP", cb_vkgrp.value);
	ds_cond.SetColumn(0, "KUNNR", cb_user.value);
	ds_cond.SetColumn(0, "CBOX1", chk1.value);
	ds_cond.SetColumn(0, "CBOX2", chk2.value);
	ds_cond.SetColumn(0, "CBOX3", chk3.value);
	ds_cond.SetColumn(0, "CBOX4", chk4.value);
	ds_cond.SetColumn(0, "CBOX5", chk5.value);
	ds_cond.SetColumn(0, "CBOX6", chk6.value);

	// sort mark 삭제 
	gfn_clearGridSortMark(grd_list);
	ds_list.row = -1;
	ds_list.ClearData();

	tit_callService(
        "BS0104002A_ACT"
        , "query"
        , "ds_cond=ds_cond"
        , "ds_list=ds_list ds_error=ds_error"
        , ""
        , "fn_afterQuery");
}

