/********************************************************************************
* 기      능   : 지역콤보 변경시
********************************************************************************/
function cb_ara_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex) {
	if(length(trim(strCode)) == 0) {
		ds_gno.Filter("LENGTH(ARA) == 0 || 1==1");
		ds_user.Filter("LENGTH(LIFNR) == 0 || 1==1");
		cb_gno.Index = 0;
		cb_user.Index = 0;
	} else {
		ds_gno.Filter("LENGTH(ARA) == 0 || ARA == "+quote(strCode));
		ds_user.Filter("LENGTH(LIFNR) == 0 || ARA == "+quote(strCode));
		cb_gno.Index = 0;
		cb_user.Index = 0;
	}	
}

/********************************************************************************
* 기      능   : 협력업체콤보 변경시
********************************************************************************/
function cb_gno_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex) {
	if(length(trim(strCode)) == 0) {
		if(length(trim(cb_ara.Value)) > 0)
			ds_user.Filter("LENGTH(LIFNR) == 0 || ARA == "+quote(cb_ara.Value));
		else
		ds_user.Filter("LENGTH(LIFNR) == 0 || 1==1");

		cb_user.Index = 0;
	} else {
		ds_user.Filter("LENGTH(LIFNR) == 0 || LIFNR == "+quote(strCode));
		cb_user.Index = 0;
	}	
}

/********************************************************************************
* 기      능   : 원프로젝트 팝업
********************************************************************************/
function btn_pjt_OnClick(obj) {
	var arg = "pjt="+quote(ed_pjt.Text)+" gbn=A";
	gfn_openPopup("CS9601002A",true,arg);
}

function cb_vkbur_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex) {
	ds_cond_tem.SetColumn(0, "VKGRP", substr(strCode,0,2));
	cb_vkgrp.value = "";

	if(length(trim(strCode)) == 0) {
		fn_searchVkgrpCode();
	} else {
		fn_ChgVkgrp();
	}

	ds_user.Filter("1==1");
	cb_user.Index = 0;
	cb_user.Enable = false;
}

function cb_vkgrp_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex) {
	if(length(trim(strCode)) == 0) {
		ds_user.Filter("1==1");
		cb_user.Index = 0;
		cb_user.Enable = false;
	} else {
		cb_user.Enable = true;
		ds_user.Filter("TEAM == " + quote(strCode));
		gfn_insertEmptyRow(ds_user, G_CODE_ALL);
		cb_user.Index = 0;
	}
}
