//=========================================================================================
// [ PART 1 ]
// Form에서 사용될 전역변수를 선언
// FORM에서 사용되는 전역변수는 대문자로 작성하며, F_XXX 의 형태를 갖도록 한다. 

//진행상태 = 10	: 작성         ,수금조건	 = 10 : 현금
//진행상태 = 15	: 전송         ,수금조건	 = 20 : 어음
//진행상태 = 20	: 접수         ,판정결과	 = 10 : 승인
//진행상태 = 30	: 확정         ,판정결과	 = 20 : 불가
//불가사유 = 10	: 기 거래선    ,제품상세구분 = A  : 승강기
//불가사유 = 20	: 기 견적승인  ,제품상세구분 = B  : 주차  
//불가사유 = 30	: 수금조건     ,제품상세구분 = C  : PSD   
//불가사유 = 40	: 납기문제     ,제품상세구분 = D  : 물류  
//불가사유 = 90	: 기타

//=========================================================================================
var userGroup;
var FROW = 0;
//=========================================================================================
// [ PART 2 ]
// Form Loading / Unloading 시 작업 정의
//=========================================================================================
function fn_OnLoadCompleted(obj) 
{
	gfn_initForm(true);

	userGroup = gfn_getUserGroup();

	//코드콤보 설정 : 지역, 구분2 
	var codeArr = ["BS101","BS112","BS102","BS103","BS103","CS131"];
	var dsObjArr = [ds_pst,ds_ncd,ds_sukum,ds_jrt,ds_jrt2,ds_gbn];
	var addTypeArr = [G_CODE_ALL,G_CODE_NONE,G_CODE_NONE,G_CODE_ALL,G_CODE_ALL,G_CODE_ALL];
	gfn_searchCommCode(codeArr, dsObjArr, addTypeArr);

	cal_fcdt.Value = left(gfn_getCurrDate(),6)+"01";
	cal_tcdt.Value = gfn_getCurrDate();

	cb_ord1.index = 0;
	cb_ord2.Index = 0;
	tit_clearActionInfo();
	tit_addSearchActionInfo("bs01:BS0106001A_S1");
	tit_addSearchActionInfo("bs01:BS0106001A_S3");
	tit_addSearchActionInfo("bs01:BS0106001A_S4");

	tit_callService(
        ""
        , ""
        , "ds_cond=ds_cond"
        , "ds_gno=ds_gno ds_user=ds_user ds_ara=ds_ara"
        , ""
        , "fn_afterGno"
        , true
        , true);
}
/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterGno(errCode, errMsg) 
{
	gfn_insertEmptyRow( ds_ara, G_CODE_ALL );
	cb_ara.value = "";
	gfn_insertEmptyRow( ds_gno, G_CODE_ALL );
	cb_gno.value = "";
	gfn_insertEmptyRow( ds_user, G_CODE_ALL );
	cb_user.value = "";

	if(	userGroup == "20")
	{
		ds_ara.Filter("CODE=="+quote(G_AREA_CODE_R));
		cb_ara.Index = 0;
		cb_ara.Enable = false;

		cb_ara_OnChanged(ds_ara,cb_ara.Value,"","","");
	}	
}
/********************************************************************************
* 기      능   : 공통코드가 조회된 후의 Callback 함수 
********************************************************************************/
function fn_completeInit(sCode, sDsObj, sCodeType) 
{
	cb_ara.value = "";
	cb_pst.value = "";
	cb_jrt.value = "";

	if(G_DPT1 == 'B3')
		cb_gbn.Index = 1;
	else if(G_DPT1 == 'B9')
		cb_gbn.Index = 2;
	else if(G_DPT1 == 'B8')
		cb_gbn.Index = 3;
	else if(G_DPT1 == 'C6')
		cb_gbn.Index = 4;
	else
		cb_gbn.Index = 0;		
}
/********************************************************************************
* 기      능   : form UnloadCompleted 
********************************************************************************/
function fn_OnUnloadCompleted(obj) 
{
	return gfn_isFormUnload(obj);
}
