/********************************************************************************
* 기      능   : 조회 
********************************************************************************/
function fn_query() 
{
	// 필수 입력사항 및 일자 체크 
	if ( !gfn_checkFromToObj(cal_fcdt, cal_tcdt, true, st_date.value) )
		return;
/*
	if(cb_user.Value > '')
	{
		ds_cond.SetColumn(0,"USER",cb_user.Text);
	}
	else
	{
		ds_cond.SetColumn(0,"USER","");
	}
*/
	tit_clearActionInfo();
	tit_addSearchActionInfo("bs01:BS0106001A_S2");

	tit_callService(
        ""
        , ""
        , "ds_cond=ds_cond"
        , "ds_list=ds_list"
        , ""
        , "fn_afterQuery"
        , true
        , true);
}
/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterQuery(errCode, errMsg) 
{
	gfn_showMsg("CI00002",ds_list.RowCount()+"");
	ds_list.row = FROW;
}
/********************************************************************************
* 기      능   : 저장
********************************************************************************/
function fn_save() 
{
	if(ds_list.rowcount == 0)
	{
		gfn_showMsg("CW00113",st_param.value);
		return;
	}

	if ( !gfn_isChangeDs(ds_list, true, "")){
		return;
	}

	ds_list1.DeleteAll();
	ds_list2.DeleteAll();

	for(var i=0; i<ds_list.rowcount; i++)
	{
		if(ds_list.GetColumn(i,"FLAG") == 'U') {
			if(length(ds_list.GetColumn(i,"BS101_JRT")) == 0) {
				gfn_showMsg("CW00001", st_pst.value);
				return;
			} else if(ds_list.GetColumn(i,"BS101_JRT") == '20' && length(ds_list.GetColumn(i,"BS101_NCD")) == 0) {
				var arr = [st_panguyl.value, st_bulga.value, st_ncd.value];
				// [${}] 항목의 [${}] 값이 입력될 경우 [${}] 항목은 필수입력입니다.\n확인하여 주십시오.
				gfn_showMsg("CW00144", arr);
				return;
			}
			if(userGroup == '30' && (ds_list.GetColumn(i,"BS101_PST") == 15 || ds_list.GetColumn(i,"BS101_PST") == 20))
			{
				var rows = ds_list1.AddRow();
				ds_list1.SetColumn(rows,"BS101_PST","20");
				ds_list1.SetColumn(rows,"BS101_JRT",ds_list.GetColumn(i,"BS101_JRT"));
				ds_list1.SetColumn(rows,"BS101_NCD",ds_list.GetColumn(i,"BS101_NCD"));
				ds_list1.SetColumn(rows,"BS101_RDT",ds_list.GetColumn(i,"BS101_RDT"));
				ds_list1.SetColumn(rows,"BS101_SEQ",ds_list.GetColumn(i,"BS101_SEQ"));
				ds_list1.SetColumn(rows,"FLAG",ds_list.GetColumn(i,"FLAG"));
			}
			else if(userGroup == '40' && (ds_list.GetColumn(i,"BS101_PST") == 20 || ds_list.GetColumn(i,"BS101_PST") == 30))
			{
				var rows = ds_list2.AddRow();
				ds_list2.SetColumn(rows,"BS101_PST","30");
				ds_list2.SetColumn(rows,"BS101_JRT",ds_list.GetColumn(i,"BS101_JRT"));
				ds_list2.SetColumn(rows,"BS101_NCD",ds_list.GetColumn(i,"BS101_NCD"));
				ds_list2.SetColumn(rows,"BS101_RDT",ds_list.GetColumn(i,"BS101_RDT"));
				ds_list2.SetColumn(rows,"BS101_SEQ",ds_list.GetColumn(i,"BS101_SEQ"));
				ds_list2.SetColumn(rows,"FLAG",ds_list.GetColumn(i,"FLAG"));
			}
			else return;
		}
	}

	tit_clearActionInfo();

	tit_addSaveActionInfo(
		""
		, "bs01:BS0106001A_U1"
		, ""
		, "FLAG"
		, ""
		, -1
		, "");

	tit_addSaveActionInfo(
		""
		, "bs01:BS0106001A_U2"
		, ""
		, "FLAG"
		, ""
		, -1
		, "");

	tit_callService(
		""
		, ""
		, "ds_list1=ds_list1:A ds_list2=ds_list2:A"
		, ""
		, ""
		, "fn_afterSave");
}
/********************************************************************************
* 기      능   : 저장 Callback
********************************************************************************/
function fn_afterSave(errCode, errMsg) 
{
	if ( errCode!= 0 ) 
	{
		gfn_showMsg("CE00001");
	} 
	else 
	{
		gfn_showMsg("CI00003");
		fn_query();
	}
}
/********************************************************************************
* 기      능   : 닫기  
********************************************************************************/
function fn_close() 
{
	close();
}
