/********************************************************************************
* 기      능   : 그리드2에 DATASET변경시
********************************************************************************/
function ds_list_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	obj.fireevent = false;

	switch( strColumnID ) 
	{
		case "CHK":
			if(userGroup == '40')
			{
				if(ds_list.GetColumn(nRow,"BS101_PST") != "20" && ds_list.GetColumn(nRow,"BS101_PST") != "30")
				{
					ds_list.SetColumn(nRow,"CHK",0);
					ds_list.SetColumn(nRow,"FLAG","");
					break;
				}
				if(varNewValue == "1") 
				{
					if((G_DPT1 == 'B3' && ds_list.GetColumn(nRow,"BS101_GBN") == '승강기') ||
					   (G_DPT1 == 'B9' && ds_list.GetColumn(nRow,"BS101_GBN") == '주차') ||
					   (G_DPT1 == 'B8' && ds_list.GetColumn(nRow,"BS101_GBN") == 'PSD') ||
					   (G_DPT1 == 'C6' && ds_list.GetColumn(nRow,"BS101_GBN") == '물류') )
					{   
					ds_list.SetColumn(nRow,"CHK",1);
					ds_list.SetColumn(nRow,"FLAG","U");
					}
					else
					{
						ds_list.SetColumn(nRow,"CHK",0);
						ds_list.SetColumn(nRow,"FLAG","");
					}
				} else {
					ds_list.SetColumn(nRow,"CHK",0);
					ds_list.SetColumn(nRow,"FLAG","");
				}
			}
			else
			{
				break;
			}

			break;
		case "BS101_JRT": //판정결과
			if(userGroup == '30' && (ds_list.GetColumn(nRow,"BS101_PST") != "15" && ds_list.GetColumn(nRow,"BS101_PST") != "20"))
			{
				ds_list.SetColumn(nRow,"CHK",0);
				ds_list.SetColumn(nRow,"FLAG","");
				ds_list.SetColumn(nRow,"BS101_JRT",ds_list.GetOrgColumn(nRow,"BS101_JRT"));
				break;
			}
			if(userGroup == '40' && (ds_list.GetColumn(nRow,"BS101_PST") != "20" && ds_list.GetColumn(nRow,"BS101_PST") != "30"))
			{
				ds_list.SetColumn(nRow,"CHK",0);
				ds_list.SetColumn(nRow,"FLAG","");
				ds_list.SetColumn(nRow,"BS101_JRT",ds_list.GetOrgColumn(nRow,"BS101_JRT"));
				break;
			}

			if(ds_list.GetColumn(nRow,"BS101_JRT") != '20')
			{
				ds_list.SetColumn(nRow,"BS101_NCD","");
			}

			if(trim(ds_list.GetColumn(nRow,"BS101_JRT")) == trim(ds_list.GetOrgColumn(nRow,"BS101_JRT")) && 
			   trim(ds_list.GetColumn(nRow,"BS101_NCD")) == trim(ds_list.GetOrgColumn(nRow,"BS101_NCD")) ) 
			{
				ds_list.SetColumn(nRow,"CHK",0);
				ds_list.SetColumn(nRow,"FLAG","");
			}
			else 
			{
				if((G_DPT1 == 'B3' && ds_list.GetColumn(nRow,"BS101_GBN") == '승강기') ||
				   (G_DPT1 == 'B9' && ds_list.GetColumn(nRow,"BS101_GBN") == '주차') ||
				   (G_DPT1 == 'B8' && ds_list.GetColumn(nRow,"BS101_GBN") == 'PSD') ||
				   (G_DPT1 == 'C6' && ds_list.GetColumn(nRow,"BS101_GBN") == '물류') )
				{   
					ds_list.SetColumn(nRow,"CHK",1);
					ds_list.SetColumn(nRow,"FLAG","U");
				}
				else
				{
					gfn_showMsg("CW00185", st_panguyl.Value);

					ds_list.SetColumn(nRow,"CHK",0);
					ds_list.SetColumn(nRow,"FLAG","");
					ds_list.SetColumn(nRow,"BS101_JRT",ds_list.GetOrgColumn(nRow,"BS101_JRT"));
					ds_list.SetColumn(nRow,"BS101_NCD",ds_list.GetOrgColumn(nRow,"BS101_NCD"));
				}
			}
			break;
		case "BS101_NCD": //불가사유
			if(userGroup == '30' && (ds_list.GetColumn(nRow,"BS101_PST") != "15" && ds_list.GetColumn(nRow,"BS101_PST") != "20"))
			{
				ds_list.SetColumn(nRow,"CHK",0);
				ds_list.SetColumn(nRow,"FLAG","");
				ds_list.SetColumn(nRow,"BS101_NCD",ds_list.GetOrgColumn(nRow,"BS101_NCD"));
				break;
			}
			if(userGroup == '40' && (ds_list.GetColumn(nRow,"BS101_PST") != "20" && ds_list.GetColumn(nRow,"BS101_PST") != "30"))
			{
				ds_list.SetColumn(nRow,"CHK",0);
				ds_list.SetColumn(nRow,"FLAG","");
				ds_list.SetColumn(nRow,"BS101_NCD",ds_list.GetOrgColumn(nRow,"BS101_NCD"));
				break;
			}

			if(trim(ds_list.GetColumn(nRow,"BS101_JRT")) == trim(ds_list.GetOrgColumn(nRow,"BS101_JRT")) && 
			   trim(ds_list.GetColumn(nRow,"BS101_NCD")) == trim(ds_list.GetOrgColumn(nRow,"BS101_NCD")) ) 
			{
				ds_list.SetColumn(nRow,"CHK",0);
				ds_list.SetColumn(nRow,"FLAG","");
			} 
			else 
			{
				if((G_DPT1 == 'B3' && ds_list.GetColumn(nRow,"BS101_GBN") == '승강기') ||
				   (G_DPT1 == 'B9' && ds_list.GetColumn(nRow,"BS101_GBN") == '주차') ||
				   (G_DPT1 == 'B8' && ds_list.GetColumn(nRow,"BS101_GBN") == 'PSD') ||
				   (G_DPT1 == 'C6' && ds_list.GetColumn(nRow,"BS101_GBN") == '물류') )
				{   
					ds_list.SetColumn(nRow,"CHK",1);
					ds_list.SetColumn(nRow,"FLAG","U");
				}
				else
				{
					gfn_showMsg("CW00185", st_ncd.Value);

					ds_list.SetColumn(nRow,"CHK",0);
					ds_list.SetColumn(nRow,"FLAG","");
					ds_list.SetColumn(nRow,"BS101_NCD",ds_list.GetOrgColumn(nRow,"BS101_NCD"));
				}
			}
			break;
	}	
	obj.fireevent = true;	
}
/********************************************************************************
* 기      능   : 그리드 더블 클릭시 팝업연동
********************************************************************************/
function grd_list_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	if(nCell != 23 && nCell != 0 && nCell != 1 )
	{
		var arg = "gubun='S'";
		gfn_openPopup("BS0101002A",true,arg);
		FROW = ds_list.row;
		fn_query();
	}	
}
/********************************************************************************
* 기      능   : 검증버튼 클릭시 팝업연동
********************************************************************************/
function grd_list_OnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	if(nCell == 23)
	{
		ds_list.row = nRow;
		var arg = "gubun='S'";
		gfn_openPopup("BS0106001B",true,arg);
		FROW = ds_list.row;
	}	
}
function fn_enableYn()
{
	if(userGroup == "40")
	{
		return "checkbox";
	}	
	else 
	{
		return "none";
	}	
}
/********************************************************************************
* 기      능   : 지역콤보 변경시
********************************************************************************/
function cb_ara_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	if(length(trim(strCode)) == 0) 
	{
		ds_gno.Filter("LENGTH(ARA) == 0 || 1==1");
		ds_user.Filter("LENGTH(LIFNR) == 0 || 1==1");
		cb_gno.Index = 0;
		cb_user.Index = 0;
	}	
	else 
	{
		ds_gno.Filter("LENGTH(ARA) == 0 || ARA == "+quote(strCode));
		ds_user.Filter("LENGTH(LIFNR) == 0 || ARA == "+quote(strCode));
		cb_gno.Index = 0;
		cb_user.Index = 0;
	}	
}

function cb_gno_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	if(length(trim(strCode)) == 0) 
	{
		if(length(trim(cb_ara.Value)) > 0)
			ds_user.Filter("LENGTH(LIFNR) == 0 || ARA == "+quote(cb_ara.Value));
		else
			ds_user.Filter("LENGTH(LIFNR) == 0 || 1==1");

		cb_user.Index = 0;
	}	
	else 
	{
		ds_user.Filter("LENGTH(LIFNR) == 0 || LIFNR == "+quote(strCode));
		cb_user.Index = 0;
	}
}
