function fn_OnLoadCompleted(obj) 
{
	gfn_initForm(true);

	//코드콤보 설정 : 지역, 구분2 
	var codeArr = ["BS101","CS131","BS105","BS106","BS106"];
	var dsObjArr = [ds_pst,ds_gbn,ds_rsn,ds_comp1,ds_comp2];
	var addTypeArr = [G_CODE_ALL,G_CODE_ALL,G_CODE_ALL,G_CODE_ALL,G_CODE_ALL];
	gfn_searchCommCode(codeArr, dsObjArr, addTypeArr);

	cal_fcdt.Value = left(gfn_getCurrDate(),6)+"01";
	cal_tcdt.Value = gfn_getCurrDate();

	tit_clearActionInfo();
	tit_addSearchActionInfo("bs01:BS0101006A_S2");
	tit_callService(
        ""
        , ""
        , ""
        , "ds_desc=ds_desc"
        , ""
        , ""
        , true
        , true);
}
/********************************************************************************
* 기      능   : 공통코드가 조회된 후의 Callback 함수 
********************************************************************************/
function fn_completeInit(sCode, sDsObj, sCodeType)
{
	ds_pst.Filter("CODE=='' || CODE=='10' || CODE=='15'");
	cb_pst.index = 0;
	cb_gbn.Index = 0;
	ed_gno.value = G_VEND_NAME;
	cal_fcdt.SetFocus();
}
/********************************************************************************
* 기      능   : form UnloadCompleted 
********************************************************************************/
function fn_OnUnloadCompleted(obj) 
{
	return gfn_isFormUnload(obj);
}
