/********************************************************************************
* 기      능   : 조회 
********************************************************************************/
function fn_query() 
{
	// 필수 입력사항 및 일자 체크 
	if ( !gfn_checkFromToObj(cal_fcdt, cal_tcdt, true, st_date.value) )
	{
		return;
	}

	// sort mark 삭제 
	gfn_clearGridSortMark(grd_list);
	ds_list.row = -1;
	grd_list.SetCellProp("head", 1, "Text", 0);

	if(G_USER_GROUP_B == 'B10' && ds_desc.getColumn(0,"DESC") == 'Y') ds_cond.SetColumn(0,"DESC","Y");
	else ds_cond.SetColumn(0,"DESC","N");

	tit_clearActionInfo();
	tit_addSearchActionInfo("bs01:BS0108001A_S1");
	tit_callService(
        ""
        , ""
        , "ds_cond=ds_cond"
        , "ds_list=ds_list"
        , ""
        , "fn_afterQuery"
        , true
        , true);
}
/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterQuery(errCode, errMsg) 
{
	gfn_showMsg("CI00002",ds_list.RowCount()+"");
}
/********************************************************************************
* 기      능   : 닫기  
********************************************************************************/
function fn_close() 
{
	close();
}
