/********************************************************************************
* 기      능   : 그리드 더블 클릭시 팝업연동
********************************************************************************/
function grd_list_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	if(nCell != 0 && nCell != 1 && nCell != 9 && nCell != 10 && nCell != 11 && nCell != 12 && nCell != 13 && 
	   nCell != 14 && nCell != 15 && nCell != 16 && nCell != 17 && nCell != 18 && nCell != 19 && nCell != 20 && nCell != 21 )
	{   
		var arg = "gubun='S'";
		gfn_openPopup("BS0101001A",true,arg);
		fn_query();
	}	
}
/********************************************************************************
* 기      능   : 그리드 헤더 클릭시 정렬 처리 
********************************************************************************/
function grd_list_OnHeadClick(obj,nCell,nX,nY,nPivotIndex)
{
	if ( nCell == 0 ) {
		gfn_selectGridCol("grd_list");
	} else if ( nCell == 1 ) {
		gfn_selectAllGrid(obj,nCell,nCell,true);
	} else {
		gfn_sortGrid(obj,nCell);
	}	
}
