/********************************************************************************
* 기      능   : 저장
********************************************************************************/
function fn_saveTrans(gbn) 
{
	if(ds_list.rowcount == 0)
	{
		gfn_showMsg("CW00113",st_param.value);
		return;
	}

	if ( !gfn_isChangeDs(ds_list, true, ""))
	{
		return;
	}

	ds_list2.ClearData();
	var rows = 0;
	for(var i=0; i<ds_list.rowcount; i++)
	{
		if(ds_list.GetColumn(i,"FLAG") == 'U') 
		{
			ds_list2.AddRow();
			ds_list2.SetColumn(rows,"CHK","1");
			ds_list2.SetColumn(rows,"FLAG","U");
			ds_list2.SetColumn(rows,"BS101_RDT",ds_list.GetColumn(i,"BS101_RDT"));
			ds_list2.SetColumn(rows,"BS101_SEQ",ds_list.GetColumn(i,"BS101_SEQ"));
			ds_list2.SetColumn(rows,"BS101_GBN",ds_list.GetColumn(i,"BS101_GBN"));
			ds_list2.SetColumn(rows,"BS101_CID",ds_list.GetColumn(i,"BS101_CID"));
			ds_list2.SetColumn(rows,"BS101_AYM",ds_list.GetColumn(i,"BS101_AYM"));
			ds_list2.SetColumn(rows,"BS101_ASQ",ds_list.GetColumn(i,"BS101_ASQ"));
			ds_list2.SetColumn(rows,"BS101_NO",ds_list.GetColumn(i,"BS101_NO"));
			ds_list2.SetColumn(rows,"BS101_GNM",ds_list.GetColumn(i,"BS101_GNM"));
			ds_list2.SetColumn(rows,"BS101_SPT",ds_list.GetColumn(i,"BS101_SPT"));
			ds_list2.SetColumn(rows,"BS101_QTY",ds_list.GetColumn(i,"BS101_QTY"));
			ds_list2.SetColumn(rows,"BS101_PST",ds_list.GetColumn(i,"BS101_PST"));
			ds_list2.SetColumn(rows,"BS101_GNO",ds_list.GetColumn(i,"BS101_GNO"));
			ds_list2.SetColumn(rows,"BS101_JRT",ds_list.GetColumn(i,"BS101_JRT"));
			ds_list2.SetColumn(rows,"BS103_TDT",ds_list.GetColumn(i,"BS103_TDT"));
			ds_list2.SetColumn(rows,"BS103_GDT",ds_list.GetColumn(i,"BS103_GDT"));
			ds_list2.SetColumn(rows,"BS103_COMA",ds_list.GetColumn(i,"BS103_COMA"));
			ds_list2.SetColumn(rows,"BS103_AMTA",ds_list.GetColumn(i,"BS103_AMTA"));
			ds_list2.SetColumn(rows,"BS103_AMTW",ds_list.GetColumn(i,"BS103_AMTW"));
			ds_list2.SetColumn(rows,"BS103_AMTT",ds_list.GetColumn(i,"BS103_AMTT"));
			ds_list2.SetColumn(rows,"BS103_RSN",ds_list.GetColumn(i,"BS103_RSN"));
			ds_list2.SetColumn(rows,"BS103_COMB",ds_list.GetColumn(i,"BS103_COMB"));
			ds_list2.SetColumn(rows,"BS103_AMTB",ds_list.GetColumn(i,"BS103_AMTB"));
			ds_list2.SetColumn(rows,"BS103_RMK",ds_list.GetColumn(i,"BS103_RMK"));
			if(gbn == "A") ds_list2.SetColumn(rows,"BS103_PST","10");
			else if(gbn == "B") ds_list2.SetColumn(rows,"BS103_PST","15");
			ds_list2.SetColumn(rows,"BS102_TYP",ds_list.GetColumn(i,"BS102_TYP"));

			rows++;
		}	
	}	

	if(ds_list2.rowcount == 0)
	{
		gfn_showMsg("CW00113",st_param.value);
		return;
	}

	for(var i=0; i<ds_list2.rowcount; i++)
	{
		if(ds_list2.GetColumn(i,"FLAG") == 'U') {
			if(length(ds_list2.GetColumn(i,"BS103_TDT")) == 0) {
				ds_list.row = ds_list.FindRow("BS101_NO",ds_list2.GetColumn(i,"BS101_NO"));
				gfn_showMsg("CW00001", st_tdt.value);
				return;
			}
			if(length(ds_list2.GetColumn(i,"BS103_COMA")) == 0) {
				ds_list.row = ds_list.FindRow("BS101_NO",ds_list2.GetColumn(i,"BS101_NO"));
				gfn_showMsg("CW00001", st_coma.value);
				return;
			} 
			if(ds_list2.GetColumn(i,"BS103_AMTA") <= 0) {
				ds_list.row = ds_list.FindRow("BS101_NO",ds_list2.GetColumn(i,"BS101_NO"));
				gfn_showMsg("CW00001", st_amta.value);
				return;
			} 
			if(ds_list2.GetColumn(i,"BS103_AMTW") <= 0) {
				ds_list.row = ds_list.FindRow("BS101_NO",ds_list2.GetColumn(i,"BS101_NO"));
				gfn_showMsg("CW00001", st_amtw.value);
				return;
			} 
			if(ds_list2.GetColumn(i,"BS103_AMTT") <= 0) {
				ds_list.row = ds_list.FindRow("BS101_NO",ds_list2.GetColumn(i,"BS101_NO"));
				gfn_showMsg("CW00001", st_amtt.value);
				return;
			} 
			if(ds_list2.GetColumn(i,"BS103_COMA") != '10' && length(ds_list2.GetColumn(i,"BS103_RSN")) == 0) {
				ds_list.row = ds_list.FindRow("BS101_NO",ds_list2.GetColumn(i,"BS101_NO"));
				var arr = [st_coma.value, st_tasa.value, st_rsn.value];
				// [${}] 항목의 [${}] 값이 입력될 경우 [${}] 항목은 필수입력입니다.\n확인하여 주십시오.
				gfn_showMsg("CW00144", arr);
				return;
			}
			if(ds_list2.GetColumn(i,"BS103_COMA") == '10' && length(ds_list2.GetColumn(i,"BS103_GDT")) == 0) {
				ds_list.row = ds_list.FindRow("BS101_NO",ds_list2.GetColumn(i,"BS101_NO"));
				var arr = [st_coma.value, st_jasa.value, st_gdt.value];
				// [${}] 항목의 [${}] 값이 입력될 경우 [${}] 항목은 필수입력입니다.\n확인하여 주십시오.
				gfn_showMsg("CW00144", arr);
				return;
			}
		}
	}

	tit_clearActionInfo();
	tit_addSelectExActionInfo("bs01:BS0108001A_S2", "bs01:BS0108001A_I1", "bs01:BS0108001A_U1");
	tit_callService(
		""
		, ""
		, "ds_list=ds_list2:A"
		, ""
		, ""
		, "fn_afterSave");
}
/********************************************************************************
* 기      능   : 저장 Callback
********************************************************************************/
function fn_afterSave(errCode, errMsg) 
{
	ds_list2.ClearData();
	if ( errCode!= 0 ) 
	{
		gfn_showMsg("CE00001");
	} 
	else 
	{
		gfn_showMsg("CI00003");
		fn_query();
	}

}

/********************************************************************************
* 기      능   : DS_LIST 칼럼 변경시
********************************************************************************/
function ds_list_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	obj.fireevent = false;
	switch( strColumnID )
	{
		case "CHK":
			if(varNewValue == '1' && (ds_list.GetColumn(nRow,"BS103_PST") == "10" || ds_list.GetColumn(nRow,"BS103_PST") == ""))
			{
				ds_list.SetColumn(nRow,"CHK","1");
				ds_list.SetColumn(nRow,"FLAG","U");
			}
			else
			{
				ds_list.SetColumn(nRow,"CHK","0");
				ds_list.SetColumn(nRow,"FLAG","");
			}
			break;
		case "BS103_COMA":
			if(ds_list.GetColumn(nRow,"BS103_COMA") == "10" )
			{
				ds_list.SetColumn(nRow,"BS103_RSN","");
//				ds_list.SetColumn(nRow,"BS103_COMB","");
//				ds_list.SetColumn(nRow,"BS103_AMTB","0");
			}
			break;
		case "BS103_RSN":
			if(ds_list.GetColumn(nRow,"BS103_COMA") == "10" )
			{
				ds_list.SetColumn(nRow,"BS103_RSN","");
			}
			break;
		case "BS103_COMB":
			if(ds_list.GetColumn(nRow,"BS103_COMA") == "10" )
			{
//				ds_list.SetColumn(nRow,"BS103_COMB","");
			}
			break;
		case "BS103_AMTA":
			if(ds_list.GetColumn(nRow,"BS103_COMA") == "10")
			{
				ds_list.SetColumn(nRow,"BS103_AMTT",ds_list.GetColumn(nRow,"BS103_AMTA"));
			}
			if(ds_list.GetColumn(nRow,"BS103_AMTA") != 0 )
			{
				var rate = ROUND(ds_list.GetColumn(nRow,"BS103_AMTW")/ds_list.GetColumn(nRow,"BS103_AMTA")*100,2);
				ds_list.SetColumn(nRow,"SI_RATE",rate);
				rate = ROUND(ds_list.GetColumn(nRow,"BS103_AMTT")/ds_list.GetColumn(nRow,"BS103_AMTA")*100,2);
				ds_list.SetColumn(nRow,"TU_RATE",rate);
			}
			else
			{
				ds_list.SetColumn(nRow,"SI_RATE",0);
				ds_list.SetColumn(nRow,"TU_RATE",0);
			}
			break;
		case "BS103_AMTW":
			if(ds_list.GetColumn(nRow,"BS103_AMTA") != 0)
			{
				var rate = ROUND(ds_list.GetColumn(nRow,"BS103_AMTW")/ds_list.GetColumn(nRow,"BS103_AMTA")*100,2);
				ds_list.SetColumn(nRow,"SI_RATE",rate);

			}
			else
			{
				ds_list.SetColumn(nRow,"SI_RATE",0);
			}
			if(ds_list.GetColumn(nRow,"BS103_AMTB") != 0) 
			{
				var rate = ROUND(ds_list.GetColumn(nRow,"BS103_AMTW")/ds_list.GetColumn(nRow,"BS103_AMTB")*100,2);
				ds_list.SetColumn(nRow,"TU2_RATE",rate);
			}
			else
			{
				ds_list.SetColumn(nRow,"TU2_RATE",0);
			}
			break;
		case "BS103_AMTT":
			if(ds_list.GetColumn(nRow,"BS103_AMTA") != 0)
			{
				var rate = ROUND(ds_list.GetColumn(nRow,"BS103_AMTT")/ds_list.GetColumn(nRow,"BS103_AMTA")*100,2);
				ds_list.SetColumn(nRow,"TU_RATE",rate);
			}
			else
			{
				ds_list.SetColumn(nRow,"TU_RATE",0);
			}
			break;
		case "BS103_AMTB":
			if(ds_list.GetColumn(nRow,"BS103_AMTB") != 0 )
			{
				var rate = ROUND(ds_list.GetColumn(nRow,"BS103_AMTW")/ds_list.GetColumn(nRow,"BS103_AMTB")*100,2);
				ds_list.SetColumn(nRow,"TU2_RATE",rate);
			}
			else
			{
				ds_list.SetColumn(nRow,"TU2_RATE",0);
			}
			break;
	}
	obj.fireevent = true;
}
/********************************************************************************
* 기      능   : 그리드에서 날짜 입력시
********************************************************************************/
function grd_list_OnExpandEdit(obj,nRow,nCell,strVal,nPivotIndex)
{
	var rect = obj.GetCellRect(nRow, ncell);

	cal_temp.top = rect[1] + 60;
	if (cal_temp.top > 400)
	{
		cal_temp.top = 400;
	}
	cal_temp.Left = rect[0] + 10;
	if (cal_temp.Left > 790)
	{
		cal_temp.Left = 790;
	}
	cal_temp.Visible = true;

	if(nCell == 9) {
		cal_temp.value = ds_list.getColumn(nRow, "BS103_TDT");
	} else if(nCell == 10) {
		cal_temp.value = ds_list.getColumn(nRow, "BS103_GDT");
	}

//	cal_temp.value = ds_list.getColumn(nRow, "BS103_TDT");
	cal_temp.SetFocus();
}
/********************************************************************************
* 기      능   : 달력에서 일자 선택했을 경우
********************************************************************************/
function cal_temp_OnDayClick(obj,strText)
{
	if(grd_list.GetClickedCell() == 9) {
		ds_list.SetColumn(ds_list.row, "BS103_TDT", strText);
	} else if(grd_list.GetClickedCell() == 10) {
		ds_list.SetColumn(ds_list.row, "BS103_GDT", strText);
	}
	obj.Visible = false;
}
