function fn_OnLoadCompleted(obj) 
{
var mesg;
	gfn_initForm(true);

	userGroup = gfn_getUserGroup();

	//기준일자 초기화
	cb_datgbn.Index = 0;
	cal_fdt.Value 		= left(gfn_getCurrDate(),6)+"01";
	cal_tdt.Value 		= gfn_getCurrDate();

	//코드콤보 설정 : 지역, 구분2 
	var codeArr = ["CS131"];
	var dsObjArr = [ds_gbn];
	var addTypeArr = [G_CODE_ALL];
	gfn_searchCommCode(codeArr, dsObjArr, addTypeArr);

	//영업 
	if (G_USER_GROUP_B = "") {
		fn_disableComboArea_B(true);
		fn_hide_SD_amounts();
	}
	else
		fn_Init_ComboArea_B();
	//설치
	if (G_USER_GROUP_E = "")
	 	fn_disableComboArea_E(true);
	else
		fn_Init_ComboArea_E();
}
/********************************************************************************
* 영업지역 콤보구성
********************************************************************************/
function fn_Init_ComboArea_B() {
	tit_clearActionInfo();
	tit_addSearchActionInfo("bs01:BS0106001A_S1");
	tit_addSearchActionInfo("bs01:BS0106001A_S3");
	tit_addSearchActionInfo("bs01:BS0106001A_S4");

	tit_callService(
        ""
        , ""
        , "ds_cond=ds_cond"
        , "ds_gno=ds_gno ds_user=ds_user ds_ara=ds_ara"
        , ""
        , "fn_afterGno"
        , true
        , true);
}
/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterGno(errCode, errMsg) 
{
	gfn_insertEmptyRow( ds_ara, G_CODE_ALL );
	cb_ara.value = "";
	gfn_insertEmptyRow( ds_gno, G_CODE_ALL );
	cb_gno.value = "";
	gfn_insertEmptyRow( ds_user, G_CODE_ALL );
	cb_user.value = "";

	if(G_USER_GROUP_B == "B20")
	{
		ds_ara.Filter("CODE=="+quote(G_AREA_CODE_R));
		cb_ara.Index = 0;

		cb_ara_OnChanged(ds_ara,cb_ara.Value,"","","");
	}
	else if(G_USER_GROUP_B == "B10")
	{
		cb_ara.Index = 0;

		ds_gno.Filter("CODE=="+quote(G_VEND_CODE));
		cb_gno.Index = 0;

		cb_gno_OnChanged(ds_gno,cb_gno.Value,"","","");
	}
	fn_disableComboArea_B(false);
}
/********************************************************************************
* 영업지역 콤보 userGroup별 부분 비활성화
********************************************************************************/
function fn_disableComboArea_B(all_disabled)
{
	if(all_disabled == true) {
		cb_ara.Enable 	= false;
		cb_gno.Enable 	= false;
		cb_user.Enable	= false;
	} else {
		switch(G_USER_GROUP_B) {
		case "B10":
			cb_ara.Enable = false;
			cb_gno.Enable = false;
			break;
		case "B20":
			cb_ara.Enable = false;
			break;
		default:
		}
	}
}
/********************************************************************************
* 영업관련 금액 숨기기
********************************************************************************/
function fn_hide_SD_amounts() {
	fn_hide_grid_column("KEUM_A");
	fn_hide_grid_column("KEUM_B");
	fn_hide_grid_column("KEUM_C");
	fn_hide_grid_column("KEUM_SUM");
	fn_hide_grid_column("SUKEUM");
}
/********************************************************************************
* Hide Grid Column
********************************************************************************/
function fn_hide_grid_column(colid) {
	grd_list.SetCellProp("head",	grd_list.GetBindCellIndex("body",colid),"Display","null");
	grd_list.SetCellProp("body",	grd_list.GetBindCellIndex("body",colid),"Display","null");
	grd_list.SetCellProp("summary",	grd_list.GetBindCellIndex("body",colid),"Display","null");
	grd_list.SetColProp(grd_list.GetBindCellIndex("body",colid),"Width",0);
}
/********************************************************************************
* 설치지역 콤보구성
********************************************************************************/
function fn_Init_ComboArea_E() {
	if( G_USER_GROUP_E == 'E10' )
	{
		tit_clearActionInfo();
		tit_addSearchActionInfo("ps02:PS0201001A_S11");
		tit_callService(
			""
			, ""
			, ""
			, "ds_zzlifnr_cd=ds_zzlifnr_cd"
			, ""
			, "fn_afterZzlifnrSearch"
			, true,true);
	}
	else if( G_USER_GROUP_E == 'E30' )
	{
		tit_clearActionInfo();
		tit_addSearchActionInfo("ps02:PS0201001A_S31");
		tit_addSearchActionInfo("ps02:PS0201001A_S32");
		tit_addSearchActionInfo("ps02:PS0201001A_S33");
		tit_callService(
			""
			, ""
			, ""
			, "ds_zzactss_cd=ds_zzactss_cd ds_zzpmnum_cd=ds_zzpmnum_cd ds_zzlifnr_cd=ds_zzlifnr_cd"
			, ""
			, "fn_afterZzactssSearch"
			, true,true);
	}
	else if( G_USER_GROUP_E == 'E40' )
	{
		//코드콤보 설정 : 지역, 구분2
		//var codeArr = ["PS003"];
		//var dsObjArr = [ds_zzactss_cd];
		//var addTypeArr = [G_CODE_ALL];
		//gfn_searchCommCode(codeArr, dsObjArr, addTypeArr);
		gfn_initDs(ds_zzactss_cond,true);
		ds_zzactss_cond.setColumn(0,"LANG",G_LANG);
		ds_zzactss_cond.setColumn(0,"S_CODE","PS003");

		tit_clearActionInfo();
		tit_addSearchActionInfo("wb01:ZWBTZ901_S1");
		tit_addSearchActionInfo("ps02:PS0201001A_S33");
		tit_callService(
			""
			, ""
			, "ds_cond=ds_zzactss_cond"
			, "ds_zzactss_cd=ds_list ds_zzlifnr_cd=ds_zzlifnr_cd"
			, ""
			, "fn_afterSearchAll"
			, true,true);

	}
	fn_disableComboArea_E(false);
}
/********************************************************************************
* 설치지역 콤보 userGroup별 부분 비활성화
********************************************************************************/
function fn_disableComboArea_E(all_disabled)
{
	if(all_disabled == true) {
		cb_zzactss.Enable = false;
		cb_zzpmnum.Enable = false;
		cb_zzlifnr.Enable = false;
	} else {
		ufn_enableBtn("query", true);
		ufn_enableBtn("save", false);
		switch(G_USER_GROUP_E) {
		case "E10":
			cb_zzactss.Enable = false;
			cb_zzpmnum.Enable = false;
			cb_zzlifnr.Enable = false;
			break;
		case "E30":
			cb_zzactss.Enable = false;
			cb_zzpmnum.Enable = true;
			cb_zzlifnr.Enable = true;
			break;
		case "E40":
			cb_zzactss.Enable = true;
			cb_zzpmnum.Enable = true;
			cb_zzlifnr.Enable = true;
			break;
		default:
			cb_zzactss.Enable = false;
			cb_zzpmnum.Enable = false;
			cb_zzlifnr.Enable = false;
		}
	}
}
/********************************************************************************
* 기      능   : 조회 Callback  - 설치협력사 로그인시 협력업체코드 첫번째 선택
********************************************************************************/
function fn_afterZzlifnrSearch(errCode, errMsg) {
	cb_zzlifnr.Index = 0;
}
/********************************************************************************
* 기      능   : 조회 Callback  - 설치사무소 로그인시 사무소코드 첫번째 선택
********************************************************************************/
function fn_afterZzactssSearch(errCode, errMsg) {
	cb_zzactss.Index = 0;

	gfn_insertEmptyRow( ds_zzlifnr_cd, G_CODE_ALL );
	gfn_insertEmptyRow( ds_zzpmnum_cd, G_CODE_ALL );

	cb_zzpmnum.value = "";
	cb_zzlifnr.value = "";
}
/********************************************************************************
* 기      능   : 조회 Callback  - 협력업체 전체추가
********************************************************************************/
function fn_afterSearchAll(errCode, errMsg) {
	gfn_insertEmptyRow( ds_zzlifnr_cd, G_CODE_ALL );
	gfn_insertEmptyRow( ds_zzactss_cd, G_CODE_ALL );
	cb_zzlifnr.value = "";
	cb_zzactss.Index = 0;
}
/********************************************************************************
* 기      능   : 공통코드가 조회된 후의 Callback 함수 
********************************************************************************/
function fn_completeInit(sCode, sDsObj, sCodeType)
{
	cb_ara.value = "";

	if(G_DPT1 == 'B2')
		cb_gbn.Index = 1;
	else if(G_DPT1 == 'B9')
		cb_gbn.Index = 2;
	else if(G_DPT1 == 'B8')
		cb_gbn.Index = 3;
	else if(G_DPT1 == 'C6')
		cb_gbn.Index = 4;
	else
		cb_gbn.Index = 0;

	ds_gbn.Filter("CODE=='' || CODE=='A' || CODE=='B'");
	cb_gbn.index = 0;

	cb_state.index = 0;
}
