/********************************************************************************
* 기      능   : 조회 
********************************************************************************/
function fn_query() 
{
	// 필수 입력사항 및 일자 체크 
	if ( !gfn_checkFromToObj(cal_fdt, cal_tdt, true, st_tdate.value) )
	{
		return;
	}

	var arr = [cb_datgbn.Text, '12'];
	if(gfn_getDayCnt(ds_days, cal_fdt.Value, cal_tdt.Value) > 365) {
		gfn_showAlert("CW10087", arr);
		cal_frymd.SetFocus();
		return false;
	}

	// sort mark 삭제 
	gfn_clearGridSortMark(grd_list);
	ds_list.row = -1;


	// sql 
	ufn_SetCond();

	trace(ds_cond.SaveXML());

	tit_clearActionInfo();
	tit_addSearchActionInfo("bs01:BS0109001A_S1");
	tit_callService(
        ""
        , ""
        , "ds_cond=ds_cond"
        , "ds_list=ds_list"
        , ""
        , "fn_afterQuery"
        , true
        , true);
}




function ufn_SetCond() 
{
	var sqlstr;  

	if(cb_datgbn.Value == "APPLD")
	{
		sqlstr += " AND PS.APPLD BETWEEN '"+replace(cal_fdt.Value, ".", "")+"' AND '"+replace(cal_tdt.Value, ".", "")+"'";
	}
	else if(cb_datgbn.Value == "APPLS")
	{
		sqlstr += " AND PS.APPLS BETWEEN '"+replace(cal_fdt.Value, ".", "")+"' AND '"+replace(cal_tdt.Value, ".", "")+"'";
	}
	else if(cb_datgbn.Value == "OPLES")
	{
		sqlstr += " AND PS.OPELS BETWEEN '"+replace(cal_fdt.Value, ".", "")+"' AND '"+replace(cal_tdt.Value, ".", "")+"'";
	}

	ds_cond.setColumn(0, "SQLSTR", sqlstr);
}



