var gubun = "I";

//=========================================================================================
// [ PART 2 ]
// Form Loading / Unloading 시 작업 정의
//=========================================================================================
function fn_OnLoadCompleted(obj) 
{
	global.language = G_LANG;
	gfn_initForm();


	//코드콤보 설정 : 지급조건,인도조건,진행상태
	var codeArr = ["ZTERM1","INCO1","SOPOS","LAND1","ZMAKE","ZTYPE","VRKME","LAND1"];
	var dsObjArr = [ds_zterm,ds_inco1,ds_sopos,ds_land1,ds_zmake,ds_ztype,ds_vrkme,ds_inland];
	var addTypeArr = [G_CODE_BLANK,G_CODE_BLANK,G_CODE_BLANK,G_CODE_BLANK,G_CODE_NONE,G_CODE_NONE,G_CODE_NONE,G_CODE_BLANK];
	var orderTypeArr = ["CTEXT1","CTEXT1","CTEXT1","CTEXT1","CTEXT1","CTEXT1","CTEXT1,DTEXT1","CTEXT1"];
	gfn_searchCommCode2(codeArr, dsObjArr, addTypeArr,orderTypeArr);

	tit_clearActionInfo();
	tit_addSearchActionInfo("bs02:BS0201001B_S5");

	tit_callService(
        ""
        , ""
        , ""
        , "ds_imemp=ds_imemp"
        , ""
        , ""
        , true
        , true);

	if(isExistVar("fa_kunnr", true))
	{
		ds_cond.SetColumn(0,"KUNNR",fa_kunnr);
		ds_cond.SetColumn(0,"INELN",fa_ineln);
		ds_cond.SetColumn(0,"INSEQ",fa_inseq);
		fn_query();
	} 

}
/********************************************************************************
* 기      능   : 공통코드가 조회된 후의 Callback 함수 
********************************************************************************/
function fn_completeInit(sCode, sDsObj, sCodeType) 
{
	if(!isExistVar("fa_kunnr", true))
	{
		ds_head.AddRow();
		cal_soym.Text = SubStr(gfn_getCurrDate(),0,6);
		cb_inco1.index = 0;
		cb_zterm.index = 0;
		cb_sopos.index = 0;

	}
	if(user_grp > "20") 
	{   
	    // 해외영업부 권한
		if(G_VEND_CODE ="0000009906" || G_VEND_CODE ="0000009922") { 
			cb_imemp.Enable = true;
		} 

	}
}
/********************************************************************************
* 기      능   : form UnloadCompleted 
********************************************************************************/
function fn_OnUnloadCompleted(obj) 
{
	return gfn_isFormUnload(obj);
}
