/********************************************************************************
* 기      능   : 조회 
********************************************************************************/
function fn_query() 
{
	tit_clearActionInfo();
	tit_addSearchActionInfo("bs02:BS0201001B_S1");
	tit_addSearchActionInfo("bs02:BS0201001B_S2");

	tit_callService(
        ""
        , ""
        , "ds_cond=ds_cond"
        , "ds_head=ds_head ds_list=ds_list"
        , ""
        , "fn_afterQuery"
        , true
        , true);
}
/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterQuery(errCode, errMsg) 
{
	gfn_showSearchMsg(grd_list);

	if(ds_head.rowcount() == 0) 
	{
		ds_head.AddRow();
	}
	else
	{
		gubun = "U";
	}
}
/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterUser(errCode, errMsg) 
{
}
/********************************************************************************
* 기      능   : 저장
********************************************************************************/
function fn_save() 
{
	if(gfn_isNull(cb_land1, true, domain.bs.land1)) return false; // 국가
	if(gfn_isNull(ed_post1, true, domain.bs.post1)) return false; // 현장명
	if(gfn_isNull(ed_kunnt, true, domain.bs.kunnt)) return false; // 고객명 
	if(gfn_isNull(me_kwmeng, true, domain.bs.kwmeng)) return false; // 대수
	if(gfn_isNull(me_netwr, true, domain.bs.netwr)) return false; // 금액
	if(gfn_isNull(cal_soym, true, domain.bs.soym)) return false; // 예상 수주월 

	// 견적정보 필수입력 체크
	var dsArr = ["ZMAKE","ZTYPE","KWMENG","VRKME","WAERK","DEDAT"];
	var lbArr = [domain.bs.zmake,domain.bs.ztype,domain.bs.kwmeng,domain.bs.vrkme,domain.bs.waerk,domain.bs.dedat];
	if ( gfn_isGridNull(grd_list,dsArr,lbArr,"") ) 
	{
		return;
	}

	//진행상태 : 01의뢰,02접수,03검토중,04견적중,05견적완료,A 승인요청,B승인완료
	if(user_grp < "20" && length(ds_head.GetColumn(0,"ZPSTAT")) > 0 )
	{
		//이미 [${}]신청되어 결재중입니다.
		gfn_showMsg("CW00212",domain.bs.inel);
		return;
	}

    // 예상 수주월 확인 
	if(ds_head.GetColumn(0,"SOYM") <= substr(gfn_getCurrDate(),1,6) )
	{
		var arr = [domain.bs.soym, domain.com.currdt];
		gfn_showMsg("CW00024",arr);
		return;
	}

	//입력확인
	if(ds_list.rowcount == 0)
	{
		gfn_showAlert("CW00113",domain.com.details);
		return;
	}


	for(var i = 0; i< ds_list.rowcount; i++)
	{
		if (ds_list.GetColumn(i,"NETPR") == "" ) {
			ds_list.SetColumn(i,"NETPR",0);
			ds_list.SetColumn(i,"NETWR",0);
		}
	}


	// 저장확인
	if (!gfn_showConfirm("CI00008")) {
		return;
	}


	tit_clearActionInfo();

	if(gubun == "I") 
	{
		// 트랜젝션 시작 - ZSDT0060,61 막음
		tit_addSearchActionInfo("bs02:BS0201001B_T1");
		// tit_addSearchActionInfo("bs02:BS0201001B_T2",true);

		tit_addSearchActionInfo("bs02:BS0201001B_S3",true);
		tit_addSingleActionInfo("bs02:BS0201001B_I1");
		tit_addMultiActionInfo("bs02:BS0201001B_I2");
	} 
	else if(gubun == "U") 
	{	
		if (ds_head.GetColumn(0,"FLAG") == "U")
		{
			tit_addSingleActionInfo("bs02:BS0201001B_U1");
		}
		tit_addSaveActionInfo(
			"bs02:BS0201001B_I3"
			, "bs02:BS0201001B_U2"
			, "bs02:BS0201001B_D1"
			, "FLAG"
			,""
			, 0
			, "");
	}	

	tit_callService(
		""
		, ""
		, "ds_key=ds_key ds_head=ds_head:A ds_list=ds_list:A"
		, "ds_key=ds_key"
		, ""
		, "fn_afterSave");
}
/********************************************************************************
* 기      능   : 저장 Callback
********************************************************************************/
function fn_afterSave(errCode, errMsg) 
{
	if ( errCode!= 0 ) 
	{
		gfn_showMsg("CE00001");
	} 
	else 
	{
		gfn_showMsg("CI00003");
		if(isExistVar("fa_kunnr", true))
		{
			fn_query();
		}
		else
		{
			ds_cond.ClearData();
			ds_cond.AddRow();
			ds_cond.SetColumn(0,"KUNNR",G_VEND_CODE);
			ds_cond.SetColumn(0,"INELN",ds_key.GetColumn(0,"INELN"));
			ds_cond.SetColumn(0,"INSEQ","001");
			fn_query();
		}
	}
}

/********************************************************************************
* 기      능   : 취소 
********************************************************************************/
function fn_cancel() 
{
	ds_head.ClearData();
	ds_list.ClearData();
	fn_OnLoadCompleted();
}


/********************************************************************************
* 기      능   : 닫기  
********************************************************************************/
function fn_close() 
{
	close();
}
