/********************************************************************************
* 기      능   : 그리드에 row추가
********************************************************************************/
function btn_plus_OnClick(obj)
{
	if(ds_head.rowcount() < 1) return;

	var ser = "10";
	var cnt = ds_list.GetRowCount();

	if (cnt > 0) 
	{
		ser = parseInt(ds_list.GetColumn(ds_list.GetRowCount()-1,"POSNR")) + 10;
	}

	ds_list.AddRow();
	ds_list.setColumn(ds_list.row, "FLAG", "I");

	if(gubun == "U") 
	{
		ds_list.SetColumn(ds_list.row,"INELN",ds_head.GetColumn(0,"INELN"));
		ds_list.SetColumn(ds_list.row,"INSEQ",ds_head.GetColumn(0,"INSEQ"));
	}

	ds_list.SetColumn(ds_list.row,"POSNR",ser);
	ds_list.SetColumn(ds_list.row,"WAERK","USD");

	ds_list.RowPos = cnt;
}
/********************************************************************************
* 기      능   : 그리드에 row삭제
********************************************************************************/
function btn_minus_OnClick(obj)
{
	if ( ds_list.rowCount() == 0 ) {
		// 삭제할 자료 선택 
		// CW00013=처리할 자료를 먼저 선택해 주십시오.
		gfn_showMsg("CW00013");
		return;
	}

	if (ds_list.GetOrgColumn(ds_list.rowpos,"MANDT") == G_MANDT)
	{
    	ds_list.setColumn(ds_list.rowpos, "FLAG", "D");
	}
	else
	{
		var serial = ds_list.GetColumn(ds_list.rowpos-1, "POSNR");
		var row = ds_list.rowpos;
		ds_list.DeleteRow(ds_list.rowpos);

		for (var i = ds_list.currow; i < ds_list.rowcount; i++){
			if (row < ds_list.rowcount && i != 0)
			{
				serial = parseInt(serial) + 10;
				ds_list.SetColumn(i, "POSNR", serial);
			}
		}
	}
}
/********************************************************************************
* 기      능   : 그리드2에 DATASET변경시
********************************************************************************/
function ds_list_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	switch( strColumnID ) 
	{
		case "KWMENG":	// 대수
		case "NETPR":	// 단가
			ds_list.SetColumn(nRow,"NETWR",parseInt(ds_list.GetColumn(nRow,"KWMENG"))*parseInt(ds_list.GetColumn(nRow,"NETPR")));
			var meng = null;
			var net = null;
			for(var i =0; i < ds_list.RowCount(); i++)
			{
				meng += ds_list.GetColumn(i,"KWMENG");
				net  += ds_list.GetColumn(i,"NETWR");
			}
			me_kwmeng.Value = meng;
			me_netwr.Value = net;

			if (ds_list.GetColumn(ds_list.row, "FLAG") != "I") {
				ds_list.SetColumn(nRow,"FLAG","U");
			}
			break;
	}	
}
// 달력 처리 부분 
/********************************************************************************
* 기      능   : 그리드에서 날짜 입력시 
********************************************************************************/
function grd_list_OnExpandEdit(obj,nRow,nCell,strVal,nPivotIndex)
{
	var rect = obj.GetCellRect(nRow, ncell);
	cal_temp.top = rect[1] + 220;
	{
		cal_temp.top = 400;
	}
	cal_temp.Left = rect[0] - 50; 
	if (cal_temp.Left > 790)
	{
		cal_temp.Left = 790;
	}
	cal_temp.Visible = true;
	cal_temp.value = ds_list.getColumn(nRow, "SOYM");
	cal_temp.SetFocus();
}
/********************************************************************************
* 기      능   : 달력에서 일자 선택했을 경우 
********************************************************************************/
function cal_temp_OnDayClick(obj,strText)
{
	ds_list.SetColumn(ds_list.row, "DEDAT", strText);
	obj.Visible = false;
}

/********************************************************************************
* 기      능   : 달력에서 포커스 잃었을 경우 
********************************************************************************/
function cal_temp_OnKillFocus(obj)
{
	obj.Visible = false;
}

function ds_head_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if (gubun == "U")
	{
		ds_head.SetColumn(0,"FLAG","U");
	}
}
