#include "LIB::commons.js";	// 공통 스크립트 

//=========================================================================================
// [ PART 1 ]
// Form에서 사용될 전역변수를 선언
// FORM에서 사용되는 전역변수는 대문자로 작성하며, F_XXX 의 형태를 갖도록 한다. 
//=========================================================================================
var user_grp = null;
var gbn = "";
//=========================================================================================
// [ PART 2 ]
// Form Loading / Unloading 시 작업 정의
//=========================================================================================
function fn_OnLoadCompleted(obj) 
{
	global.language = G_LANG;
	gfn_initForm();

	Tab00.Visible = true;
	Tab01.Visible = false;
	Tab02.Visible = false;

	// 권한 정보 가져요기
	user_grp = gfn_getUserGroup();

	if(user_grp > "10") 
	{   // 해외영업부 권한
		if(G_VEND_CODE ="0000009906" || G_VEND_CODE ="0000009922") { 
			gbn = "B5";
		} 
		// 기술영업부 권한
		else if(G_VEND_CODE ="0000009931")
		{
			gbn = "B6";
		}
	}	

	//코드콤보 설정 : 품목    
	if(isExistVar("fa_kunnr", true))
	{
		ds_cond.SetColumn(0,"KUNNR",fa_kunnr);
		ds_cond.SetColumn(0,"INELN",fa_ineln);
		ds_cond.SetColumn(0,"INSEQ",fa_inseq);
		ds_cond.SetColumn(0,"POSNR",fa_posnr);

		tit_clearActionInfo();
		tit_addSearchActionInfo("bs02:BS0201002A_S3");

		tit_callService(
			""
			, ""
			, "ds_cond=ds_cond"
			, "ds_posnr=ds_posnr"
			, ""
			, ""
			, true);

		fn_query();
	}
	else
	{
		fn_close();
	}

}
/********************************************************************************
* 기      능   : 공통코드가 조회된 후의 Callback 함수 
********************************************************************************/
function fn_completeInit(sCode, sDsObj, sCodeType) 
{
	if(!isExistVar("fa_kunnr", true))
	{
		ds_head.AddRow();
		cal_soym.Text = SubStr(gfn_getCurrDate(),0,6);
		cb_inco1.index = 0;
		cb_zterm.index = 0;
		cb_sopos.index = 0;
	}
}
/********************************************************************************
* 기      능   : form UnloadCompleted 
********************************************************************************/
function fn_OnUnloadCompleted(obj) 
{
	return gfn_isFormUnload(obj);
}
//=========================================================================================
// [ PART 3 ]
