/********************************************************************************
* 기      능   : 조회 
********************************************************************************/
function fn_query() 
{
	ds_list.ClearData();

	tit_clearActionInfo();
	tit_addSearchActionInfo("bs02:BS0201002A_S1");

	tit_callService(
        ""
        , ""
        , "ds_cond=ds_cond"
        , "ds_head=ds_head"
        , ""
        , "fn_afterQuery"
        , true
        , true);
}


/********************************************************************************
* 기      능   : 저장
********************************************************************************/
function fn_save() 
{

    //변경값 확인
	if ( !gfn_isChangeDs(ds_list, true) ) {
		return;
	}
	// 사양정보 필수입력 체크
	var dsArr = null;
	if (gfn_replaceSpecialChar(ed_ztype.Text) == "EL"){
		dsArr = ["EL_ATYP","EL_AUSE","EL_AMAN","EL_AOPEN","EL_ASPD","EL_AFQ","EL_ASTQ"];
	} else if (gfn_replaceSpecialChar(ed_ztype.Text) == "ES"){
		dsArr = ["ES_ALOCA","ES_ATYP","ES_ALOC","ES_AWID","ES_ADEG","ES_AFLAT","ES_ASPE","ES_ACODE"];
	}else {
		dsArr = ["MW_ALOCA","MW_ATYP","MW_ALOC","MW_AWID","MW_ADEG","MW_ASPE","MW_ACODE"];
	}

	var arktx = "";
	for(var i = 0; i < dsArr.length(); i++){

		var nRow = ds_list.searchRow("CODE2== " + quote(dsArr[i]));

		if (nRow >= 0 && length(ds_list.GetColumn(nRow,"VALUE1")) <= 0){
			gfn_showAlert("CW00221",ds_list.GetColumn(nRow,"CHARAC"));
			return;
		}
		if(i > 0){
			if (dsArr[i] != "EL_ASTQ"){
				arktx += "-";
			}	
			else{
				arktx += "/";
			}
		}
		arktx += ds_list.GetColumn(nRow,"VALUE1");
	}
	if (IndexOf(arktx,"-") = 0)
	{
		arktx = "";
	}

	//진행상태 : 01의뢰,02접수,03검토중,04견적중,05견적완료,A 승인요청,B승인완료
	if(user_grp == "10" && length(ds_head.GetColumn(0,"ZPSTAT")) > 0 )
	{
		//이미 [${}]신청되어 결재중입니다.
		gfn_showMsg("CW00212",domain.bs.inel);
		return;
	}

	// 저장확인
	if (!gfn_showConfirm("CI00008")) {
		return;
	}

	ds_head.SetColumn(0,"ARKTX",arktx);

	ds_list.fireevent = false;
	if(gbn == "B6"){
		for (var i=0; i < ds_list.RowCount(); i++){
			ds_list.SetColumn(i,"VALUE",ds_list.GetColumn(i,"VALUE2"));
			ds_list.SetColumn(i,"ZGUBUN","R");
		}
	} else {
		for (var i=0; i < ds_list.RowCount(); i++){
			ds_list.SetColumn(i,"VALUE",ds_list.GetColumn(i,"VALUE1"));
			ds_list.SetColumn(i,"ZGUBUN","I");
		}
	}

	ds_list.UnFilter();

	for (var i=0; i < ds_list.RowCount(); i++)
	{
			if ( ds_list.GetColumn(i,"FLAG") != "D" && length(Trim(ds_list.GetColumn(i,"VALUE"))) <= 0){
			ds_list.DeleteRow(i);
			i--;
		}
	}
	ds_list.fireevent = true;

	tit_clearActionInfo();

	// 트랜젝션 시작 
	if(gbn != "B6"){
		tit_addSingleActionInfo("bs02:BS0201002A_U1");
	}

	tit_addSaveActionInfo(
		  "bs02:BS0201002A_I1"
		, "bs02:BS0201002A_U2"
		, "bs02:BS0201002A_D1"
		, "FLAG"
		,""
		, 0
		, "");

	tit_callService(
		""
		, ""
		, "ds_head=ds_head:A ds_list=ds_list:A"
		, ""
		, ""
		, "fn_afterSave");
}
/********************************************************************************
* 기      능   : 저장 Callback
********************************************************************************/
function fn_afterSave(errCode, errMsg) 
{
	if ( errCode!= 0 ) 
	{
		gfn_showMsg("CE00001");
	} 
	else 
	{
		gfn_showMsg("CI00003");
		fn_query();
	}
}

/********************************************************************************
* 기      능   : 취소 
********************************************************************************/
function fn_cancel() 
{
	ds_head.ClearData();
	ds_list.ClearData();
	fn_OnLoadCompleted();
}


/********************************************************************************
* 기      능   : 닫기  
********************************************************************************/
function fn_close() 
{
	close();
}
//=========================================================================================
// [ PART 4]
// Callback 함수 정의 : callback 함수명은 메인 함수의 fn_xxxx() 에서 xxxx 부분은 fn_afterXXXX()
// 형태가 되도록 작성 
//=========================================================================================
/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterQuery(errCode, errMsg) 
{
	gfn_showSearchMsg(grd_list);

	var ztyp = gfn_replaceSpecialChar(ds_head.GetColumn(0,"ZTYPE"));

	switch (ztyp)
	{
		case 'EL':  Tab00.Visible = true;
					Tab00.Enable = true;
					Tab01.Enable = false;
					Tab01.Visible = false;
					Tab02.Enable = false;
					Tab02.Visible = false;
					Tab00.TabIndex =0;

					if (user_grp == "10")
					{
						Tab00.DESC.Div0.ta_zdesc1.enable = false;
						Tab00.DESC.Div0.ta_zdesc2.Readonly = true;
						Tab00.DESC.Div0.ta_zdesc3.Readonly = true;
					}
					else if(user_grp > "20") 
					{
					    // 해외영업부 권한
						if(gbn == "B5") { 
							Tab00.DESC.Div0.ta_zdesc1.Readonly = true;
							Tab00.DESC.Div0.ta_zdesc2.Readonly = true;
							Tab00.DESC.Div0.ta_zdesc3.Readonly = false;
						} 
						// 기술영업부 권한
						else if(gbn == "B6")
						{
							Tab00.DESC.Div0.ta_zdesc1.Readonly = true;
							Tab00.DESC.Div0.ta_zdesc2.Readonly = false;
							Tab00.DESC.Div0.ta_zdesc3.Readonly = true;
						}
					}
					Tab00.grd_list.redraw = true;

				    break;
		case 'ES':  Tab00.Visible = false;
					Tab00.Enable = false;
					Tab01.Visible = true;
					Tab01.Enable = true;
					Tab02.Enable = false;
					Tab02.Visible = false;
					Tab01.TabIndex =0;


					if (user_grp == "10")
					{
						Tab01.DESC.Div0.ta_zdesc1.enable = false;
						Tab01.DESC.Div0.ta_zdesc2.Readonly = true;
						Tab01.DESC.Div0.ta_zdesc3.Readonly = true;
					}
					else if(user_grp > "20") 
					{   // 해외영업부 권한
						if(gbn == "B5") { 
							Tab01.DESC.Div0.ta_zdesc1.Readonly = true;
							Tab01.DESC.Div0.ta_zdesc2.Readonly = true;
							Tab01.DESC.Div0.ta_zdesc3.Readonly = false;
						} 
						// 기술영업부 권한
						else if(gbn == "B6")
						{
							Tab01.DESC.Div0.ta_zdesc1.Readonly = true;
							Tab01.DESC.Div0.ta_zdesc2.Readonly = false;
							Tab01.DESC.Div0.ta_zdesc3.Readonly = true;
						}
					}				
					Tab01.grd_list.redraw = true;

				    break;
		case 'MW':  Tab00.Visible = false;
					Tab00.Enable = false;
					Tab01.Enable = false;
					Tab01.Visible = false;
					Tab02.Visible = true;
					Tab02.Enable = true;
					Tab02.TabIndex =0;


					if (user_grp == "10")
					{
						Tab02.DESC.Div0.ta_zdesc1.enable = false;
						Tab02.DESC.Div0.ta_zdesc2.Readonly = true;
						Tab02.DESC.Div0.ta_zdesc3.Readonly = true;
					}
					else if(user_grp > "20") 
					{   // 해외영업부 권한
						if(gbn == "B5") { 
							Tab02.DESC.Div0.ta_zdesc1.Readonly = true;
							Tab02.DESC.Div0.ta_zdesc2.Readonly = true;
							Tab02.DESC.Div0.ta_zdesc3.Readonly = false;
						} 
						// 기술영업부 권한
						else if(gbn == "B6")
						{
							Tab02.DESC.Div0.ta_zdesc1.Readonly = true;
							Tab02.DESC.Div0.ta_zdesc2.Readonly = false;
							Tab02.DESC.Div0.ta_zdesc3.Readonly = true;
						}
					}
				    break;
					Tab02.grd_list.redraw = true;
	}

	if (ds_head.RowCount() > 0)
	{
		ds_cond.setColumn(0,"GUBN",ztyp);

		if (user_grp > "20" && gbn == "B6")
		{
			ds_cond.setColumn(0,"ZGUBUN","R");
		}
		else
		{
			ds_cond.setColumn(0,"ZGUBUN","I");
		}

		tit_clearActionInfo();
		tit_addSearchActionInfo("bs02:BS0201002A_S2");
		tit_addSearchActionInfo("bs02:BS0201002A_S4");

		tit_callService(
			""
			, ""
			, "ds_cond=ds_cond"
			, "ds_list=ds_list ds_sayang=ds_sayang"
			, ""
			, "fn_afterDetail"
			, true);
	}

}

/********************************************************************************
* 기      능   : 디테일 조회Callback  
********************************************************************************/
function fn_afterDetail(errCode, errMsg) 
{
	gfn_showSearchMsg(grd_list);

	// if(ds_sayang.rowcount > 0)
	// {
	// }
	ufn_setTabDs();
}

function ufn_setTabDs()
{
	if (ds_list.RowCount() > 0)
	{
		ds_list.fireevent = false;
		ds_list.Filter(" DTEXT1=" + quote('A'));
		ds_list.fireevent = true;
	}
}
/********************************************************************************
* 기      능   : 그리드에 DATASET변경시
********************************************************************************/
function ds_list_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	ds_list.fireevent = false;
	if (gbn == "B6") {
		if (length(obj.GetOrgColumn(nRow,"VALUE2")) > 0 && length(obj.GetColumn(nRow,"VALUE2")) == 0){
			obj.SetColumn(nRow,"FLAG","D");
		}
		else if (length(obj.GetOrgColumn(nRow,"VALUE2")) == 0 && length(obj.GetColumn(nRow,"VALUE2")) == 0){
			obj.SetColumn(nRow,"FLAG","");
		}
		else if (length(obj.GetOrgColumn(nRow,"VALUE2")) > 0){
			obj.SetColumn(nRow,"FLAG","U");
		} 
		else {
			obj.SetColumn(nRow,"FLAG","I");
		}
	}
	else {
		if (length(obj.GetOrgColumn(nRow,"VALUE1")) > 0 && length(obj.GetColumn(nRow,"VALUE1")) == 0){
			obj.SetColumn(nRow,"FLAG","D");
		}
		else if (length(obj.GetOrgColumn(nRow,"VALUE1")) == 0 && length(obj.GetColumn(nRow,"VALUE1")) == 0){
			obj.SetColumn(nRow,"FLAG","");
		}
		else if (length(obj.GetOrgColumn(nRow,"VALUE1")) > 0){
			obj.SetColumn(nRow,"FLAG","U");
		}
		 else {
			obj.SetColumn(nRow,"FLAG","I");
		}
	}

	ds_list.fireevent = true;
}

/********************************************************************************
* 기      능   : 탭변경시 관련 사양으로 필터처리
********************************************************************************/

function Tab_OnChanging(obj,nOldIndex,nNewIndex)
{
	ds_list.fireevent = false;
	ds_list.Filter(" DTEXT1=" +quote(obj.GetItem(obj.TabIndex).id));
	ds_list.fireevent = true;	
}

/********************************************************************************
* 기      능   : 품목 변경시 조회처리
********************************************************************************/

function cb_posnr_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	if (gfn_isChangeDs(ds_head,false,"FLAG") || gfn_isChangeDs(ds_list,false,"FLAG"))
	{
		if (gfn_showConfirm("CI00014",domain.com.chginfo)) {
			fn_save();
		}
	}
	ds_cond.setColumn(0,"POSNR", cb_posnr.Value);
	ds_cond.setColumn(0,"GUBN", gfn_replaceSpecialChar(ed_ztype.Text));
	fn_query();
}

function ds_head_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	ds_head.setColumn(0,"FLAG","U");
}

