var user_grp = null;
var gbn = null;
//=========================================================================================
// [ PART 2 ]
// Form Loading / Unloading 시 작업 정의
//=========================================================================================
function fn_OnLoadCompleted(obj) {
	global.language = G_LANG;
	// Form 로드 이벤트 맨 첫줄에 기술 
	gfn_initForm(true);

	//코드콤보 설정 : 지급조건,인도조건,진행상태
	var codeArr = ["ZTERM1","INCO1","ZPSTAT","SOPOS","ZMAKE","ZTYPE","VRKME","B6"];
	var dsObjArr = [ds_zterm,ds_inco1,ds_zpstat,ds_sopos,ds_zmake,ds_ztype,ds_vrkme,ds_zempl];
	var addTypeArr = [G_CODE_ALL,G_CODE_ALL,G_CODE_ALL,G_CODE_NONE,G_CODE_NONE,G_CODE_NONE,G_CODE_NONE,G_CODE_NONE];
	gfn_searchCommCode(codeArr, dsObjArr, addTypeArr);

		// 버튼 비활성화
	ufn_enableBtn("excel",  false);

    user_grp = gfn_getUserGroup();

	if(user_grp > "20") 
	{   // 해외영업부 권한
		if(G_VEND_CODE ="0000009906" || G_VEND_CODE ="0000009922") { 
			gbn = 'B5';
		} 
		// 기술영업부 권한
		else if(G_VEND_CODE ="0000009931")
		{
			gbn = 'B6';
		}
	}

	tit_clearActionInfo();

	tit_addSearchActionInfo("bs02:BS0201001A_S3");
	tit_addSearchActionInfo("bs02:BS0201001A_S4");

	tit_callService(
        ""
        , ""
        , ""
        , "ds_big=ds_big ds_small=ds_small"
        , "GBN="+quote(gbn)
        , "fn_afterCombo"
        , true
        , true);

    cal_redat_to.value = gfn_getBeforeDate(gfn_getCurrDate(),-90);
    cal_redat_fm.value = gfn_getBeforeDate(cal_redat_to.value, 120);

    cal_soym_to.value = gfn_getBeforeDate(gfn_getCurrDate(), -90);
    cal_soym_fm.value = gfn_getBeforeDate(cal_soym_to.value, 120);

}
/********************************************************************************
* 기      능   : 공통코드가 조회된 후의 Callback 함수 
********************************************************************************/
function fn_completeInit(sCode, sDsObj, sCodeType) {
	cb_zterm.Index = 0;
	cb_inco1.Index = 0;
	cb_zpstat.Index = 0;
	gfn_showMsg("CI00007");
	btn_copy.enable = false;

	if(user_grp > "20") 
	{
	   // 기술영업부 권한
		if(gbn = "B6")
		{
			ds_zpstat.Filter("CODE=="+quote("") +" || (CODE =="+quote("03")+" || CODE =="+quote("04")+" || CODE =="+quote("05")+")");
		}
	}	
}
