/********************************************************************************
* 기      능   : 조회 1
********************************************************************************/
function fn_query() {

    // Message 영역 초기화
	global.FRM_BOTTOM.img_msgType.imageId = "";
	global.FRM_BOTTOM.st_msg.value = "";

	// sort mark 삭제 
	gfn_clearGridSortMark(grd_list);
	ds_list.fireevent = false;
	ds_list.row = -1;
	ds_list.ClearData();
	ds_detail.ClearData();
	ds_list.fireevent = true;

	tit_clearActionInfo();
	tit_addSearchActionInfo("bs02:BS0201001A_S1");

	tit_callService(
        ""
        , ""
        , "ds_cond=ds_cond"
        , "ds_list=ds_list"
        , ""
        , "fn_afterQuery"
        , true);
}

