// 상태 변경
function fn_doUpdate(stat,gbn,dom) {

	//  처리할 데이터셋을 복사
	gfn_copyDs(ds_list, ds_list_0, "CHECK=1");


	// 처리 데이터 확인
	if (ds_list_0.rowcount < 1) {
		gfn_showAlert("CW00013");
		return;
	}


	// 의뢰전송 대상 자료 check
	var able = false;

	for (var i = 0; i < ds_list_0.rowcount ; i++)
	{
		able = true;

		if(gbn == "A" && length(ds_list_0.GetColumn(i,"ZPSTAT")) > 0)
		{
			able = false;
		}
		else if (stat > "" && ds_list_0.GetColumn(i, "ZPSTAT") != stat)
		{
			able = false;
		}
		if (!able)
		{
			gfn_showAlert("CW00071", quote(dom)+"("+quote(domain.bs.ineln)+": "
			              + quote(ds_list_0.GetColumn(i,"INELN"))+quote(ds_list_0.GetColumn(i,"INSEQ"))+")");
			return;
		}

		ds_list_0.SetColumn(i,"GBN",gbn);
	}

	if (!gfn_showConfirm("CI00024",dom)) {
		return;
	}


	tit_clearActionInfo();
	tit_addMultiActionInfo("bs02:BS0201001A_U1");


	tit_callService(
		 ""
		, ""
		, "ds_list=ds_list_0:A"
		, ""
		, ""
		, "fn_afterUpdates"
		, "");
}


// 사양검토
function fn_check() {

	//  처리할 데이터셋을 복사
	gfn_copyDs(ds_detail, ds_detail_0, "CHECK=1");


	// 처리 데이터 확인
	if (ds_detail_0.rowcount < 1) {
		gfn_showAlert("CW00013");
		return;
	}
	var arr = gfn_openPopup("BS0201005A",true,,300,150);

	if (arr != null && arr.length > 0 ){

		// 의뢰전송 대상 자료 check
		if(ds_list.GetColumn(ds_list.currow,"ZPSTAT") < "02" || ds_list.GetColumn(ds_list.currow,"ZPSTAT") > "05")
		{
			gfn_showAlert("CW00071", quote(domain.btn.check)+"("+quote(domain.bs.ineln)+": "
						  + quote(ds_list.GetColumn(ds_list.currow,"INELN"))+quote(ds_list.GetColumn(ds_list.currow,"INSEQ"))+")");
			return;
		}

		for (var i = 0; i < ds_detail_0.rowcount ; i++)
		{
			ds_detail_0.SetColumn(i,"ZEMPL",arr[0]);
		}

		if (!gfn_showConfirm("CI00024",domain.btn.check)) {
			return;
		}


		tit_clearActionInfo();
		tit_addMultiActionInfo("bs02:BS0201001A_U2");
		tit_addMultiActionInfo("bs02:BS0201004A_U1");

		tit_callService(
			 ""
			, ""
			, "ds_detail=ds_detail_0:A"
			, ""
			, ""
			, "fn_afterUpdates"
			, "");

	}
}

function fn_approval() {
	//  처리할 데이터셋을 복사
	gfn_copyDs(ds_list, ds_list_0, "CHECK=1");

//user_grp = "20";
	// 처리 데이터 확인
	if (ds_list_0.rowcount() < 1) {
		gfn_showAlert("CW00013");
		return;
	}

	if(user_grp > "20" ) { 
		if (ds_list_0.rowcount() > 1)	{
			gfn_showAlert("CW00222");
			return;
		}
	}

	var manager = false;
	var stat = "";
	// 의뢰전송 대상 자료 check
	for (var i = 0; i < ds_list_0.rowcount ; i++)
	{
		if (user_grp >= "20" && ds_list_0.GetColumn(i, "ZPSTAT") != "A"){
			if (ds_list_0.GetColumn(i, "ZPSTAT") != "02" && ds_list_0.GetColumn(i, "ZPSTAT") != "05")
			{
				gfn_showAlert("CW00071", quote(domain.bs.appask)+"("+quote(domain.bs.ineln)+": "
							  + quote(ds_list_0.GetColumn(i,"INELN"))+quote(ds_list_0.GetColumn(i,"INSEQ"))+")");
				return;
			}
			ds_list_0.SetColumn(i,"GBN","K");
		} 
		else if(user_grp > "20" ) {
			if (ds_list_0.GetColumn(i,"ZPSTAT") != "A")
			{ 
			    gfn_showAlert("CW00071", quote(domain.btn.reco)+"("+quote(domain.bs.ineln)+": "
							  + quote(ds_list_0.GetColumn(i,"INELN"))+quote(ds_list_0.GetColumn(i,"INSEQ"))+")");
				return;
			}
			ds_list_0.SetColumn(i,"GBN","L");
		}
		if (ds_list_0.GetColumn(i,"ZPSTAT") == "A") {
			manager = true;
			if (stat = "")
			{
				stat = ds_list_0.GetColumn(i,"ZPSTAT");
			}
			else if (stat != ds_list_0.GetColumn(i,"ZPSTAT"))
			{
				gfn_showAlert("CW00214");
				return;
			}


		}

	}

	if(user_grp == "20" ) {
		if (!gfn_showConfirm("CI00024",domain.bs.appask)) {
			return;
		}
	}
	else if(user_grp > "20" ) {
		if (!gfn_showConfirm("CI00024",domain.btn.reco)) {
			return;
		}
	} 



	tit_clearActionInfo();
	tit_addMultiActionInfo("bs02:BS0201001A_U1");
	// 승인 완료시 견적서 데이터 작성
	if( user_grp > "20" && manager) {
		tit_addSearchActionInfo("bs02:BS0201004A_T1");
		tit_addSearchActionInfo("bs02:BS0201004A_S1",true);
		tit_addSingleActionInfo("bs02:BS0201004A_I1");
		tit_addSingleActionInfo("bs02:BS0201004A_I2");
	}

	tit_callService(
		 ""
		, ""
		, "ds_key=ds_key ds_list=ds_list_0"
		, "ds_key=ds_key"
		, ""
		, "fn_afterUpdates");
}


