/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterQuery(errCode, errMsg) {
	// 메세지 처리
	if (ds_list.rowcount > 0)
	{
		gfn_showMsg("CI00002",ds_list.RowCount()+"");
		ds_list.row = 0;
	    ufn_enableBtn("excel",  true);
	}

}
/********************************************************************************
* 기      능   : 전송 Callback  
********************************************************************************/
function fn_afterUpdates(errCode, errMsg) {
	if ( errCode != 0 ) {
		gfn_showMsg("CE00001");
	} else {
		gfn_showMsg("CI00003");
		fn_query();
	}
}

function fn_afterCombo()
{
	if (user_grp == "10")
	{
		cb_small.value = G_VEND_CODE;
		cb_big.Enable = false;
		cb_small.Enable = false;
	}
	else
	{
		ds_small.insertRow(0);
		ds_small.SetColumn(0,"CODE","");
		ds_small.SetColumn(0,"CODE_NAME","-전체-");
		ds_small.SetColumn(0,"PM","");

		if (user_grp > "20")
		{
			ds_big.insertRow(0);
			ds_big.SetColumn(0,"CODE","");
			ds_big.SetColumn(0,"CODE_NAME","-전체-");
			cb_big.Index = 0;
		}
		else
		{
			cb_big.Value = G_USER_ID;
			cb_big.Enable = false;
		}
		cb_big_OnChanged();
	}
}
/********************************************************************************
* 기      능   :  삭제 Callback  
********************************************************************************/
function fn_afterDelete(errCode, errMsg){
	if ( errCode != 0 ) {
		gfn_showMsg("CE00001");
	} else {
		gfn_showMsg("CI00003");
		fn_query();
	}
}

