/********************************************************************************
* 기      능   : Excel출력 조회 Callback  
********************************************************************************/
function fn_afterExcelQuery(errCode, errMsg) {
	// 메세지 처리
	if (ds_list.rowcount > 0)
	{
		gfn_showMsg("CI00002",ds_exlist.RowCount()+"");

		create("grid","grdExcel",'BindDataSet="' + grd_exlist.BindDataset +'"');
		grdExcel.Contents = grd_exlist.Contents;

		var str = domain.bs.vender + " : " + cb_big.text + " ( " + cb_small.text    + "), "
		         + domain.bs.beredat + " : " + substr(ds_cond.GetColumn(0, "REDAT_FM"),0,4)+"."+substr(ds_cond.GetColumn(0, "REDAT_FM"),4,2)+"."+substr(ds_cond.GetColumn(0, "REDAT_FM"),6,2) 
		         + " ~ " + substr(ds_cond.GetColumn(0, "REDAT_TO"),0,4)+"."+substr(ds_cond.GetColumn(0, "REDAT_TO"),4,2)+"."+substr(ds_cond.GetColumn(0, "REDAT_TO"),6,2)    + ", "
				 + "  " + domain.bs.soym +" : "+ substr(ds_cond.GetColumn(0, "SOYM_FM"),0,4)+"."+substr(ds_cond.GetColumn(0, "SOYM_FM"),4,2)+"."+substr(ds_cond.GetColumn(0, "SOYM_FM"),6,2) 
		         + " ~ " + substr(ds_cond.GetColumn(0, "SOYM_TO"),0,4)+"."+substr(ds_cond.GetColumn(0, "SOYM_TO"),4,2)+"."+substr(ds_cond.GetColumn(0, "SOYM_TO"),6,2)    + ", "
		         + domain.bs.zpstat +" : "+ cb_zpstat.text     + ", "
				 + "  " + domain.bs.zterm +" : "+ cb_zterm.text     + ", "
				 + "  " + domain.bs.inco1 +" : "+ cb_inco1.text     + "";
	    var str1 = substr(gfn_getCurrDate(),0,4)+"."+substr(gfn_getCurrDate(),4,2)+"."+substr(gfn_getCurrDate(),6,2);

		var headCnt = getHeadCount(grdExcel);
		var totCol  = grdExcel.GetColCount();

		var irow;

		for(var i = 0 ; i < headCnt ; i++ )
		{
			irow = toInteger(grdExcel.GetCellProp("head",i,"row"));
			grdExcel.SetCellProp("head",i,"row",irow+3);
		}

		var str_temp = grdExcel.GetCurContents();
		var str_Head = '<cell align="center" col="0"  colspan="' + totCol + '" bkcolor="user5" display="text"  font="굴림,15,bold" border="none" text="' + 'INQUIRY LIST' + '"/>'	
		             + '<cell align="right" col="0"  colspan="' + totCol + '" row=1 bkcolor="user5" display="text" text="' + str1 + '"/>'
		             + '<cell align="left" col="0" row=2 colspan="'+ (totCol-2) +'" bkcolor="user5" display="text" text="' + str + '"/>'
		             + '<cell align="right" col="'+ (totCol-2) +'" colspan=2 row=2  bkcolor="user5" display="text" text="(UNIT : USD阡)"/>';

		str_temp = replace(str_temp, '<head>','<head>'+str_Head);

		grdExcel.contents = str_temp;
		//trace(str_temp);
		grdExcel.ExportExcelEx("INQUIRY LIST","A1",false,true,true);
		Destroy(grdExcel);
	}

}

function getHeadCount(grdObj)
{
	var strConts = grdObj.Contents;

	var iPos = pos(strConts,"<head>");

	var strHead = Mid(strConts,iPos);

	iPos = pos(strHead,"</head>");

	strHead = Mid(strHead,0,iPos+7);

//	trace(strHead);

	var spObj = split(strHead,chr(10),"webstyle");

	return spObj.length()-2;
}

