/********************************************************************************
* 기      능   : 그리드 헤더 클릭시 정렬 처리 
********************************************************************************/
function grd_list_OnHeadClick(obj,nCell,nX,nY,nPivotIndex)
{
	if ( nCell == 0 ) {
		// 그리드 표시할 컬럼 선택 
		gfn_selectAllGrid(obj);
	} else {
		// 그리드 정렬 처리 
		gfn_sortGrid(obj,nCell);
	}
}


/********************************************************************************
* 기      능   : 상세정보 표시
********************************************************************************/
function ufn_DetailQuery() 
{
	// detail 조회
	ds_subcond.SetColumn(0,"KUNNR",ds_list.GetColumn(ds_list.row,"KUNNR"));
	ds_subcond.SetColumn(0,"INELN",ds_list.GetColumn(ds_list.row,"INELN"));
	ds_subcond.SetColumn(0,"INSEQ",ds_list.GetColumn(ds_list.row,"INSEQ"));

	tit_clearActionInfo();
	tit_addSearchActionInfo("bs02:BS0201001A_S2");
	tit_callService(
		""
		, ""
		, "ds_subcond=ds_subcond"
		, "ds_detail=ds_detail ds_subcond=ds_subcond"
		, ""
		, "fn_afterDetailQuery"
		, true);
}


