function cb_big_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	if (gbn == "B5")
	{
		if (cb_big.Value == "")
		{
			ds_small.UnFilter();
		}
		else
		{
			ds_small.Filter(" PM=="+ quote(cb_big.Value) + " || PM==" +quote(""));
		}
	}
	cb_small.index=0;
}


function grd_detail_OnHeadClick(obj,nCell,nX,nY,nPivotIndex)
{
	if ( nCell == 0 ) {
		// 전체 선택 취소
		gfn_selectGridCol(obj);
	} else {
		// 그리드 정렬 처리 
		gfn_sortGrid(obj,nCell);
	}
}
