#include "LIB::commons.js";	// 공통 스크립트 
//=========================================================================================
// [ PART 1 ]
// Form에서 사용될 전역변수를 선언
// FORM에서 사용되는 전역변수는 대문자로 작성하며, F_XXX 의 형태를 갖도록 한다. 
//=========================================================================================
var user_grp = null;
var gbn = null;
//=========================================================================================
// [ PART 2 ]
// Form Loading / Unloading 시 작업 정의
//=========================================================================================
function fn_OnLoadCompleted(obj) {
	global.language = G_LANG;
	// Form 로드 이벤트 맨 첫줄에 기술 
	gfn_initForm(true);

	//코드콤보 설정 : 지급조건,인도조건,진행상태
	var codeArr = ["ZPSTAT","ZMAKE","ZTYPE","VRKME","B6"];
	var dsObjArr = [ds_zpstat,ds_zmake,ds_ztype,ds_vrkme,ds_zempl];
	var addTypeArr = [G_CODE_ALL,G_CODE_NONE,G_CODE_NONE,G_CODE_NONE,G_CODE_ALL];
	gfn_searchCommCode(codeArr, dsObjArr, addTypeArr);

    user_grp = gfn_getUserGroup();

	tit_clearActionInfo();

	tit_addSearchActionInfo("bs02:BS0201001A_S3");
	tit_addSearchActionInfo("bs02:BS0201001A_S4");

	tit_callService(
        ""
        , ""
        , ""
        , "ds_big=ds_big ds_small=ds_small"
        , "GBN="+quote("B5")
        , "fn_afterCombo"
        , true
        , true);

    cal_spdat_to.value = gfn_getBeforeDate(gfn_getCurrDate(), -30);
    cal_spdat_fm.value = gfn_getBeforeDate(cal_spdat_to.value, 90);

}
/********************************************************************************
* 기      능   : 공통코드가 조회된 후의 Callback 함수 
********************************************************************************/
function fn_completeInit(sCode, sDsObj, sCodeType) {
	cb_zempl.Index = 0;
	cb_zpstat.Index = 0;
	ds_zpstat.Filter("CODE=="+quote("") +" || (CODE =="+quote("03")+" || CODE =="+quote("04")+" || CODE =="+quote("05")+")");

	gfn_showMsg("CI00007");
}
/********************************************************************************
* 기      능   : form UnloadCompleted 
********************************************************************************/
function fn_OnUnloadCompleted(obj) {
	return gfn_isFormUnload(obj);
}
//=========================================================================================
// [ PART 3 ]
// Main Process Event 정의(조회,입력,삭제,저장,인쇄,form닫기)
//=========================================================================================
/********************************************************************************
* 기      능   : 조회 1
********************************************************************************/
function fn_query() {

    // Message 영역 초기화
	global.FRM_BOTTOM.img_msgType.imageId = "";
	global.FRM_BOTTOM.st_msg.value = "";

	// sort mark 삭제 
	gfn_clearGridSortMark(grd_list);
	ds_list.row = -1;
	ds_list.ClearData();

	tit_clearActionInfo();
	tit_addSearchActionInfo("bs02:BS0201006A_S1");

	tit_callService(
        ""
        , ""
        , "ds_cond=ds_cond"
        , "ds_list=ds_list"
        , ""
        , "fn_afterQuery"
        , true);
}

/********************************************************************************
* 기      능   : 닫기  
********************************************************************************/
function fn_close() {
	close();
}


// 상태 변경
function fn_receipt() {

	//  처리할 데이터셋을 복사
	gfn_copyDs(ds_list, ds_list_0, "CHECK=1");


	// 처리 데이터 확인
	if (ds_list_0.rowcount < 1) {
		gfn_showAlert("CW00013");
		return;
	}


	// 의뢰전송 대상 자료 check

	for (var i = 0; i < ds_list_0.rowcount ; i++)
	{
		if (ds_list_0.GetColumn(i, "ZPSTAT") != "03")
		{
			gfn_showAlert("CW00071", quote(domain.btn.receipt)+"("+quote(domain.bs.ineln)+": "
			              + quote(ds_list_0.GetColumn(i,"INELN"))+quote(ds_list_0.GetColumn(i,"INSEQ"))+")");
			return;
		}

		ds_list_0.SetColumn(i,"GBN","E");
	}

	if (!gfn_showConfirm("CI00024",domain.btn.receipt)) {
		return;
	}


	tit_clearActionInfo();
	tit_addMultiActionInfo("bs02:BS0201001A_U1");


	tit_callService(
		 ""
		, ""
		, "ds_list=ds_list_0:A"
		, ""
		, ""
		, "fn_afterReceipt"
		, "");
}


// 상태 변경
function fn_trans() {

	//  처리할 데이터셋을 복사
	gfn_copyDs(ds_list, ds_list_0, "CHECK=1");


	// 처리 데이터 확인
	if (ds_list_0.rowcount < 1) {
		gfn_showAlert("CW00013");
		return;
	}


	// 의뢰전송 대상 자료 check

	for (var i = 0; i < ds_list_0.rowcount ; i++)
	{
		if (ds_list_0.GetColumn(i, "ZPSTAT") != "04")
		{
			gfn_showAlert("CW00071", quote(domain.btn.trans)+"("+quote(domain.bs.ineln)+": "
			              + quote(ds_list_0.GetColumn(i,"INELN"))+quote(ds_list_0.GetColumn(i,"INSEQ"))+")");
			return;
		}

		ds_list_0.SetColumn(i,"GBN","F");
	}

	if (!gfn_showConfirm("CI00024",domain.btn.trans)) {
		return;
	}


	tit_clearActionInfo();
	tit_addMultiActionInfo("bs02:BS0201006A_U1");
	tit_addMultiActionInfo("bs02:BS0201006A_U2");


	tit_callService(
		 ""
		, ""
		, "ds_list=ds_list_0:A"
		, ""
		, ""
		, "fn_afterTrans"
		, "");
}


//=========================================================================================
// [ PART 4]
// Callback 함수 정의 : callback 함수명은 메인 함수의 fn_xxxx() 에서 xxxx 부분은 fn_afterXXXX()
// 형태가 되도록 작성 
//=========================================================================================
/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterQuery(errCode, errMsg) {
	// 메세지 처리
	if (ds_list.rowcount > 0)
	{
		gfn_showMsg("CI00002",ds_list.RowCount()+"");
		ds_list.row = 0;
		btn_copy.enable = true;
	}

}
/********************************************************************************
* 기      능   : 전송 Callback  
********************************************************************************/
function fn_afterReceipt(errCode, errMsg) {
	if ( errCode != 0 ) {
		gfn_showMsg("CE00001");
	} else {
		gfn_showMsg("CI00003");
		fn_query();
	}
}

function fn_afterTrans(errCode, errMsg) {
	if ( errCode != 0 ) {
		gfn_showMsg("CE00001");
	} else {
		gfn_showMsg("CI00003");
		fn_query();
	}
}

function fn_afterCombo()
{
	ds_small.insertRow(0);
	ds_small.SetColumn(0,"CODE","");
	ds_small.SetColumn(0,"CODE_NAME","-전체-");
	ds_small.SetColumn(0,"PM","");

	ds_big.insertRow(0);
	ds_big.SetColumn(0,"CODE","");
	ds_big.SetColumn(0,"CODE_NAME","-전체-");
	cb_big.Index = 0;

	cb_big_OnChanged();
}

