#include "LIB::commons.js";	// 공통 스크립트 
//=========================================================================================
// [ PART 1 ]
// Form에서 사용될 전역변수를 선언
// FORM에서 사용되는 전역변수는 대문자로 작성하며, F_XXX 의 형태를 갖도록 한다. 
//=========================================================================================
var user_grp = null;
var gbn = null;
//=========================================================================================
// [ PART 2 ]
// Form Loading / Unloading 시 작업 정의
//=========================================================================================
function fn_OnLoadCompleted(obj) {
	global.language = G_LANG;
	// Form 로드 이벤트 맨 첫줄에 기술 
	gfn_initForm(true);

	//코드콤보 설정 : 지급조건,인도조건,진행상태
	var codeArr = ["ZPSTAT","ZMAKE","ZTYPE"];
	var dsObjArr = [ds_zpstat,ds_zmake,ds_ztype];
	var addTypeArr = [G_CODE_ALL,G_CODE_NONE,G_CODE_NONE];
	gfn_searchCommCode(codeArr, dsObjArr, addTypeArr);

	// 버튼 비활성화
	ufn_enableBtn("excel",  false);
	ufn_enableBtn("trans",  false);
	ufn_enableBtn("approval",  false);
	ufn_enableBtn("sendback",  false);

    user_grp = gfn_getUserGroup();

	if(user_grp > "20") 
	{   // 해외영업부 권한
		if(G_VEND_CODE ="0000009906" || G_VEND_CODE ="0000009922") { 
			gbn = 'B5';
		} 
	}

	tit_clearActionInfo();

	tit_addSearchActionInfo("bs02:BS0201001A_S3");
	tit_addSearchActionInfo("bs02:BS0201001A_S4");

	tit_callService(
        ""
        , ""
        , ""
        , "ds_big=ds_big ds_small=ds_small"
        , "GBN="+quote(gbn)
        , "fn_afterCombo"
        , true
        , true);

    cal_ofdat_to.value = gfn_getBeforeDate(gfn_getCurrDate(), -90);
    cal_ofdat_fm.value = gfn_getBeforeDate(cal_ofdat_to.value, 120);
}
/********************************************************************************
* 기      능   : 공통코드가 조회된 후의 Callback 함수 
********************************************************************************/
function fn_completeInit(sCode, sDsObj, sCodeType) {
	cb_zpstat.Index = 0;

	if (user_grp < "40"){
		btn_approval.Enable = false;
		btn_sendback.Enable = false;
	}

	gfn_showMsg("CI00007");
}
/********************************************************************************
* 기      능   : form UnloadCompleted 
********************************************************************************/
function fn_OnUnloadCompleted(obj) {
	return gfn_isFormUnload(obj);
}
