/********************************************************************************
* 기      능   : 조회 1
********************************************************************************/
function fn_query() {

    // Message 영역 초기화
	global.FRM_BOTTOM.img_msgType.imageId = "";
	global.FRM_BOTTOM.st_msg.value = "";

	// sort mark 삭제 
	gfn_clearGridSortMark(grd_list);
	ds_list.fireevent = false;
	ds_list.row = -1;
	ds_list.ClearData();
	ds_detail.ClearData();
	ds_list.fireevent = true;

	tit_clearActionInfo();
	tit_addSearchActionInfo("bs02:BS0202001A_S1");

	tit_callService(
        ""
        , ""
        , "ds_cond=ds_cond"
        , "ds_list=ds_list"
        , ""
        , "fn_afterQuery"
        , true);
}

/********************************************************************************
* 기      능   : 닫기  
********************************************************************************/
function fn_close() {
	close();
}



/********************************************************************************
* 기      능   : 전송 60
********************************************************************************/
function fn_trans() {
	fn_doUpdate("","A",domain.btn.trans);
}

function fn_approval() {
	fn_doUpdate("A","B",domain.btn.reco);
}

function fn_sendback() {
	fn_doUpdate("A","C",domain.btn.sendback);
}

// 상태 변경
function fn_doUpdate(stat,gbn,dom) {

	//  처리할 데이터셋을 복사
	gfn_copyDs(ds_list, ds_list_0, "CHECK=1");


	// 처리 데이터 확인
	if (ds_list_0.rowcount < 1) {
		gfn_showAlert("CW00013");
		return;
	}


	// 의뢰전송 대상 자료 check
	var able = false;

	for (var i = 0; i < ds_list_0.rowcount ; i++)
	{
		able = true;

		if(gbn == "A" && length(ds_list_0.GetColumn(i,"VALDAT")) < 8)
		{
			gfn_showAlert("CW00221",quote(domain.bs.valdat));
			return;
		}

		if(gbn == "A" && length(ds_list_0.GetColumn(i,"ZPSTAT")) > 0)
		{
			able = false;
		}
		else if (stat > "" && ds_list_0.GetColumn(i, "ZPSTAT") != stat)
		{
			able = false;
		}
		if (!able)
		{
			gfn_showAlert("CW00071", quote(dom)+"("+quote(domain.bs.ofeln)+": "
			              + quote(ds_list_0.GetColumn(i,"OFELN"))+quote(ds_list_0.GetColumn(i,"OFSEQ"))+")");
			return;
		}

		ds_list_0.SetColumn(i,"GBN",gbn);
	}

	if (!gfn_showConfirm("CI00024",dom)) {
		return;
	}


	tit_clearActionInfo();
	tit_addMultiActionInfo("bs02:BS0202001A_U1");


	tit_callService(
		 ""
		, ""
		, "ds_list=ds_list_0:A"
		, ""
		, ""
		, "fn_afterUpdates"
		, "");
}

/********************************************************************************
* 기      능   : 엑셀 출력
********************************************************************************/
function fn_excel() {

    // Message 영역 초기화
	global.FRM_BOTTOM.img_msgType.imageId = "";
	global.FRM_BOTTOM.st_msg.value = "";

	// sort mark 삭제 
	gfn_clearGridSortMark(grd_exlist);

	ds_exlist.fireevent = false;
	ds_exlist.row = -1;
	ds_exlist.ClearData();
	ds_exlist.fireevent = true;

	tit_clearActionInfo();
	tit_addSearchActionInfo("bs02:BS0202001A_S3");

	tit_callService(
        ""
        , ""
        , "ds_cond=ds_cond"
        , "ds_exlist=ds_exlist"
        , ""
        , "fn_afterExcelQuery"
        , true);
}


//=========================================================================================
// [ PART 4]
// Callback 함수 정의 : callback 함수명은 메인 함수의 fn_xxxx() 에서 xxxx 부분은 fn_afterXXXX()
// 형태가 되도록 작성 
//=========================================================================================
/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterQuery(errCode, errMsg) {
	// 메세지 처리
	if (ds_list.rowcount > 0)
	{
		gfn_showMsg("CI00002",ds_list.RowCount()+"");
		ds_list.row = 0;
	    ufn_enableBtn("excel",  true);
	    ufn_enableBtn("trans",  true);
	    if(user_grp > "20") 
	    {
			ufn_enableBtn("approval",  true);
			ufn_enableBtn("sendback",  true);
	    }
	}

}
/********************************************************************************
* 기      능   : 전송 Callback  
********************************************************************************/
function fn_afterUpdates(errCode, errMsg) {
	if ( errCode != 0 ) {
		gfn_showMsg("CE00001");
	} else {
		gfn_showMsg("CI00003");
		fn_query();
	}
}

function fn_afterCombo()
{
	if (user_grp == "10")
	{
		cb_small.value = G_VEND_CODE;
		cb_big.Enable = false;
		cb_small.Enable = false;
	}
	else
	{
		ds_small.insertRow(0);
		ds_small.SetColumn(0,"CODE","");
		ds_small.SetColumn(0,"CODE_NAME","-전체-");
		ds_small.SetColumn(0,"PM","");

		if (user_grp > "20")
		{
			ds_big.insertRow(0);
			ds_big.SetColumn(0,"CODE","");
			ds_big.SetColumn(0,"CODE_NAME","-전체-");
			cb_big.Index = 0;
		}
		else
		{
			cb_big.Value = G_USER_ID;
			cb_big.Enable = false;
		}
		cb_big_OnChanged();
	}
}


/********************************************************************************
* 기      능   : 상세조회 Callback  
********************************************************************************/
function fn_afterDetailQuery(errCode, errMsg) 
{
 	if ( ds_list.rowCount() > 0 ) 
	{
		gfn_showMsg("CI00002", ds_detail.rowCount() + "");
	} 
	else 
	{
		gfn_showMsg("CW00010");
	}
}

/********************************************************************************
* 기      능   : Excel출력 조회 Callback  
********************************************************************************/
function fn_afterExcelQuery(errCode, errMsg) {
	// 메세지 처리
	if (ds_list.rowcount > 0)
	{
		gfn_showMsg("CI00002",ds_exlist.RowCount()+"");

		create("grid","grdExcel",'BindDataSet="' + grd_exlist.BindDataset +'"');
		grdExcel.Contents = grd_exlist.Contents;

		var str = domain.bs.vender + " : " + cb_big.text + " ( " + cb_small.text    + "), "
				 + "  " + domain.bs.ineln +" : "+ ds_cond.GetColumn(0, "INELN")   + ", "
				 + "  " + domain.bs.post1 +" : "+ ds_cond.GetColumn(0, "POST1")   + "";
		var str1 = domain.bs.ofdat + " : " + substr(ds_cond.GetColumn(0, "OFDAT_FM"),0,4)+"."+substr(ds_cond.GetColumn(0, "OFDAT_FM"),4,2)+"."+substr(ds_cond.GetColumn(0, "OFDAT_FM"),6,2) 
		         + " ~ " + substr(ds_cond.GetColumn(0, "OFDAT_TO"),0,4)+"."+substr(ds_cond.GetColumn(0, "OFDAT_TO"),4,2)+"."+substr(ds_cond.GetColumn(0, "OFDAT_TO"),6,2)    + ", "
				 + "  " + domain.bs.ofeln +" : "+ ds_cond.GetColumn(0, "OFELN")    + ", "
		         + domain.bs.zpstat +" : "+ cb_zpstat.text     + "";

	    var date = substr(gfn_getCurrDate(),0,4)+"."+substr(gfn_getCurrDate(),4,2)+"."+substr(gfn_getCurrDate(),6,2);

		var headCnt = getHeadCount(grdExcel);
		var totCol  = grdExcel.GetColCount();

		var irow;

		for(var i = 0 ; i < headCnt ; i++ )
		{
			irow = toInteger(grdExcel.GetCellProp("head",i,"row"));
			grdExcel.SetCellProp("head",i,"row",irow+3);
		}

		var str_temp = grdExcel.GetCurContents();
		var str_Head = '<cell align="center" col="0"  colspan="' + totCol + '" bkcolor="user5" display="text"  font="굴림,15,bold" border="none" text="' + 'OFFER LIST' + '"/>'	
		             + '<cell align="left"  col="0" colspan="' + (totCol-2) + '" row=1 bkcolor="user5" display="text" text="' + str + '"/>'
		             + '<cell align="right" col="' + (totCol-2) + '"  colspan="2" row=1 bkcolor="user5" display="text" text="' + date + '"/>'
		             + '<cell align="left"  col="0" colspan="'+ (totCol-2) +'" row=2  bkcolor="user5" display="text" text="' + str1 + '"/>'
		             + '<cell align="right" col="'+ (totCol-2) +'" colspan=2 row=2  bkcolor="user5" display="text" text="(UNIT : USD阡)"/>';

		str_temp = replace(str_temp, '<head>','<head>'+str_Head);

		grdExcel.contents = str_temp;
		//trace(str_temp);
		grdExcel.ExportExcelEx("OFFER LIST","A1",false,true,true);
		Destroy(grdExcel);
	}

}



function getHeadCount(grdObj)
{
	var strConts = grdObj.Contents;

	var iPos = pos(strConts,"<head>");

	var strHead = Mid(strConts,iPos);

	iPos = pos(strHead,"</head>");

	strHead = Mid(strHead,0,iPos+7);

//	trace(strHead);

	var spObj = split(strHead,chr(10),"webstyle");

	return spObj.length()-2;
}

