/********************************************************************************
* 기      능   : 그리드 헤더 클릭시 정렬 처리 
********************************************************************************/
function grd_list_OnHeadClick(obj,nCell,nX,nY,nPivotIndex)
{
	if ( nCell == 0 ) {
		// 그리드 표시할 컬럼 선택 
		gfn_selectAllGrid(obj);
	} else {
		// 그리드 정렬 처리 
		gfn_sortGrid(obj,nCell);
	}
}


/********************************************************************************
* 기      능   : 상세정보 표시
********************************************************************************/
function ufn_DetailQuery() 
{
	// detail 조회
	ds_subcond.SetColumn(0,"KUNNR",ds_list.GetColumn(ds_list.row,"KUNNR"));
	ds_subcond.SetColumn(0,"OFELN",ds_list.GetColumn(ds_list.row,"OFELN"));
	ds_subcond.SetColumn(0,"OFSEQ",ds_list.GetColumn(ds_list.row,"OFSEQ"));

	tit_clearActionInfo();
	tit_addSearchActionInfo("bs02:BS0202001A_S2");
	tit_callService(
		""
		, ""
		, "ds_subcond=ds_subcond"
		, "ds_detail=ds_detail ds_subcond=ds_subcond"
		, ""
		, "fn_afterDetailQuery"
		, true);
}


/*************************************************
* 견적의뢰 클릭시 상세 조회
**************************************************/
function ds_list_OnCellPosChanged(obj,nOldRow,nRow)
{
	// detail 조회
	ufn_DetailQuery();	
}

/*************************************************
* 견적의뢰 더블클릭시 견적 화면 팝업
**************************************************/

function grd_list_OnCellDBLClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	var arg = "fa_kunnr=" + quote(ds_list.GetColumn(nRow,"KUNNR"));
	    arg += " fa_ofeln=" + quote(ds_list.GetColumn(nRow,"OFELN"));
	    arg += " fa_ofseq=" + quote(ds_list.GetColumn(nRow,"OFSEQ"));
	    arg += " fa_ineln=" + quote(ds_list.GetColumn(nRow,"INELN"));
	    arg += " fa_inseq=" + quote(ds_list.GetColumn(nRow,"INSEQ"));

	gfn_openPopup("BS0202001B", true, arg, 950, 568);
}


function cb_big_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	if (gbn == "B5")
	{
		if (cb_big.Value == "")
		{
			ds_small.UnFilter();
		}
		else
		{
			ds_small.Filter(" PM=="+ quote(cb_big.Value) + " || PM==" +quote(""));
		}
	}
	cb_small.index=0;
}


function grd_detail_OnHeadClick(obj,nCell,nX,nY,nPivotIndex)
{
	if ( nCell == 0 ) {
		// 전체 선택 취소
		gfn_selectGridCol(obj);
	} else {
		// 그리드 정렬 처리 
		gfn_sortGrid(obj,nCell);
	}
}

function grd_detail_OnCellDBLClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	var arg = "fa_kunnr=" + quote(ds_detail.GetColumn(nRow,"KUNNR"));
	    arg += " fa_ineln=" + quote(ds_detail.GetColumn(nRow,"INELN"));
	    arg += " fa_inseq=" + quote(ds_detail.GetColumn(nRow,"INSEQ"));
	    arg += " fa_posnr=" + quote(ds_detail.GetColumn(nRow,"POSNR"));

	gfn_openPopup("BS0202001D", true, arg, 610, 568);
}
