/********************************************************************************
* 기      능   : 조회 
********************************************************************************/
function fn_query()
{
	if ( !gfn_checkFromToObj(cal_fdt, cal_tdt, true, st_tdate.value, 0, false) )
	{
		return;
	}
	if ( !gfn_checkFromToObj(cal_cfdt, cal_ctdt, true, st_cdate.value, 0, false) )
	{
		return;
	}

	if(ed_pjt.Text == '' && cal_fdt.Value == '' && cal_cfdt.Value == '')
	{
		gfn_showMsg("CW00207","프로젝트/계획기간/등록일");
		return;	
	}
	if(cb_gubun.Value > '' && cal_fdt.Value == '')
	{
		var arr = ['공정구분','계획기간'];
		gfn_showMsg("CW00004",arr);
		return;
	}
	if(chk_stat1.value == '0' && chk_stat2.value == '0' && chk_stat3.value == '0' && chk_stat4.value == '0')
	{
		gfn_showMsg("CW00207","등록상태");
		return;
	}

	if(chk_stat1.value == '0') ds_cond.SetColumn(0,"STAT1","");
	if(chk_stat2.value == '0') ds_cond.SetColumn(0,"STAT2","");
	if(chk_stat3.value == '0') ds_cond.SetColumn(0,"STAT3","");

	//sort mark 삭제 
	gfn_clearGridSortMark(grd_list);
	ds_list.row = -1;

	tit_clearActionInfo();
	tit_callService(
        "BS0301001S_ACT"
        , "query"
        , "ds_cond=ds_cond"
        , "ds_list=ds_list"
        , ""
        , "fn_afterQuery"
        , false);

}
/********************************************************************************
* 기      능   : 저장
********************************************************************************/
function fn_save() {

	gfn_clearMsgBar();

	if ( ds_list.rowcount == 0 )
	{
		gfn_showMsg("CW00066");
		return;
	}

	for( var i=0; i<ds_list.rowcount; i++ )
	{    
	    if(ds_list.getColumn(i,"GBN2") == '0')
		{
			ds_list.SetColumn(i,"GBN2",'');
		}
		else if(ds_list.getColumn(i,"GBN2") == '1') 
		{
		   if(ds_list.getColumn(i,"GBN1") == 'C')
	       {
			   gfn_showAlert("CW10025", "완료일 등록이 되었을 경우 저장이 불가합니다.");
			   return false;
	       }

		    ds_list.SetColumn(i,"GBN1","S");
			ds_list.SetColumn(i,"GBN2",'X');

			if(length(ds_list.getColumn(i,"SALES_P")) == 0) {
				gfn_showAlert("CW10025", "영업문제점 항목은 필수입력입니다.");
				return false;
			}

			if(length(ds_list.getColumn(i,"DS_GUBUN")) == 0) {
				gfn_showAlert("CW10025", "구분 항목은 필수입력입니다.");
				return false;
			}

			if( G_VEND_CODE == "0000009931" ) { //|| G_VEND_CODE == "0000009922" ) {
			 if(length(ds_list.getColumn(i,"ZGUBUN")) == 0) {
				gfn_showAlert("CW10025", "문제원인 항목은 필수입력입니다.");
		 		return false;
			 }
			}

			if(gfn_getCurrDate() > ds_list.getColumn(i,"CMDAT"))
			{

			  if('00000000' < ds_list.getColumn(i,"CMDAT"))
			  {
			  	gfn_showAlert("CW10025", "완료일은 금일 이전 날짜의 입력이 불가합니다.");
		        return false;
			  }

			}

			if(length(ds_list.getColumn(i,"CREID")) < 1)
			{
			   ds_list.SetColumn(i,"CREID",'H'+G_USER_ID);
		   	   ds_list.SetColumn(i,"GBN3","A");
			}

			if (length(ds_list.getColumn(i,"CRDAT")) > 0 && ds_list.getColumn(i,"CMDAT") > '00000000')
			{
				ds_list.SetColumn(i,"GBN3","B");
			}			
            //변경자 추가 2014.03.21
			ds_list.SetColumn(i,"UPDID",'H'+G_USER_ID);
		}
	}

	gfn_copyDs(ds_list, ds_list_s, "GBN2='X'");
	//trace(ds_list.savexml());
	// 저장 처리
	tit_callService(
        "BS0301001S_ACT"
        , "save"
        , "ds_list=ds_list_s:A ds_cond=ds_cond"
        , "ds_error=ds_error ds_list=ds_list"
        , ""
        , "fn_afterSave");

}
/********************************************************************************
* 기      능   : 전송
********************************************************************************/
function fn_trans() {

	gfn_clearMsgBar();

	if ( ds_list.rowcount == 0 )
	{
		gfn_showMsg("CW00066");
		return;
	}

    if (!gfn_showConfirm("CW10025","영업부/기술영업부는 완료일 입력을 확인하시기 바랍니다. 미입력시 문자/메일 전송되지 않습니다. 전송을 원하시면 '확인' 버튼을 클릭하시기 바랍니다.")) {
		return;
	}

	for( var i=0; i<ds_list.rowcount; i++ )
	{    
	    if(ds_list.getColumn(i,"GBN2") == '0')
		{
			ds_list.SetColumn(i,"GBN2",'');
		}
		else if(ds_list.getColumn(i,"GBN2") == '1') 
		{  
		   if(ds_list.getColumn(i,"SEQNO") == '0000')
		   {
			   gfn_showAlert("CW10025", "0 차수는 전송이 불가합니다.");
			   return false;
		   }
		    ds_list.SetColumn(i,"GBN1","T");
			ds_list.SetColumn(i,"GBN2",'X');


			if(length(ds_list.getColumn(i,"CREID")) == 0)
			{
			   ds_list.SetColumn(i,"CREID",'H'+G_USER_ID);
		   	   ds_list.SetColumn(i,"GBN3","A");
			}

			if (length(ds_list.getColumn(i,"CRDAT")) > 0 && ds_list.getColumn(i,"CMDAT") > '00000000')
			{
				ds_list.SetColumn(i,"GBN3","B");
			}			
            //변경자 추가 2014.03.21
			ds_list.SetColumn(i,"UPDID",'H'+G_USER_ID);
		}
	}

	gfn_copyDs(ds_list, ds_list_t, "GBN2='X'");

	// 저장 처리
	tit_callService(
        "BS0301001S_ACT"
        , "save"
        , "ds_list=ds_list_t:A ds_cond=ds_cond"
        , "ds_error=ds_error ds_list=ds_list"
        , ""
        , "fn_afterTrans");
}
/********************************************************************************
* 기      능   : 전송 Callback  
********************************************************************************/
function fn_afterTrans(errCode, errMsg) 
{
    if(ds_error.rowcount > 0)
	{
      gfn_showAlert(ds_error.GetColumn(0,"ERRMSG"));
    }
    else
    {
      fn_query();
    }
}
/********************************************************************************
* 기      능   : 삭제
********************************************************************************/
function fn_delete() {
    if (G_VEND_CODE != "0000009909" && G_VEND_CODE != "1260694592" && G_VEND_CODE != "0000009901" && G_VEND_CODE != "1268637313")
    {
       gfn_showAlert("CW10025", "삭제 권한이 없습니다.");
	   return false;
	}

	if ( ds_list.rowcount == 0 )
	{
		gfn_showMsg("CW00066");
		return;
	}


	if (!gfn_showConfirm("CW10025","삭제하시겠습니까?")) {
		return;
	}



	for( var i=0; i<ds_list.rowcount; i++ )
	{


		if(ds_list.getColumn(i,"GBN2") == '0')
		{
			ds_list.SetColumn(i,"GBN2",'');
		}
		else if(ds_list.getColumn(i,"GBN2") == '1') 
		{
		   if(ds_list.getColumn(i,"CMDAT") > '00000000')
		   {
			   gfn_showAlert("CW10025", "완료일 등록이 되었을 경우 삭제가 불가합니다.");
			   return false;
		   }
			ds_list.SetColumn(i,"GBN2",'X');
			ds_list.SetColumn(i,"GBN1",'D');
			ds_list.SetColumn(i,"CREID",'H'+G_USER_ID);
			ds_list.SetColumn(i,"UPDID",G_USER_ID);
		}	
	}

	gfn_copyDs(ds_list, ds_list_d, "GBN2='X'");

	// 저장 처리
	tit_callService(
        "BS0301001S_ACT"
        , "save"
        , "ds_list=ds_list_d:A ds_cond=ds_cond"
        , "ds_error=ds_error ds_list=ds_list"
        , ""
        , "fn_afterSave");

}
