}

/********************************************************************************
* 기      능   : 저장 Callback
********************************************************************************/
function fn_afterSave(errCode, errMsg) {
	if(ds_error.rowcount > 0)
	{
      gfn_showAlert(ds_error.GetColumn(0,"ERRMSG"));
    }
    else
    {
      fn_query();
      gfn_showAlert("CW10025", "'전송' 기능을 실행해야 메일/문자가 전송됩니다.");
    }

}
