/********************************************************************************
* 기      능   : 상세내역검색
********************************************************************************/
function ufn_popup(obj,nRow,nCell,nX,nY,nPivotIndex)
{
  var str_old_text_flag = "";
	if(nCell == 9){

	  str_old_text_flag = ds_list.getcolumn(nRow,"SALES_P_TEXT");

	  if(str_old_text_flag != "X")
	  {
		  if(length(ds_list.getColumn(nRow,"SALES_P")) == 0) {

			alert("'항목'을 입력해야 상세내역을 입력할 수 있습니다.");
			return;
		  }

		  if(length(ds_list.getColumn(nRow,"DS_GUBUN")) == 0) {

			alert("'구분'을 입력해야 상세내역을 입력할 수 있습니다.");
			return;
		  }
		  if(length(ds_list.getColumn(nRow,"PBTXT")) == 0) {

			alert("'내역'을 입력해야 상세내역을 입력할 수 있습니다.");
			return;
		  }
	  }

	  if(ds_list.getColumn(nRow,"SEQNO") == '0000')
	  {
	    ds_posid.SetColumn(0, "PSPID", ds_list.getColumn(nRow,"PSPID"));
	    ds_posid.SetColumn(0, "POSID", ds_list.getColumn(nRow,"POSID"));

	    tit_clearActionInfo();
		tit_addSearchActionInfo("bs03:BS0301001S_S1");
		tit_callService(
			  ""
			, ""
			, "ds_posid=ds_posid"
			, "ds_seqno=ds_seqno"
			, ""
			, "fn_afterInput2"
			, true);
	  }
	  else
	  {
	  	 var arg = "flag='A' gubn='S'";
	     gfn_openPopup("BS0301001T",true,arg);
	  }




	}
}
/********************************************************************************
* 기      능   : 데이터셋 값 변경 시 - Flag 처리
********************************************************************************/
function ds_list_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	obj.fireevent = false;
	switch( strColumnID ) {

		case "GBN2":
			if(varNewValue == '1') 
			{
				ds_list.SetColumn(nRow,"GBN2","1");
				//ds_list.SetColumn(nRow,"CMDAT","00000000");  // 입력가능으로 변경후 초기화 제거 14.03.06 hss - 정영헌sw 요청
			}
			else if(varNewValue == '0') 
			{
				ds_list.SetColumn(nRow,"GBN2","0");
				ds_list.SetColumn(nRow,"CMDAT",ds_list.GetOrgColumn(nRow,"CMDAT"));
			}
			break;
	}

	obj.fireevent = true;	
}

var col = "";

function grd_list_OnExpandEdit(obj,nRow,nCell,strVal,nPivotIndex)
{
	var rect = obj.GetCellRect(nRow, ncell);
	cal_temp.top = rect[1] +100 ;
	cal_temp.Left = rect[0]; 
	cal_temp.Visible = true;

if (nCell = 12)
	{
		cal_temp.value = ds_list.getColumn(nRow, "CMDAT");	
		col = "CMDAT";
	} 

	cal_temp.SetFocus();
}

/********************************************************************************
* 기      능   : 달력에서 일자 선택했을 경우 
********************************************************************************/
function cal_temp_OnDayClick(obj,strText)
{
	gfn_clearMsgBar();

	if(ds_list.getColumn(ds_list.row,"GBN1") == 'C')
	{
	   gfn_showAlert("CW10025", "완료일 등록이 되었을 경우 변경이 불가합니다.");
	   obj.Visible = false;
	   return false;
	}
	else
	{
      ds_list.setColumn(ds_list.row, col, strText);
      	col = "";
	    obj.Visible = false;
	}

}
/********************************************************************************
* 기      능   : 달력에서 포커스 잃었을 경우 
********************************************************************************/
function cal_temp_OnKillFocus(obj)
{
	obj.Visible = false;
	col = "";
}
/********************************************************************************
* 기      능   : 입력
********************************************************************************/
function fn_input() {

  var row_count = 0;
  var row_insert_count = 0;
  ori_copy_index = -1;
  var seqno = "";


  gfn_clearMsgBar();

  for( var i=0; i<ds_list.rowcount; i++ )
  {
     if(ds_list.getColumn(i,"GBN2") == '1')
     {
       row_count = row_count + 1;
       ori_copy_index = i;
     }

     if(ds_list.getColumn(i,"GBN1") == 'I')
     {
       row_insert_count = row_insert_count + 1;
     }
  }

  if(row_count != 1)
  {
    gfn_showAlert("CW10025", "복사할 대상을 1건만 선택하시기 바랍니다.");
	return false;
  }

  if(row_insert_count > 0)
  {

    gfn_showAlert("CW10025", "기존 입력 항목 저장 후 실행 가능합니다.");
	return false;
  }


	if(ds_list.getColumn(ori_copy_index,"SEQNO") == '0000')
	{
	  gfn_showAlert("CW10025", "0번째 차수는 바로 입력하시면 됩니다.");
	  return false;
	}

//	if(ds_list.getColumn(ori_copy_index,"CMDAT") == '00000000' || ds_list.getColumn(ori_copy_index,"CMDAT") == '0000-00-00')
//	{
//	   gfn_showAlert("CW10025", "완료일 등록이 끝난 대상건만 차수를 증가할 수 있습니다.");
//	   return false;
//	}

	ds_posid.SetColumn(0, "PSPID", ds_list.getColumn(ori_copy_index,"PSPID"));
	ds_posid.SetColumn(0, "POSID", ds_list.getColumn(ori_copy_index,"POSID"));

	gfn_clearGridSortMark(grd_list);
	tit_clearActionInfo();
	tit_addSearchActionInfo("bs03:BS0301001S_S1");
	tit_callService(
		  ""
		, ""
		, "ds_posid=ds_posid"
		, "ds_seqno=ds_seqno"
		, ""
		, "fn_afterInput"
		, true);


}

/********************************************************************************
* 기      능   : 입력 Callback
********************************************************************************/
function fn_afterInput(errCode, errMsg) {

    var seqno = "";
    seqno = ds_seqno.getColumn(0,"SEQNO");

	if(seqno == "0" || seqno == "" || seqno == 0 || seqno == '0000')
	{
	   gfn_showAlert("CW10025", "차수 계산에 오류가 발생하였습니다. 정보기술부 정영헌 대리(5188)에게 문의하시기 바랍니다.");
	   return false;
	}

	gfn_addGridRow(grd_list, ori_copy_index + 1, "FLAG");
	ds_list.CopyRow(ds_list.currow,"ds_list",ori_copy_index);
	ds_list.SetColumn(ds_list.currow, "STATUS", "사양문제 미등록");
	ds_list.SetColumn(ds_list.currow, "SEQNO", seqno);
	ds_list.SetColumn(ds_list.currow, "DS_GUBUN", "");
	ds_list.SetColumn(ds_list.currow, "SALES_P", "");
	ds_list.SetColumn(ds_list.currow, "PBTXT", "");
	ds_list.SetColumn(ds_list.currow, "CRDAT", "");
	ds_list.SetColumn(ds_list.currow, "CMDAT", '00000000');
	ds_list.SetColumn(ds_list.currow, "CREID", "");
	ds_list.SetColumn(ds_list.currow, "CREIDNM", "");
	ds_list.SetColumn(ds_list.currow, "DAYS", "");
	ds_list.SetColumn(ds_list.currow, "EL_CODE", "");
	ds_list.SetColumn(ds_list.currow, "SALES_P_TEXT", "");
	ds_list.SetColumn(ds_list.currow, "GBN1", "I");

}

/********************************************************************************
* 기      능   : 입력 Callback
********************************************************************************/
function fn_afterInput2(errCode, errMsg) {
    var seqno = "";

    seqno = ds_seqno.getColumn(0,"SEQNO");

	if(seqno == "0" || seqno == "" || seqno == 0 || seqno == '0000' || seqno == null)
	{ 
	  seqno = "0001";
	}

	ds_list.SetColumn(ds_list.currow, "SEQNO", seqno);

	var arg = "flag='A' gubn='S'";
	gfn_openPopup("BS0301001T",true,arg);
}
function grd_list_OnKeyDown(obj,nChar,bShift,bCtrl,bAlt,nLLParam,nHLParam)
{
	if((bCtrl) && (nChar == 67))
	{
		ClearClipboard();
		SetClipBoard("CF_TEXT",grd_list.GetCellText("body",ds_list.row,grd_list.GetCellPos()));
	}

	if(grd_list.GetCellPos() == grd_list.GetBindCellIndex("body", "PBTXT") ||  grd_list.GetCellPos() == grd_list.GetBindCellIndex("body", "CMDAT")) {
		if(nChar == 9 || nChar == 37 || nChar == 38 || nChar == 39 || nChar == 40) {
			grd_list.SetCellProp("body", 9, "edit", "normal");
		}
	}

	if(grd_list.GetCellPos() == 9) {
		if(nChar == 13) {
			ufn_popup(obj,grd_list.CurrentRow,grd_list.GetCellPos());
		}
	}
}

function grd_list_OnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	if(nCell == 8) {
		grd_list.SetCellProp("body", 9, "edit", "normal");
	} else {
		grd_list.SetCellProp("body", 9, "edit", "none");
	}
}
