		  result4 = gfn_saveLongText(ds_longtxt, tab.sales_pb.t_detail_b_ds.value, "Z001", tdname, ds_list.getcolumn(ds_list.row,"TDOBJECT"));

		  result5 = gfn_saveLongText(ds_longtxt, tab.sales_pb.t_detail_b_ts.value, "Z002", tdname, ds_list.getcolumn(ds_list.row,"TDOBJECT"));

		  result6 = gfn_saveLongText(ds_longtxt, tab.sales_pb.e_detail_b_cd.value, "Z003", tdname, ds_list.getcolumn(ds_list.row,"TDOBJECT"));
		 	if(!elcode_check_flag)
			{
				if(tab.sales_pb.e_detail_b_cd.value != "")
				{
				  ds_list.SetColumn(ds_list.row,"EL_CODE", tab.sales_pb.e_detail_b_cd.value);
				  elcode_check_flag = true;
				}
			}
		  tdname = pspid + "-" + posid + "-" + seqno + "-" + "C";
		  result7 = gfn_saveLongText(ds_longtxt, tab.sales_pc.t_detail_c_ds.value, "Z001", tdname, ds_list.getcolumn(ds_list.row,"TDOBJECT"));

		  result8 = gfn_saveLongText(ds_longtxt, tab.sales_pc.t_detail_c_ts.value, "Z002", tdname, ds_list.getcolumn(ds_list.row,"TDOBJECT"));

		  result9 = gfn_saveLongText(ds_longtxt, tab.sales_pc.e_detail_c_cd.value, "Z003", tdname, ds_list.getcolumn(ds_list.row,"TDOBJECT"));
			if(!elcode_check_flag)
			{
				if(tab.sales_pc.e_detail_c_cd.value != "")
				{
				  ds_list.SetColumn(ds_list.row,"EL_CODE", tab.sales_pc.e_detail_c_cd.value);
				  elcode_check_flag = true;
				}
			}
		tdname = pspid + "-" + posid + "-" + seqno + "-" + "D";
		  result10 = gfn_saveLongText(ds_longtxt, tab.sales_pd.t_detail_d_ds.value, "Z001", tdname, ds_list.getcolumn(ds_list.row,"TDOBJECT"));

		  result11 = gfn_saveLongText(ds_longtxt, tab.sales_pd.t_detail_d_ts.value, "Z002", tdname, ds_list.getcolumn(ds_list.row,"TDOBJECT"));

		  result12 = gfn_saveLongText(ds_longtxt, tab.sales_pd.e_detail_d_cd.value, "Z003", tdname, ds_list.getcolumn(ds_list.row,"TDOBJECT"));
	     if(!elcode_check_flag)
			{
				if(tab.sales_pd.e_detail_d_cd.value != "")
				{
				  ds_list.SetColumn(ds_list.row,"EL_CODE", tab.sales_pd.e_detail_d_cd.value);
				  elcode_check_flag = true;
				}
			}
		tdname = pspid + "-" + posid + "-" + seqno + "-" + "E";
		  result13 = gfn_saveLongText(ds_longtxt, tab.sales_pe.t_detail_e_ds.value, "Z001", tdname, ds_list.getcolumn(ds_list.row,"TDOBJECT"));

		  result14 = gfn_saveLongText(ds_longtxt, tab.sales_pe.t_detail_e_ts.value, "Z002", tdname, ds_list.getcolumn(ds_list.row,"TDOBJECT"));

		  result15 = gfn_saveLongText(ds_longtxt, tab.sales_pe.e_detail_e_cd.value, "Z003", tdname, ds_list.getcolumn(ds_list.row,"TDOBJECT")); 

		   if(!elcode_check_flag)
			{
				if(tab.sales_pe.e_detail_e_cd.value != "")
				{
				  ds_list.SetColumn(ds_list.row,"EL_CODE", tab.sales_pe.e_detail_e_cd.value);
				  elcode_check_flag = true;
				}
			}

			tdname = pspid + "-" + posid + "-" + seqno + "-" + "F";
		  result16 = gfn_saveLongText(ds_longtxt, tab.sales_pf.t_detail_f_ds.value, "Z001", tdname, ds_list.getcolumn(ds_list.row,"TDOBJECT"));

		  result17 = gfn_saveLongText(ds_longtxt, tab.sales_pf.t_detail_f_ts.value, "Z002", tdname, ds_list.getcolumn(ds_list.row,"TDOBJECT"));

		  result18 = gfn_saveLongText(ds_longtxt, tab.sales_pf.e_detail_f_cd.value, "Z003", tdname, ds_list.getcolumn(ds_list.row,"TDOBJECT"));

			if(!elcode_check_flag)
			{
				if(tab.sales_pf.e_detail_f_cd.value != "")
				{
				  ds_list.SetColumn(ds_list.row,"EL_CODE", tab.sales_pf.e_detail_f_cd.value);
				  elcode_check_flag = true;
				}
			}

		tdname = pspid + "-" + posid + "-" + seqno + "-" + "H";
		  result19 = gfn_saveLongText(ds_longtxt, tab.sales_ph.t_detail_h_ds.value, "Z001", tdname, ds_list.getcolumn(ds_list.row,"TDOBJECT"));

		  result20 = gfn_saveLongText(ds_longtxt, tab.sales_ph.t_detail_h_ts.value, "Z002", tdname, ds_list.getcolumn(ds_list.row,"TDOBJECT"));

		  result21 = gfn_saveLongText(ds_longtxt, tab.sales_ph.e_detail_h_cd.value, "Z003", tdname, ds_list.getcolumn(ds_list.row,"TDOBJECT"));
	   			if(!elcode_check_flag)
