				if(tab.sales_ph.e_detail_h_cd.value != "")
				{
				  ds_list.SetColumn(ds_list.row,"EL_CODE", tab.sales_ph.e_detail_h_cd.value);
				  elcode_check_flag = true;
				}
			}
		  if(result1 && result2 && result3 && result4 && result5 && result6 && result7 && result8 && result9 && result10 && result11 && result12 &&
			result13 && result14 && result15 && result16 && result17 && result18 && result19 && result20 && result21)
		  {
			gfn_showMsg("CI00003");
		  }
		  else
		  {
			alert("오류가 발생하였습니다. 정보기술부 정영헌대리(5188)에게 문의하시기 바랍니다.");
			return;
		  }
      }



   }
   else if(sales_p == 'H')
   {
      result1 = gfn_saveLongText(ds_longtxt, tab.sales_ph.t_detail_h_ds.value, "Z001", tdname, ds_list.getcolumn(ds_list.row,"TDOBJECT"));

      result2 = gfn_saveLongText(ds_longtxt, tab.sales_ph.t_detail_h_ts.value, "Z002", tdname, ds_list.getcolumn(ds_list.row,"TDOBJECT"));

      result3 = gfn_saveLongText(ds_longtxt, tab.sales_ph.e_detail_h_cd.value, "Z003", tdname, ds_list.getcolumn(ds_list.row,"TDOBJECT"));

      if(result1 && result2 && result3)
      {
        ds_list.SetColumn(ds_list.row,"EL_CODE", tab.sales_ph.e_detail_h_cd.value);
        gfn_showMsg("CI00003");
      }
      else
      {
        alert("오류가 발생하였습니다. 정보기술부 정영헌대리(5188)에게 문의하시기 바랍니다.");
		return;
      }
   }
   else
   {


      result1 = gfn_saveLongText(ds_longtxt, tab.sales_pa.t_detail_a_ds.value, "Z001", tdname, ds_list.getcolumn(ds_list.row,"TDOBJECT"));



      result2 = gfn_saveLongText(ds_longtxt, tab.sales_pa.t_detail_a_ts.value, "Z002", tdname, ds_list.getcolumn(ds_list.row,"TDOBJECT"));

      result3 = gfn_saveLongText(ds_longtxt, tab.sales_pa.e_detail_a_cd.value, "Z003", tdname, ds_list.getcolumn(ds_list.row,"TDOBJECT"));



      if(result1 && result2 && result3)
      {
        ds_list.SetColumn(ds_list.row,"EL_CODE",tab.sales_pa.e_detail_a_cd.value);
        gfn_showMsg("CI00003");
      }
      else
      {
        alert("오류가 발생하였습니다. 정보기술부 정영헌대리(5188)에게 문의하시기 바랍니다.");
		return;
      }
   }
}
