/********************************************************************************
* 기      능   : 그리드 헤더 클릭시 정렬 처리 
********************************************************************************/
function grd_list_OnHeadClick(obj,nCell,nX,nY,nPivotIndex)
{
	if ( nCell == 0 ) {
		// 그리드 표시할 컬럼 선택 
		gfn_selectGridCol(obj);
	} else {
		// 그리드 정렬 처리 
		gfn_sortGrid(obj,nCell);
	}
}

function Tab_OnChanging(obj,nOldIndex,nNewIndex) 
{                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        
   var sale_p;  

   var str_old_text_flag = "";
   sales_p = ds_list.getcolumn(ds_list.row,"SALES_P");
   str_old_text_flag = ds_list.getcolumn(ds_list.row,"SALES_P_TEXT");

   if(sales_p == 'A')
   {
	 if(nNewIndex == 0)
	 {	  
	    return true;
	 }
	 else
	 {
	   return false;
	 }
   }
   else if(sales_p == 'B')
   {
	 if(nNewIndex == 1)
	 {
	   return true;
	 }
	 else
	 {
	   return false;
	 }
   }
   else if(sales_p == 'C')
   {
	 if(nNewIndex == 2)
	 {	 
	   return true;
	 }
	 else
	 {
	   return false;
	 }
   }
   else if(sales_p == 'D')
   {
	 if(nNewIndex == 3)
	 {
	   return true;
	 }
	 else
	 {
	   return false;
	 }
   }
   else if(sales_p == 'E')
   {
	 if(nNewIndex == 4)
	 {
	   return true;
	 }
	 else
	 {
	   return false;
	 }
   }
   else if(sales_p == 'F')
   {
	 if(nNewIndex == 5)
	 {
	   return true;
	 }
	 else
	 {
	   return false;
	 }
   }
   else if(sales_p == 'G')
   {
      if(str_old_text_flag == "X")
      {
         if(nNewIndex == 0)
		 {
		   return true;
		 }
		 else
		 {
		   return false;
		 }
      }
      else
      {
		 //A
		 if(nNewIndex == 0)
		 {
		   return true;
		 }
		 //B
