function fn_OnLoadCompleted(obj) {
	gfn_initForm(true);

	//코드콤보 설정
	var codeArr = ["EIS003","EIS004","EIS006","EIS005"];
	var dsObjArr = [ds_sgbn,ds_sayu,ds_proc,ds_mgbn];
	var addTypeArr = [G_CODE_NONE,G_CODE_NONE,G_CODE_NONE,G_CODE_NONE];
	gfn_searchCommCode(codeArr, dsObjArr, addTypeArr);

	fn_searchCode1();
	fn_searchCode2();
	fn_searchCode3();
	fn_searchGigun();
}

