function fn_userControl() {
	user_grp1 = G_USER_GROUP_B; //영업
	user_grp2 = G_USER_GROUP_E; //설치
	user_grp3 = G_USER_GROUP_R; //보수
	user_grp4 = G_USER_GROUP_M; //자재

	user_grp = G_USER_GROUP;

	if(G_VEND_CODE == "0000009922") { //정보기술실
		user_grp = "Z40";
	} else if(user_grp1 > "") {
		user_grp = user_grp1;
	} else if(user_grp2 > "") {
		user_grp = user_grp2;
	} else if(user_grp3 > "") {
		user_grp = user_grp3;
	} else if(user_grp4 > "") {
		user_grp = user_grp4;
	}

	gbn1 = substr(user_grp,0,1);
	gbn2 = right(user_grp,2);

	if((substr(G_USER_ID,0,1) == "7" || substr(G_USER_ID,0,1) == "8" || substr(G_USER_ID,0,1) == "9") && userGroup > "10") {
		ufn_enableBtn("query", false);
	} else if(gbn1 == "M") {
		ufn_enableBtn("query", false);
		ufn_enableBtn("excel", false);

		ed_pjt.Enable = false;
		cb_gbn1.Enable = false;
		cb_gbn2.Enable = false;
		cb_vkbur.Enable = false;
		cb_vkgrp.Enable = false;
		cb_zzactss.Enable = false;
		cb_temno.Enable = false;
	} else if(gbn1 == "R") {
		if(gbn2 == "20") {
			ds_cond.SetColumn(0, "ZZBPM", G_USER_ID);

			cb_vkbur.Enable = false;
			cb_vkgrp.Enable = false;
			cb_zzactss.Enable = false;
			cb_temno.Enable = false;
		} else {
			ufn_enableBtn("query", false);
			ufn_enableBtn("excel", false);

			ed_pjt.Enable = false;
			cb_gbn1.Enable = false;
			cb_gbn2.Enable = false;
			cb_vkbur.Enable = false;
			cb_vkgrp.Enable = false;
			cb_zzactss.Enable = false;
			cb_temno.Enable = false;
		}
	} else if(gbn1 == "E") {
		if(gbn2 == "40") {
			cb_vkbur.Enable = false;
			cb_vkgrp.Enable = false;
		} else if(gbn2 == "30" || gbn2 == "20") {
			ds_cond.SetColumn(0, "ZZACTSS", zzactss);
			ds_cond3.SetColumn(0, "ZZACTSS", zzactss);

			fn_ChgTemno();

			cb_vkbur.Enable = false;
			cb_vkgrp.Enable = false;
			cb_zzactss.Enable = false;
		} else if(gbn2 == "10") {
			cb_vkbur.Enable = false;
			cb_vkgrp.Enable = false;
			cb_zzactss.Enable = false;

			ds_temno.Filter("USERNUMB = "+quote(G_USER_ID));
			cb_temno.Index = 0;
		} else {
		}
	} else if(gbn1 == "B") {
		if(gbn2 == "40") {
			cb_zzactss.Enable = false;
			cb_temno.Enable = false;
		} else if(gbn2 == "30" || gbn2 == "20") {
			ds_cond.SetColumn(0, "VKBUR", vkbur);
			ds_cond2.SetColumn(0, "VKGRP", vkgrp);

			fn_ChgVkgrp();

			cb_vkbur.Enable = false;
			cb_zzactss.Enable = false;
			cb_temno.Enable = false;
		} else if(gbn2 == "10") {
			ufn_enableBtn("query", false);
			ufn_enableBtn("excel", false);

			ed_pjt.Enable = false;
			cb_gbn1.Enable = false;
			cb_gbn2.Enable = false;
			cb_vkbur.Enable = false;
			cb_vkgrp.Enable = false;
			cb_zzactss.Enable = false;
			cb_temno.Enable = false;
		} else {
		}
	}
}

