	return gfn_isFormUnload(obj);
}

/********************************************************************************
* 기      능   : 조회 Callback 
********************************************************************************/
function fn_afterUserSearch(errCode, errMsg) {
	vkbur = ds_user.GetColumn(0, "VKBUR");
	vkgrp = substr(ds_user.GetColumn(0, "VKBUR"),0,2);
	zzactss = ds_user.GetColumn(0, "ZZACTSS");
	zzara = ds_user.GetColumn(0, "ZZARA");

	if(ds_user.GetRowCount() == 0) {
		fn_userControl();
	}

	if(length(trim(vkgrp)) > 0 || length(trim(zzactss)) > 0 || length(trim(zzara)) > 0) {
		fn_userControl();
	} 
}

function fn_afterGijunSearch(errCode, errMsg) {
	cb_gijun.Index = 0;
	t_init = true;
}

function fn_afterCode1Search(errCode, errMsg) {
    gfn_insertEmptyRow(ds_gbn, G_CODE_ALL);
	gfn_insertEmptyRow(ds_gbn1, G_CODE_ALL);
	gfn_insertEmptyRow(ds_gbn2, G_CODE_ALL);
	gfn_insertEmptyRow(ds_waerk, G_CODE_ALL);
	cb_gbn.Index = 0;
	cb_gbn1.Index = 0;
	cb_gbn2.Index = 0;
	cb_waerk.Index = 1;
	cb_gbn2.enable = false;	
	cb_waerk.enable = false;
}

function fn_afterCode2Search(errCode, errMsg) {
	gfn_insertEmptyRow(ds_vkbur, G_CODE_ALL);
	gfn_insertEmptyRow(ds_vkgrp, G_CODE_ALL);
	cb_vkbur.Index = 0;
	cb_vkgrp.Index = 0;
}

function fn_afterCode3Search(errCode, errMsg) {
	gfn_insertEmptyRow(ds_zzactss, G_CODE_ALL);
	gfn_insertEmptyRow(ds_temno, G_CODE_ALL);
	cb_zzactss.Index = 0;
	cb_temno.Index = 0;

	fn_searchUser();
}

/********************************************************************************
* 기      능   : 조회 Callback 
********************************************************************************/
function fn_afterTemnoChange(errCode, errMsg) {
	gfn_insertEmptyRow(ds_temno, G_CODE_ALL);
	cb_temno.Index = 0;
}

function fn_afterVkgrpChange(errCode, errMsg) {
	gfn_insertEmptyRow(ds_vkgrp, G_CODE_ALL);
	cb_vkgrp.Index = 0;
}

