/********************************************************************************
* 기      능   : 조회 1
********************************************************************************/
function fn_query() {
	gfn_clearMsgBar();

	if(length(trim(cb_gijun.Value)) == 0) {
		gfn_showMsg("수주기준일을 셋팅중입니다.");
		return;
	}

	var user_grp = gfn_getUserGroup();

	ds_cond.SetColumn(0, "PROJ", ed_pjt.value);
	ds_cond.SetColumn(0, "GBN", cb_gbn.value);
	ds_cond.SetColumn(0, "GBN1", cb_gbn1.value);
	ds_cond.SetColumn(0, "GBN2", cb_gbn2.value);
	ds_cond.SetColumn(0, "VKBUR", cb_vkbur.value);
	ds_cond.SetColumn(0, "VKGRP", cb_vkgrp.value);
	ds_cond.SetColumn(0, "ZZACTSS", cb_zzactss.value);
	ds_cond.SetColumn(0, "TEMNO", cb_temno.value);
	ds_cond.SetColumn(0, "GIJUN", cb_gijun.value);
	ds_cond.SetColumn(0, "WAERK", cb_waerk.value);	

	// sort mark 삭제 
	gfn_clearGridSortMark(grd_list);
	ds_list.row = -1;
	ds_list.ClearData();

	tit_clearActionInfo();
	tit_addSearchActionInfo("bs04:BS0401001A_S1");

	tit_callService(
        ""
        , ""
        , "ds_cond=ds_cond"
        , "ds_list=ds_list"
        , ""
        , "fn_afterQuery"
        , true);
}

