}

/********************************************************************************
* 기      능   : 액셀  
********************************************************************************/
function fn_excel() {
	// Excel Export 
	gfn_saveToExcel(grd_list, true);
}

//=========================================================================================
// [ PART 4]
// Callback 함수 정의 : callback 함수명은 메인 함수의 fn_xxxx() 에서 xxxx 부분은 fn_afterXXXX()
// 형태가 되도록 작성 
//=========================================================================================
/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterQuery(errCode, errMsg) {
    if(cb_gbn.Value == "B") {
      cnt = 0;
      txt = "선적일";
      fn_hide_column();
      fn_change_column();
    } else {
      cnt = 80;
      txt = "착공예정일";
      fn_hide_column();
      fn_change_column();
    }
	gfn_showMsg("CI00002", ds_list.RowCount() + "");
}

