// [ PART 6 ]
// component Event 정의 : 각 Component 별 이벤트 
//=========================================================================================
/********************************************************************************
* 기      능   : 그리드 헤더 클릭시 정렬 처리 
********************************************************************************/
function grd_list_OnHeadClick(obj,nCell,nX,nY,nPivotIndex) {
	if(nCell == 0) {
		gfn_selectGridCol(obj);
	} else {
		// 그리드 정렬 처리 
		gfn_sortGrid(obj, nCell);
	}
}

function cb_gbn_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex) {
	if(length(trim(strCode)) == 0) {
		ds_gbn1.Filter("1==1");
		cb_gbn1.Index = 0;
		cb_waerk.Index = 0;
		cb_waerk.enable = false;

	} else if(cb_gbn.Value == "A") {
		ds_gbn1.Filter("CODE <> 'H' && CODE <> 'J' && CODE <> 'F' && CODE <> 'G' && CODE <> 'T' && CODE <> 'U' && CODE <> 'V' && CODE <> 'W' && CODE <> 'X'");
		cb_gbn1.Index = 0;

		ds_waerk.Filter("CODE <> 'U' && CODE <> 'F'");
		cb_waerk.Index = 1;
		cb_waerk.enable = false;

	} else if(cb_gbn.Value == "B") {
		ds_gbn1.Filter("CODE <> 'C' && CODE <> 'D' && CODE <> 'J' && CODE <> 'F' && CODE <> 'G' && CODE <> 'E'");
		cb_gbn1.Index = 0;

		ds_waerk.Filter("CODE <> ''");
		cb_waerk.enable = true;

    } else {
        ds_gbn1.Filter("CODE == '' || CODE == 'J' || CODE == 'F' || CODE == 'G'");
		cb_gbn1.Index = 0;

		ds_waerk.Filter("CODE <> 'U' && CODE <> 'F'");
		cb_waerk.Index = 1;
		cb_waerk.enable = false;
	}
}

function cb_gbn1_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex) {
	if(length(trim(strCode)) == 0) {
		ds_gbn2.Filter("1==1");
		cb_gbn2.Index = 0;
		cb_gbn2.enable = false;	

	} else {
		ds_gbn2.Filter("CODE == '' || SUBSTR(CODE,0,1) = "+quote(strCode));
		cb_gbn2.Index = 0;
		cb_gbn2.enable = true;	

	}
}

function cb_vkbur_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex) {
	ds_cond2.SetColumn(0, "VKGRP", substr(strCode,0,2));
	cb_vkgrp.value = "";

	if(length(trim(strCode)) == 0) {
		ds_vkgrp.Filter("1==1");
		cb_vkgrp.Index = 0;

		fn_searchCode2();
	} else {
		fn_ChgVkgrp();
	}
}

function cb_zzactss_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex) {
	ds_cond3.SetColumn(0, "ZZACTSS", strCode);
	cb_temno.value = "";

	if(length(trim(strCode)) == 0) {
		ds_zzactss.Filter("1==1");
		cb_zzactss.Index = 0;

		fn_searchCode3();
	} else {
		fn_ChgTemno();
	}
}

function fn_ChgTemno() {
	tit_clearActionInfo();
	tit_addSearchActionInfo("bs04:BS0401001A_S4");
	tit_callService(
		""
		, ""
		, "ds_cond3=ds_cond3"
		, "ds_temno=ds_temno"
		, ""
		, "fn_afterTemnoChange"
		, true
		, true);
}

function fn_ChgVkgrp() {
	tit_clearActionInfo();
	tit_addSearchActionInfo("bs04:BS0401001A_S10");
	tit_callService(
		""
		, ""
		, "ds_cond2=ds_cond2"
		, "ds_vkgrp=ds_vkgrp"
		, ""
		, "fn_afterVkgrpChange"
		, true
		, true);
}
/********************************************************************************
* Hide Grid Column
********************************************************************************/
function fn_hide_grid_column(colid) {
/*	grd_list.SetCellProp("head",	grd_list.GetBindCellIndex("body",colid),"Display","null");
	grd_list.SetCellProp("body",	grd_list.GetBindCellIndex("body",colid),"Display","null");*/
	grd_list.SetColProp(grd_list.GetBindCellIndex("body",colid),"Width",cnt);
}
/********************************************************************************
* Change Grid Column
********************************************************************************/
function fn_change_grid_column(colid) {
	grd_list.SetCellProp("head", grd_list.GetBindCellIndex("body",colid),"Text",txt);
}
/********************************************************************************
* 조회조건에 따른 특정 칼럼숨기기
********************************************************************************/
function fn_hide_column() {
	fn_hide_grid_column("ZZCHAKG");
	fn_hide_grid_column("ZZCOMP2");
	fn_hide_grid_column("ZZJUNGO");
}
/********************************************************************************
* 조회조건에 따른 head text변경
********************************************************************************/
function fn_change_column() {
	fn_change_grid_column("ZZSHIP1");
}
