var pspid;
var posid;

//=========================================================================================
// [ PART 2 ]
// Form Loading / Unloading 시 작업 정의
//=========================================================================================
function fn_OnLoadCompleted(obj) {
	gfn_initForm();

//	cal_ydt.value = gfn_getCurrDate();

	//코드콤보 설정
	var codeArr = ["EIS003","EIS004","EIS006","EIS005","EIS003","EIS004","EIS006","EIS005"];
	var dsObjArr = [ds_sgbn,ds_sayu,ds_proc,ds_mgbn,ds_sgbn2,ds_sayu2,ds_proc2,ds_mgbn2];
	var addTypeArr = [G_CODE_SELECT,G_CODE_SELECT,G_CODE_SELECT,G_CODE_SELECT,G_CODE_NONE,G_CODE_NONE,G_CODE_NONE,G_CODE_NONE];
	gfn_searchCommCode(codeArr, dsObjArr, addTypeArr);
}

/********************************************************************************
* 기      능   : 공통코드가 조회된 후의 Callback 함수 
********************************************************************************/
function fn_completeInit(sCode, sDsObj, sCodeType) {
	cb_sgbn.index = 0;
	cb_sayu.index = 0;
	cb_proc.index = 0;
	cb_mgbn.index = 0;
	cb_sayu.Enable = false;

//	ds_mgbn.Filter("CODE <> 'C'");
//	cb_mgbn.Index = 0;

	fn_queryHeader();
}

/********************************************************************************
* 기      능   : form UnloadCompleted 
********************************************************************************/
function fn_OnUnloadCompleted(obj) {
	return gfn_isFormUnload(obj);
}

//=========================================================================================
// [ PART 3 ]
// Main Process Event 정의(조회,입력,삭제,저장,인쇄,form닫기)
//=========================================================================================
/********************************************************************************
* 기      능   : 조회 
********************************************************************************/
function fn_queryHeader() {
	ds_condH.SetColumn(0, "PSPID", pspid);
	ds_condH.SetColumn(0, "POSID", posid);

	var sql = "";

	if(length(trim(posid)) > 0) {
		sql = "bs04:BS0402001B_S2";
	} else {
		sql = "bs04:BS0402001B_S3";
	}

	tit_clearActionInfo();
	tit_addSearchActionInfo(sql);

	tit_callService(
        ""
        , ""
        , "ds_condH=ds_condH"
        , "ds_listH=ds_listH"
        , ""
        , "fn_afterQueryHeader"
        , true
        , true);
}

function fn_query() {
	ds_cond.SetColumn(0, "PSPID", ds_listH.GetColumn(0, "PSPID"));

	tit_clearActionInfo();
	tit_addSearchActionInfo("bs04:BS0402001B_S1");

	tit_callService(
        ""
        , ""
        , "ds_cond=ds_cond"
        , "ds_list=ds_list"
        , ""
        , "fn_afterQuery"
        , true
        , true);
}

/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterQueryHeader(errCode, errMsg) {
	fn_query();
}

function fn_afterQuery(errCode, errMsg) {
	fn_chkHno();
	gfn_showMsg("CI00002", ds_list.RowCount()+"");
}

/********************************************************************************
* 기      능   : 저장
********************************************************************************/
function fn_save() {
	gfn_clearMsgBar();
	ds_list2.ClearData();

	if(ds_list.rowCount() < 1) {
		gfn_showMsg("CW00010");
		return;
	}

	if(ds_list.FindRow("CHK", 1) == -1) {
		gfn_showMsg("CW00013");
		return;
	}

	if(length(cb_sgbn.value) == 0) {
		var arr = [st_sgbn.value];
		gfn_showMsg("CW00001", arr);
		return;
	}

	if(cb_sgbn.value == "M" || cb_sgbn.value == "Z") {
		if(length(cb_sayu.value) == 0) {
			var arr = [st_sayu.value];
			gfn_showMsg("CW00001", arr);
			return;
		}
	}
/*
	if(length(cb_proc.value) == 0) {
		var arr = [st_proc.value];
		gfn_showMsg("CW00001", arr);
		return;
	}

	if(length(cal_ydt.value) == 0) {
		var arr = [st_ydt.value];
		gfn_showMsg("CW00001", arr);
		return;
	}

	if(length(cb_mgbn.value) == 0) {
		var arr = [st_mgbn.value];
		gfn_showMsg("CW00001", arr);
		return;
	}

	if(length(tx_desc.value) == 0) {
		var arr = [st_desc.value];
		gfn_showMsg("CW00001", arr);
		return;
	}
*/
	if(length(tx_desc.value) > 255) {
		var arr = [st_desc.value, "256자"];
		gfn_showMsg("CW00133", arr);
		return;
	}

	var j = 0;
	for(var i=0; i<ds_list.RowCount(); i++) {
		if(ds_list.GetColumn(i, "CHK") == true) { //체크박스가 true 일 경우
			ds_list2.AddRow();
			ds_list2.SetColumn(j, "FLAG", "U");
			ds_list2.SetColumn(j, "PSPID", ds_list.GetColumn(i, "PSPID"));
			ds_list2.SetColumn(j, "POSID", ds_list.GetColumn(i, "POSID"));
			ds_list2.SetColumn(j, "BSTDK", ds_list.GetColumn(i, "BSTDK"));
			ds_list2.SetColumn(j, "VDATU", ds_list.GetColumn(i, "VDATU"));
			ds_list2.SetColumn(j, "S_GUBUN", cb_sgbn.value);
			ds_list2.SetColumn(j, "S_SAYU", cb_sayu.value);
			ds_list2.SetColumn(j, "S_PROC", cb_proc.value);
			ds_list2.SetColumn(j, "M_DATE", cal_ydt.value);
			ds_list2.SetColumn(j, "M_GUBUN", cb_mgbn.value);
			ds_list2.SetColumn(j, "S_DESC", tx_desc.value);
			j++;
		}
	}

	tit_clearActionInfo();
	tit_addSaveActionInfo(
		""
		, "bs04:BS0402001B_I1"
		, ""
		, "FLAG"
		);

	tit_callService(
		""
		, ""
		, "ds_list2=ds_list2"
		, ""
		, ""
		, "fn_afterSave"
		, true);
}

/********************************************************************************
* 기      능   : 저장 Callback
********************************************************************************/
function fn_afterSave(errCode, errMsg) {
	if(errCode != 0) {
		gfn_showMsg("CE00001");
	} else {
		gfn_showMsg("CI00005");
		fn_query();
	}
}

/********************************************************************************
* 기      능   : 닫기  
********************************************************************************/
function fn_close() {
	close();
}

